import { GoogleGenAI } from "@google/genai";
import { Student } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const generateReportComment = async (student: Student): Promise<string> => {
  try {
    const gradesSummary = student.grades.map(g => `${g.subject}: ${g.score}/${g.maxScore}`).join(', ');
    const attendanceCount = Object.values(student.attendance).filter(a => a === 'PRESENT').length;
    const totalDays = Object.keys(student.attendance).length;

    const prompt = `
      As a professional school principal, write a concise (2-3 sentences) terminal report comment for the following student:
      Name: ${student.name}
      Grades: ${gradesSummary}
      Attendance: ${attendanceCount} days present out of ${totalDays}
      Tone: Encouraging and professional.
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });

    return response.text || "Consistently performing well in all subjects. Maintain this level of dedication.";
  } catch (error) {
    console.error("Error generating comment:", error);
    return "The student is making steady progress. Continued focus on subject fundamentals is recommended.";
  }
};

