import React, { useState } from 'react';
import { Student } from '../types';
import { generateReportComment } from '../services/geminiService';

interface ReportCardProps {
  students: Student[];
}

const ReportCard: React.FC<ReportCardProps> = ({ students }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [aiComment, setAiComment] = useState<string>('');
  const [isGenerating, setIsGenerating] = useState(false);

  const student = students.find(s => s.id === selectedStudentId);

  const handleGenerateComment = async () => {
    if (!student) return;
    setIsGenerating(true);
    const comment = await generateReportComment(student);
    setAiComment(comment);
    setIsGenerating(false);
  };

  if (!student) return <div>No student selected.</div>;

  return (
    <div className="space-y-6 animate-fadeIn pb-12">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-800">Terminal Report</h1>
        <div className="flex gap-4">
          <select 
            value={selectedStudentId}
            onChange={(e) => {
              setSelectedStudentId(e.target.value);
              setAiComment('');
            }}
            className="bg-white border-none rounded-2xl px-4 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-medium"
          >
            {students.map(s => (
              <option key={s.id} value={s.id}>{s.name}</option>
            ))}
          </select>
          <button className="bg-white border-none text-indigo-600 px-6 py-3 rounded-2xl font-bold hover:bg-indigo-50 transition-all shadow-sm">
            <i className="fas fa-download mr-2"></i> PDF
          </button>
        </div>
      </div>

      <div className="bg-white rounded-[2rem] shadow-xl overflow-hidden border border-gray-100 max-w-4xl mx-auto">
        {/* Report Header */}
        <div className="bg-indigo-600 p-8 text-white relative overflow-hidden">
          <div className="absolute top-0 right-0 p-8 opacity-10">
            <i className="fas fa-university text-9xl"></i>
          </div>
          <div className="relative z-10 flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
            <div className="flex items-center gap-6">
              <img src={student.avatar} className="w-24 h-24 rounded-3xl border-4 border-indigo-500 object-cover shadow-2xl" alt="" />
              <div>
                <h2 className="text-3xl font-bold">{student.name}</h2>
                <p className="text-indigo-200 font-medium">{student.gradeLevel} - Section {student.section}</p>
                <p className="text-indigo-200 text-sm">Academic Year 2023/2024</p>
              </div>
            </div>
            <div className="text-left md:text-right">
              <h3 className="text-xl font-bold">EduSphere Academy</h3>
              <p className="text-indigo-200 text-sm">Terminal Report Card</p>
              <div className="mt-2 inline-block px-3 py-1 bg-white/20 rounded-full text-xs font-bold uppercase tracking-wider">
                Term 1 Results
              </div>
            </div>
          </div>
        </div>

        {/* Grades Table */}
        <div className="p-8">
          <table className="w-full mb-8">
            <thead>
              <tr className="text-left border-b-2 border-gray-100">
                <th className="pb-4 font-bold text-gray-600">Subject Name</th>
                <th className="pb-4 font-bold text-gray-600 text-center">Score</th>
                <th className="pb-4 font-bold text-gray-600 text-center">Out of</th>
                <th className="pb-4 font-bold text-gray-600 text-right">Grade</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-50">
              {student.grades.map((grade, idx) => {
                const percentage = (grade.score / grade.maxScore) * 100;
                let letter = 'F';
                if (percentage >= 90) letter = 'A+';
                else if (percentage >= 80) letter = 'A';
                else if (percentage >= 70) letter = 'B';
                else if (percentage >= 60) letter = 'C';

                return (
                  <tr key={idx} className="group hover:bg-indigo-50/30 transition-colors">
                    <td className="py-4 font-semibold text-gray-800">{grade.subject}</td>
                    <td className="py-4 text-center font-medium text-gray-700">{grade.score}</td>
                    <td className="py-4 text-center text-gray-400">{grade.maxScore}</td>
                    <td className="py-4 text-right">
                      <span className={`font-black text-lg ${percentage >= 80 ? 'text-emerald-500' : 'text-amber-500'}`}>
                        {letter}
                      </span>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          {/* AI Insights & Teacher Comments */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="bg-gray-50 rounded-3xl p-6 border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-bold text-gray-700 flex items-center gap-2">
                  <i className="fas fa-robot text-indigo-500"></i>
                  AI Performance Insights
                </h4>
                <button 
                  onClick={handleGenerateComment}
                  disabled={isGenerating}
                  className="text-xs bg-indigo-600 text-white px-3 py-1.5 rounded-full font-bold hover:bg-indigo-700 disabled:opacity-50 transition-all shadow-sm"
                >
                  {isGenerating ? 'Analyzing...' : 'Refresh AI Analysis'}
                </button>
              </div>
              <div className="text-gray-600 text-sm leading-relaxed italic">
                {aiComment || "Click 'Refresh AI Analysis' to generate personalized terminal comments based on recent performance data."}
              </div>
            </div>

            <div className="bg-gray-50 rounded-3xl p-6 border border-gray-100 flex flex-col">
              <h4 className="font-bold text-gray-700 mb-4 flex items-center gap-2">
                <i className="fas fa-calendar-check text-emerald-500"></i>
                Attendance Summary
              </h4>
              <div className="flex-1 flex items-center justify-around text-center">
                <div>
                  <p className="text-2xl font-black text-indigo-600">
                    {Object.values(student.attendance).filter(v => v === 'PRESENT').length}
                  </p>
                  <p className="text-xs font-bold text-gray-400 uppercase">Present</p>
                </div>
                <div className="w-px h-10 bg-gray-200"></div>
                <div>
                  <p className="text-2xl font-black text-rose-500">
                    {Object.values(student.attendance).filter(v => v === 'ABSENT').length}
                  </p>
                  <p className="text-xs font-bold text-gray-400 uppercase">Absent</p>
                </div>
                <div className="w-px h-10 bg-gray-200"></div>
                <div>
                  <p className="text-2xl font-black text-gray-700">92%</p>
                  <p className="text-xs font-bold text-gray-400 uppercase">Rate</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Report Footer */}
        <div className="bg-gray-50 p-8 border-t border-gray-100 flex justify-between items-center">
          <div className="flex items-center gap-4">
            <div className="w-10 h-10 rounded-full bg-gray-200"></div>
            <div>
              <p className="text-sm font-bold text-gray-700">Dr. Sarah Thompson</p>
              <p className="text-xs text-gray-500 font-medium">Head Teacher Signature</p>
            </div>
          </div>
          <div className="text-right">
            <p className="text-xs text-gray-400 font-bold uppercase mb-1">Generated On</p>
            <p className="text-sm font-bold text-gray-700">Oct 24, 2023</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReportCard;

import React, { useState, useCallback } from 'react';
import { View, Student, Teacher, AttendanceStatus } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [teachers, setTeachers] = useState<Teacher[]>(MOCK_TEACHERS);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return {
          ...s,
          attendance: { ...s.attendance, [today]: status }
        };
      }
      return s;
    }));
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return <Dashboard students={students} teachers={teachers} />;
      case 'students':
        return <StudentList students={students} onAttendanceChange={handleAttendanceChange} />;
      case 'teachers':
        return (
