import React, { useState, useMemo } from 'react';
import { Student, Grade } from '../types';
import { SUBJECTS } from '../constants';

interface GradingProps {
  students: Student[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
}

const Grading: React.FC<GradingProps> = ({ students, onGradeUpdate }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0]);
  const [selectedTerm, setSelectedTerm] = useState(1);
  const [localScores, setLocalScores] = useState<Record<string, string>>({});
  const [isSaving, setIsSaving] = useState(false);

  // Filter students who should have grades for the selected criteria
  // For this demo, we assume all students take all subjects
  const studentGrades = useMemo(() => {
    return students.map(student => {
      const grade = student.grades.find(g => g.subject === selectedSubject && g.term === selectedTerm);
      return {
        id: student.id,
        name: student.name,
        avatar: student.avatar,
        gradeLevel: student.gradeLevel,
        currentScore: grade?.score ?? 0,
        maxScore: grade?.maxScore ?? 100
      };
    });
  }, [students, selectedSubject, selectedTerm]);

  const handleScoreChange = (studentId: string, val: string) => {
    // Only allow numbers between 0 and 100
    if (val === '' || (Number(val) >= 0 && Number(val) <= 100)) {
      setLocalScores(prev => ({ ...prev, [studentId]: val }));
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 800));

    Object.entries(localScores).forEach(([studentId, score]) => {
      if (score !== '') {
        onGradeUpdate(studentId, selectedSubject, selectedTerm, Number(score));
      }
    });

    setLocalScores({});
    setIsSaving(false);
    alert('Grades updated successfully!');
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-10">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Grading Management</h1>
          <p className="text-gray-500">Record and update student assessments for the current term.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
            {[1, 2, 3].map(term => (
              <button
                key={term}
                onClick={() => setSelectedTerm(term)}
                className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${
                  selectedTerm === term ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                Term {term}
              </button>
            ))}
          </div>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            className="bg-white border-none rounded-2xl px-4 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-medium"
          >
            {SUBJECTS.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="glass-card rounded-3xl overflow-hidden shadow-sm">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Student</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Grade Level</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center">Previous Score</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center w-40">New Score</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-right">Performance</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {studentGrades.map((item) => {
              const currentVal = localScores[item.id] !== undefined ? localScores[item.id] : '';
              const displayScore = currentVal !== '' ? Number(currentVal) : item.currentScore;

              return (
                <tr key={item.id} className="hover:bg-gray-50/50 transition-colors group">
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={item.avatar} className="w-10 h-10 rounded-xl object-cover" alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{item.name}</p>
                        <p className="text-xs text-gray-500">ID: {item.id}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-sm text-gray-600 font-medium">
                    {item.gradeLevel}
                  </td>
                  <td className="px-6 py-5 text-center">
                    <span className="text-gray-400 font-medium">{item.currentScore}</span>
                    <span className="text-gray-300 mx-1">/</span>
                    <span className="text-gray-300">{item.maxScore}</span>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex justify-center">
                      <input
                        type="number"
                        min="0"
                        max="100"
                        placeholder={item.currentScore.toString()}
                        value={currentVal}
                        onChange={(e) => handleScoreChange(item.id, e.target.value)}
                        className="w-20 text-center py-2 bg-gray-50 border-2 border-transparent focus:border-indigo-500 focus:bg-white rounded-xl outline-none font-bold text-indigo-600 transition-all"
                      />
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex items-center justify-end gap-2">
                      <div className="w-24 bg-gray-100 h-2 rounded-full overflow-hidden">
                        <div 
                          className={`h-full transition-all duration-500 ${displayScore >= 75 ? 'bg-emerald-500' : displayScore >= 50 ? 'bg-amber-500' : 'bg-rose-500'}`}
                          style={{ width: `${displayScore}%` }}
                        ></div>
                      </div>
                      <span className="text-sm font-bold text-gray-700 w-10">{displayScore}%</span>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>

        <div className="p-6 bg-gray-50/50 border-t border-gray-100 flex justify-end">
          <button
            onClick={handleSave}
            disabled={isSaving || Object.keys(localScores).length === 0}
            className="bg-indigo-600 text-white px-8 py-3 rounded-2xl font-bold hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-lg shadow-indigo-200 flex items-center gap-2"
          >
            {isSaving ? (
              <>
                <i className="fas fa-spinner fa-spin"></i>
                Saving...
              </>
            ) : (
              <>
                <i className="fas fa-save"></i>
                Save Term {selectedTerm} Grades
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Grading;

import React, { useState, useCallback } from 'react';
import { View, Student, Teacher, AttendanceStatus } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [teachers, setTeachers] = useState<Teacher[]>(MOCK_TEACHERS);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return {
          ...s,
          attendance: { ...s.attendance, [today]: status }
        };
      }
      return s;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];

        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }

        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return <Dashboard students={students} teachers={teachers} />;
      case 'students':
        return <StudentList students={students} onAttendanceChange={handleAttendanceChange} />;
      case 'teachers':
        return (
          <div className="flex flex-col items-center justify-center h-[60vh] text-center space-y-4">
            <div className="w-24 h-24 bg-purple-100 rounded-full flex items-center justify-center text-4xl text-purple-600">
              <i className="fas fa-chalkboard-teacher"></i>
            </div>
            <h2 className="text-2xl font-bold text-gray-800">Teacher Management</h2>
            <p className="text-gray-500 max-w-md">Staff management module is currently under maintenance. You can still view statistics on the dashboard.</p>
          </div>
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return <ReportCard students={students} />;
      default:
        return <Dashboard students={students} teachers={teachers} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search anything..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="relative group cursor-pointer">
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-rose-500 border-2 border-white rounded-full text-[10px] text-white flex items-center justify-center font-bold">
                3
              </div>
              <i className="fas fa-bell text-gray-400 text-xl group-hover:text-indigo-600 transition-colors"></i>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://picsum.photos/seed/admin/100/100" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Admin Portal</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">Super Administrator</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar {
          display: none;
        }
        .scrollbar-hide {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.4s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

