              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center">Previous Score</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center w-64">New Score & Progress</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-right">Rating</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {studentGrades.map((item) => {
              const currentVal = localScores[item.id] !== undefined ? localScores[item.id] : '';
              const displayScore = currentVal !== '' ? Number(currentVal) : item.currentScore;

              return (
                <tr key={item.id} className="hover:bg-gray-50/50 transition-colors group">
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={item.avatar} className="w-12 h-12 rounded-2xl object-cover border-2 border-transparent group-hover:border-indigo-100 transition-all" alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{item.name}</p>
                        <p className="text-xs text-gray-500 font-semibold uppercase">{item.gradeLevel}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-center">
                    <div className="inline-flex items-center justify-center w-12 h-12 rounded-full bg-gray-100 text-gray-500 font-bold text-sm">
                      {item.currentScore}
                    </div>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex flex-col gap-2">
                      <div className="flex items-center gap-3">
                        <input
                          type="number"
                          min="0"
                          max="100"
                          placeholder={item.currentScore.toString()}
                          value={currentVal}
                          onChange={(e) => handleScoreChange(item.id, e.target.value)}
                          className="w-20 text-center py-2 bg-gray-50 border-2 border-transparent focus:border-indigo-500 focus:bg-white rounded-xl outline-none font-bold text-indigo-600 transition-all"
                        />
                        <span className="text-sm font-bold text-gray-400">/ 100</span>
                      </div>
                      <div className="w-full h-2 bg-gray-100 rounded-full overflow-hidden relative">
                        <div 
                          className={`absolute inset-y-0 left-0 transition-all duration-500 ease-out ${getStatusColor(displayScore)}`}
                          style={{ width: `${displayScore}%` }}
                        ></div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex flex-col items-end">
                      <span className={`text-sm font-black px-3 py-1 rounded-full ${getStatusColor(displayScore).replace('bg-', 'text-').replace('500', '600')} ${getStatusColor(displayScore).replace('bg-', 'bg-')}/10`}>
                        {displayScore}%
                      </span>
                      <span className="text-[10px] font-bold text-gray-400 uppercase tracking-widest mt-1">
                        {getStatusText(displayScore)}
                      </span>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>

        <div className="p-6 bg-gray-50/50 border-t border-gray-100 flex justify-end">
          <button
            onClick={handleSave}
            disabled={isSaving || Object.keys(localScores).length === 0}
            className="bg-indigo-600 text-white px-8 py-4 rounded-2xl font-bold hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-lg shadow-indigo-200 flex items-center gap-3 active:scale-95"
          >
            {isSaving ? (
              <>
                <i className="fas fa-circle-notch fa-spin"></i>
                Processing...
              </>
            ) : (
              <>
                <i className="fas fa-check-double"></i>
                Commit Term {selectedTerm} Grades
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

