import React, { useState, useCallback } from 'react';
import { View, Student, Teacher, AttendanceStatus } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [teachers, setTeachers] = useState<Teacher[]>(MOCK_TEACHERS);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return {
          ...s,
          attendance: { ...s.attendance, [today]: status }
        };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return {
          ...t,
          attendance: { ...t.attendance, [today]: status }
        };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];

        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }

        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return <Dashboard students={students} teachers={teachers} />;
      case 'students':
        return <StudentList students={students} onAttendanceChange={handleAttendanceChange} />;
      case 'teachers':
        return <TeacherList teachers={teachers} onAttendanceChange={handleTeacherAttendanceChange} />;
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return <ReportCard students={students} />;
      default:
        return <Dashboard students={students} teachers={teachers} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search anything..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="relative group cursor-pointer">
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-rose-500 border-2 border-white rounded-full text-[10px] text-white flex items-center justify-center font-bold">
                3
              </div>
              <i className="fas fa-bell text-gray-400 text-xl group-hover:text-indigo-600 transition-colors"></i>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://picsum.photos/seed/admin/100/100" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Admin Portal</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">Super Administrator</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar {
          display: none;
        }
        .scrollbar-hide {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.4s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useMemo } from 'react';
import { Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateReportComment } from '../services/geminiService';

interface ReportCardProps {
  students: Student[];
}

const ReportCard: React.FC<ReportCardProps> = ({ students }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [selectedTerm, setSelectedTerm] = useState<number>(1);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>(SUBJECTS);
  const [aiComment, setAiComment] = useState<string>('');
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);

  const student = students.find(s => s.id === selectedStudentId);

  const filteredGrades = useMemo(() => {
    if (!student) return [];
    return student.grades.filter(g => 
      g.term === selectedTerm && 
      selectedSubjects.includes(g.subject)
    );
  }, [student, selectedTerm, selectedSubjects]);

  const attendanceStats = useMemo(() => {
    if (!student) return { present: 0, absent: 0, rate: 0 };
    const attendanceValues = Object.values(student.attendance);
    const present = attendanceValues.filter(v => v === AttendanceStatus.PRESENT).length;
    const total = attendanceValues.length || 1;
    const absent = attendanceValues.filter(v => v === AttendanceStatus.ABSENT).length;
    return {
      present,
      absent,
      rate: Math.round((present / total) * 100)
    };
  }, [student]);

  const toggleSubject = (subject: string) => {
    setSelectedSubjects(prev => 
      prev.includes(subject) 
        ? prev.filter(s => s !== subject) 
        : [...prev, subject]
    );
  };

  const handleGenerateComment = async () => {
    if (!student) return;
    setIsGeneratingAI(true);
    const comment = await generateReportComment({
      ...student,
      grades: filteredGrades // Pass only the filtered context for better AI accuracy
    });
    setAiComment(comment);
    setIsGeneratingAI(false);
  };

  const handleExportPDF = () => {
    setIsExporting(true);
    setExportProgress(0);

    const interval = setInterval(() => {
      setExportProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(() => {
            setIsExporting(false);
            alert(`Report for ${student?.name} (Term ${selectedTerm}) has been generated and downloaded.`);
          }, 500);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  if (!student) return <div className="p-8 text-center text-gray-500">No student selected.</div>;

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Performance Reporting</h1>
          <p className="text-gray-500 mt-1">Configure and generate official terminal academic transcripts.</p>
        </div>

        <div className="flex flex-wrap gap-3">
          <select 
            value={selectedStudentId}
            onChange={(e) => {
              setSelectedStudentId(e.target.value);
              setAiComment('');
            }}
            className="bg-white border-2 border-transparent hover:border-indigo-100 rounded-2xl px-5 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-gray-700 transition-all"
          >
            {students.map(s => (
              <option key={s.id} value={s.id}>{s.name} ({s.id})</option>
            ))}
          </select>

          <button 
            onClick={handleExportPDF}
            disabled={isExporting}
            className="bg-indigo-600 text-white px-8 py-3 rounded-2xl font-bold hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-200 flex items-center gap-2 disabled:opacity-50"
          >
            {isExporting ? <i className="fas fa-spinner fa-spin"></i> : <i className="fas fa-file-pdf"></i>}
            Generate PDF
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-4 gap-8 items-start">
        {/* Configuration Sidebar */}
        <div className="xl:col-span-1 space-y-6">
          <div className="glass-card p-6 rounded-[2rem] shadow-sm border border-white">
            <h3 className="text-sm font-black text-gray-400 uppercase tracking-widest mb-4">Report Settings</h3>

            <div className="space-y-6">
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-3">Academic Term</label>
                <div className="grid grid-cols-3 gap-2">
                  {[1, 2, 3].map(t => (
                    <button
                      key={t}
                      onClick={() => setSelectedTerm(t)}
                      className={`py-2 rounded-xl text-xs font-bold transition-all ${selectedTerm === t ? 'bg-indigo-600 text-white shadow-md' : 'bg-gray-50 text-gray-500 hover:bg-gray-100'}`}
                    >
                      Term {t}
                    </button>
                  ))}
                </div>
              </div>

              <div>
                <label className="block text-sm font-bold text-gray-700 mb-3">Included Subjects</label>
