                      </table>
                    </div>
                  ) : (
                    <div className="p-10 text-center bg-gray-50 rounded-3xl border-2 border-dashed border-gray-200">
                      <i className="fas fa-folder-open text-3xl text-gray-300 mb-3"></i>
                      <p className="text-gray-500 font-bold text-sm">No assessments found for the selected criteria.</p>
                      <button onClick={() => setSelectedSubjects(SUBJECTS)} className="text-xs text-indigo-600 font-bold mt-2 hover:underline">Reset Filters</button>
                    </div>
                  )}
                </div>

                {/* Summary & Insights */}
                <div className="lg:col-span-2 space-y-8">
                  <div className="space-y-4">
                    <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                      <i className="fas fa-chart-pie text-emerald-500"></i>
                      Attendance Trend
                    </h4>
                    <div className="bg-emerald-50 rounded-3xl p-6 border border-emerald-100">
                      <div className="flex items-end justify-between">
                        <div>
                          <p className="text-4xl font-black text-emerald-700">{attendanceStats.rate}%</p>
                          <p className="text-[10px] font-black text-emerald-600 uppercase mt-1 tracking-widest">Presence Rate</p>
                        </div>
                        <div className="text-right">
                          <p className="text-xs font-bold text-emerald-600/60 uppercase">Term Summary</p>
                          <p className="text-xs font-black text-emerald-700">{attendanceStats.present} Days Present</p>
                        </div>
                      </div>
                      <div className="mt-4 w-full h-1.5 bg-emerald-100 rounded-full overflow-hidden">
                        <div className="h-full bg-emerald-500" style={{ width: `${attendanceStats.rate}%` }}></div>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                        <i className="fas fa-brain text-purple-500"></i>
                        AI Commentary
                      </h4>
                      <button 
                        onClick={handleGenerateComment}
