import React, { useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from 'recharts';
import { Student, Teacher } from '../types';
import { SUBJECTS } from '../constants';

interface DashboardProps {
  students: Student[];
  teachers: Teacher[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ students, teachers, onGradeUpdate }) => {
  const [quickGrade, setQuickGrade] = useState({
    studentId: '',
    subject: SUBJECTS[0],
    term: 1,
    score: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);

  const stats = [
    { label: 'Total Students', value: students.length, icon: 'fa-user-graduate', color: 'bg-blue-500' },
    { label: 'Total Teachers', value: teachers.length, icon: 'fa-chalkboard-teacher', color: 'bg-purple-500' },
    { label: 'Avg Attendance', value: '94%', icon: 'fa-calendar-check', color: 'bg-emerald-500' },
    { label: 'Pending Grades', value: '12', icon: 'fa-exclamation-circle', color: 'bg-amber-500' },
  ];

  const attendanceData = [
    { name: 'Mon', value: 92 },
    { name: 'Tue', value: 95 },
    { name: 'Wed', value: 88 },
    { name: 'Thu', value: 96 },
    { name: 'Fri', value: 94 },
  ];

  const handleQuickSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (quickGrade.studentId && quickGrade.score !== '') {
      onGradeUpdate(quickGrade.studentId, quickGrade.subject, quickGrade.term, Number(quickGrade.score));
      setShowSuccess(true);
      setQuickGrade(prev => ({ ...prev, score: '' }));
      setTimeout(() => setShowSuccess(false), 3000);
    }
  };

  return (
    <div className="space-y-8 animate-fadeIn">
      <div className="flex justify-between items-end">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">School Overview</h1>
          <p className="text-gray-500 mt-1">Quick insights into your school's current performance.</p>
        </div>
        <div className="text-right hidden sm:block">
          <p className="text-sm font-medium text-gray-400">Current Date</p>
          <p className="text-lg font-bold text-gray-700">{new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, i) => (
          <div key={i} className="glass-card p-6 rounded-3xl shadow-sm hover:shadow-md transition-shadow duration-300">
            <div className="flex items-center gap-4">
              <div className={`${stat.color} w-12 h-12 rounded-2xl flex items-center justify-center text-white text-xl`}>
                <i className={`fas ${stat.icon}`}></i>
              </div>
              <div>
                <p className="text-sm text-gray-500 font-medium">{stat.label}</p>
                <p className="text-2xl font-bold text-gray-800">{stat.value}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-6">
          <div className="glass-card p-8 rounded-3xl shadow-sm">
            <h3 className="text-xl font-bold text-gray-800 mb-6">Attendance Trend</h3>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={attendanceData}>
                  <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f1f5f9" />
                  <XAxis dataKey="name" axisLine={false} tickLine={false} />
                  <YAxis axisLine={false} tickLine={false} unit="%" />
                  <Tooltip 
                    cursor={{ fill: '#f8fafc' }}
                    contentStyle={{ borderRadius: '12px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }}
                  />
                  <Bar dataKey="value" radius={[6, 6, 0, 0]}>
                    {attendanceData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={index === 3 ? '#6366f1' : '#cbd5e1'} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          <div className="glass-card p-8 rounded-3xl shadow-sm border-2 border-indigo-50">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-gray-800 flex items-center gap-3">
                <i className="fas fa-bolt text-amber-500"></i>
                Quick Grade Entry
              </h3>
              {showSuccess && (
                <span className="text-emerald-500 text-sm font-bold animate-bounce flex items-center gap-1">
                  <i className="fas fa-check-circle"></i> Saved!
                </span>
              )}
            </div>

            <form onSubmit={handleQuickSubmit} className="grid grid-cols-1 md:grid-cols-5 gap-4">
              <div className="md:col-span-1">
                <label className="block text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Student</label>
                <select 
                  value={quickGrade.studentId}
                  onChange={(e) => setQuickGrade({...quickGrade, studentId: e.target.value})}
                  required
                  className="w-full bg-gray-50 border-none rounded-xl px-3 py-2 text-sm focus:ring-2 focus:ring-indigo-500 transition-all outline-none font-medium"
                >
                  <option value="">Select Student</option>
                  {students.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                </select>
              </div>
              <div className="md:col-span-1">
                <label className="block text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Subject</label>
                <select 
                  value={quickGrade.subject}
                  onChange={(e) => setQuickGrade({...quickGrade, subject: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-xl px-3 py-2 text-sm focus:ring-2 focus:ring-indigo-500 transition-all outline-none font-medium"
                >
                  {SUBJECTS.map(s => <option key={s} value={s}>{s}</option>)}
                </select>
              </div>
              <div className="md:col-span-1">
                <label className="block text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Term</label>
                <select 
                  value={quickGrade.term}
                  onChange={(e) => setQuickGrade({...quickGrade, term: Number(e.target.value)})}
                  className="w-full bg-gray-50 border-none rounded-xl px-3 py-2 text-sm focus:ring-2 focus:ring-indigo-500 transition-all outline-none font-medium"
                >
                  {[1, 2, 3].map(t => <option key={t} value={t}>Term {t}</option>)}
                </select>
              </div>
              <div className="md:col-span-1">
                <label className="block text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2">Score (0-100)</label>
                <input 
                  type="number" 
                  min="0" 
                  max="100"
                  value={quickGrade.score}
                  onChange={(e) => setQuickGrade({...quickGrade, score: e.target.value})}
                  placeholder="85"
                  required
                  className="w-full bg-gray-50 border-none rounded-xl px-3 py-2 text-sm focus:ring-2 focus:ring-indigo-500 transition-all outline-none font-bold"
                />
              </div>
              <div className="md:col-span-1 flex items-end">
                <button 
                  type="submit"
                  className="w-full bg-indigo-600 text-white py-2 rounded-xl font-bold hover:bg-indigo-700 active:scale-95 transition-all shadow-md shadow-indigo-200 text-sm"
                >
                  Save Grade
                </button>
              </div>
            </form>
          </div>
        </div>

        <div className="glass-card p-8 rounded-3xl shadow-sm">
          <h3 className="text-xl font-bold text-gray-800 mb-6">Top Performers</h3>
          <div className="space-y-6">
            {students.slice(0, 3).map((student, i) => {
              const avg = student.grades.reduce((acc, curr) => acc + curr.score, 0) / (student.grades.length || 1);
              return (
                <div key={i} className="flex items-center gap-4 group cursor-pointer">
                  <img src={student.avatar} className="w-12 h-12 rounded-full object-cover border-2 border-indigo-100 group-hover:border-indigo-500 transition-colors" alt="" />
                  <div className="flex-1">
                    <p className="font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">{student.name}</p>
                    <p className="text-xs text-gray-500 uppercase font-semibold">{student.gradeLevel}</p>
                  </div>
                  <div className="text-right">
                    <span className="text-indigo-600 font-bold">{avg.toFixed(1)}%</span>
                  </div>
                </div>
              );
            })}
          </div>
          <button className="w-full mt-8 py-3 bg-gray-50 text-gray-600 font-bold rounded-2xl hover:bg-gray-100 transition-colors">
            View All Ranking
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState, useCallback } from 'react';
import { View, Student, Teacher, AttendanceStatus } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [teachers, setTeachers] = useState<Teacher[]>(MOCK_TEACHERS);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return {
          ...s,
          attendance: { ...s.attendance, [today]: status }
        };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return {
          ...s,
          attendance: { ...s.attendance, [today]: status }
        };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return {
          ...t,
          attendance: { ...t.attendance, [today]: status }
        };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return {
          ...t,
          attendance: { ...t.attendance, [today]: status }
        };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];

        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }

        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} />;
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return <ReportCard students={students} />;
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search anything..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="relative group cursor-pointer">
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-rose-500 border-2 border-white rounded-full text-[10px] text-white flex items-center justify-center font-bold">
                3
              </div>
              <i className="fas fa-bell text-gray-400 text-xl group-hover:text-indigo-600 transition-colors"></i>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://picsum.photos/seed/admin/100/100" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Admin Portal</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">Super Administrator</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar {
          display: none;
        }
        .scrollbar-hide {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.4s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useMemo } from 'react';
import { Student, AttendanceStatus } from '../types';

interface StudentListProps {
  students: Student[];
  onAttendanceChange: (studentId: string, status: AttendanceStatus) => void;
  onBulkAttendanceChange: (studentIds: string[], status: AttendanceStatus) => void;
}

const StudentList: React.FC<StudentListProps> = ({ students, onAttendanceChange, onBulkAttendanceChange }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
  const today = new Date().toISOString().split('T')[0];

  const filteredStudents = useMemo(() => {
    return students.filter(student => 
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.id.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [students, searchTerm]);

  const toggleAll = () => {
    if (selectedIds.size === filteredStudents.length) {
      setSelectedIds(new Set());
    } else {
      setSelectedIds(new Set(filteredStudents.map(s => s.id)));
    }
  };

  const toggleOne = (id: string) => {
    const next = new Set(selectedIds);
    if (next.has(id)) {
      next.delete(id);
    } else {
      next.add(id);
    }
    setSelectedIds(next);
  };

  const handleBulkUpdate = (status: AttendanceStatus) => {
    onBulkAttendanceChange(Array.from(selectedIds), status);
    setSelectedIds(new Set());
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-24">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <h1 className="text-3xl font-bold text-gray-800">Student Directory</h1>
        <button className="bg-indigo-600 text-white px-6 py-3 rounded-2xl font-bold hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-200">
          <i className="fas fa-plus mr-2"></i> Add Student
        </button>
      </div>

      <div className="relative group max-w-md">
        <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 group-focus-within:text-indigo-500 transition-colors"></i>
        <input 
          type="text" 
          placeholder="Search by name or ID..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-all"
        />
        {searchTerm && (
          <button 
            onClick={() => setSearchTerm('')}
            className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 transition-colors"
          >
            <i className="fas fa-times-circle"></i>
          </button>
        )}
      </div>

      <div className="glass-card rounded-3xl overflow-hidden shadow-sm relative">
        {filteredStudents.length > 0 ? (
          <table className="w-full text-left">
            <thead className="bg-gray-50 border-b border-gray-100">
              <tr>
                <th className="px-6 py-4 w-12">
                  <input 
                    type="checkbox" 
                    className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer"
                    checked={selectedIds.size > 0 && selectedIds.size === filteredStudents.length}
                    ref={el => {
                      if (el) el.indeterminated = selectedIds.size > 0 && selectedIds.size < filteredStudents.length;
                    }}
                    onChange={toggleAll}
                  />
                </th>
                <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Student</th>
                <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Grade & Section</th>
                <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center">Today's Attendance</th>
                <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-right">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-100">
              {filteredStudents.map((student) => {
                const currentStatus = student.attendance[today] || null;
                const isSelected = selectedIds.has(student.id);

                return (
                  <tr key={student.id} className={`transition-colors group ${isSelected ? 'bg-indigo-50/50' : 'hover:bg-gray-50/50'}`}>
                    <td className="px-6 py-5">
                      <input 
                        type="checkbox" 
                        className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer"
                        checked={isSelected}
                        onChange={() => toggleOne(student.id)}
                      />
                    </td>
                    <td className="px-6 py-5">
                      <div className="flex items-center gap-4">
                        <img src={student.avatar} className="w-12 h-12 rounded-2xl object-cover" alt="" />
                        <div>
                          <p className="font-bold text-gray-800">{student.name}</p>
                          <p className="text-sm text-gray-500">ID: {student.id}</p>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-5">
                      <span className="px-3 py-1 bg-indigo-50 text-indigo-700 rounded-full text-xs font-bold">
                        {student.gradeLevel} - {student.section}
                      </span>
                    </td>
                    <td className="px-6 py-5">
                      <div className="flex justify-center gap-2">
                        <button 
                          onClick={() => onAttendanceChange(student.id, AttendanceStatus.PRESENT)}
                          title="Mark Present"
                          className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${
                            currentStatus === AttendanceStatus.PRESENT 
                              ? 'bg-emerald-500 text-white shadow-lg' 
                              : 'bg-gray-100 text-gray-400 hover:bg-emerald-50'
                          }`}
                        >
                          <i className="fas fa-check"></i>
                        </button>
                        <button 
                          onClick={() => onAttendanceChange(student.id, AttendanceStatus.ABSENT)}
                          title="Mark Absent"
                          className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${
                            currentStatus === AttendanceStatus.ABSENT 
                              ? 'bg-rose-500 text-white shadow-lg' 
                              : 'bg-gray-100 text-gray-400 hover:bg-rose-50'
                          }`}
                        >
                          <i className="fas fa-times"></i>
                        </button>
                      </div>
                    </td>
                    <td className="px-6 py-5 text-right">
                      <button className="text-gray-400 hover:text-indigo-600 p-2 rounded-xl hover:bg-white transition-all">
                        <i className="fas fa-ellipsis-v"></i>
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <div className="p-12 text-center">
            <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4 text-gray-400">
              <i className="fas fa-search text-2xl"></i>
            </div>
            <p className="text-gray-500 font-medium">No students found matching "{searchTerm}"</p>
            <button 
              onClick={() => setSearchTerm('')}
              className="mt-2 text-indigo-600 font-bold hover:underline"
            >
              Clear search
            </button>
          </div>
        )}
      </div>

      {/* Floating Bulk Action Bar */}
      {selectedIds.size > 0 && (
