import React, { useState, useMemo } from 'react';
import { Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateReportComment } from '../services/geminiService';

interface ReportCardProps {
  students: Student[];
}

const ReportCard: React.FC<ReportCardProps> = ({ students }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [selectedTerm, setSelectedTerm] = useState<number>(1);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>(SUBJECTS);
  const [aiComment, setAiComment] = useState<string>('');
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);

  const student = students.find(s => s.id === selectedStudentId);

  const filteredGrades = useMemo(() => {
    if (!student) return [];
    return student.grades.filter(g => 
      g.term === selectedTerm && 
      selectedSubjects.includes(g.subject)
    );
  }, [student, selectedTerm, selectedSubjects]);

  const attendanceStats = useMemo(() => {
    if (!student) return { present: 0, absent: 0, rate: 0 };
    const attendanceValues = Object.values(student.attendance);
    const present = attendanceValues.filter(v => v === AttendanceStatus.PRESENT).length;
    const total = attendanceValues.length || 1;
    const absent = attendanceValues.filter(v => v === AttendanceStatus.ABSENT).length;
    return {
      present,
      absent,
      rate: Math.round((present / total) * 100)
    };
  }, [student]);

  const toggleSubject = (subject: string) => {
    setSelectedSubjects(prev => 
      prev.includes(subject) 
        ? prev.filter(s => s !== subject) 
        : [...prev, subject]
    );
  };

  const handleGenerateComment = async () => {
    if (!student) return;
    setIsGeneratingAI(true);
    const comment = await generateReportComment({
      ...student,
      grades: filteredGrades // Pass only the filtered context for better AI accuracy
    });
    setAiComment(comment);
    setIsGeneratingAI(false);
  };

  const handleExportPDF = () => {
    setIsExporting(true);
    setExportProgress(0);

    const interval = setInterval(() => {
      setExportProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(() => {
            setIsExporting(false);
            alert(`Report for ${student?.name} (Term ${selectedTerm}) has been generated and downloaded.`);
          }, 500);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  if (!student) return <div className="p-8 text-center text-gray-500">No student selected.</div>;

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Performance Reporting</h1>
          <p className="text-gray-500 mt-1">Configure and generate official terminal academic transcripts.</p>
        </div>

        <div className="flex flex-wrap gap-3">
          <select 
            value={selectedStudentId}
            onChange={(e) => {
              setSelectedStudentId(e.target.value);
              setAiComment('');
            }}
            className="bg-white border-2 border-transparent hover:border-indigo-100 rounded-2xl px-5 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-gray-700 transition-all"
          >
            {students.map(s => (
              <option key={s.id} value={s.id}>{s.name} ({s.id})</option>
            ))}
          </select>

          <button 
            onClick={handleExportPDF}
            disabled={isExporting}
            className="bg-indigo-600 text-white px-8 py-3 rounded-2xl font-bold hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-200 flex items-center gap-2 disabled:opacity-50"
          >
            {isExporting ? <i className="fas fa-spinner fa-spin"></i> : <i className="fas fa-file-pdf"></i>}
            Generate PDF
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-4 gap-8 items-start">
        {/* Configuration Sidebar */}
        <div className="xl:col-span-1 space-y-6">
          <div className="glass-card p-6 rounded-[2rem] shadow-sm border border-white">
            <h3 className="text-sm font-black text-gray-400 uppercase tracking-widest mb-4">Report Settings</h3>

            <div className="space-y-6">
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-3">Academic Term</label>
                <div className="grid grid-cols-3 gap-2">
                  {[1, 2, 3].map(t => (
                    <button
                      key={t}
                      onClick={() => setSelectedTerm(t)}
                      className={`py-2 rounded-xl text-xs font-bold transition-all ${selectedTerm === t ? 'bg-indigo-600 text-white shadow-md' : 'bg-gray-50 text-gray-500 hover:bg-gray-100'}`}
                    >
                      Term {t}
                    </button>
                  ))}
                </div>
              </div>

              <div>
                <label className="block text-sm font-bold text-gray-700 mb-3">Included Subjects</label>
                <div className="flex flex-wrap gap-2">
                  {SUBJECTS.map(s => (
                    <button
                      key={s}
                      onClick={() => toggleSubject(s)}
                      className={`px-3 py-1.5 rounded-full text-[10px] font-black uppercase tracking-wider transition-all border-2 ${selectedSubjects.includes(s) ? 'bg-indigo-50 border-indigo-200 text-indigo-700' : 'bg-transparent border-gray-100 text-gray-400 hover:border-gray-200'}`}
                    >
                      {s}
                    </button>
                  ))}
                </div>
              </div>

              <div className="pt-4 border-t border-gray-100">
                <button 
                  onClick={() => setSelectedSubjects(SUBJECTS)}
                  className="text-xs font-bold text-indigo-600 hover:text-indigo-800 transition-colors mr-4"
                >
                  Select All
                </button>
                <button 
                  onClick={() => setSelectedSubjects([])}
                  className="text-xs font-bold text-gray-400 hover:text-gray-600 transition-colors"
                >
                  Clear All
                </button>
              </div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-indigo-600 to-purple-700 p-6 rounded-[2rem] text-white shadow-xl">
            <div className="flex items-center gap-3 mb-4">
              <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center">
                <i className="fas fa-lightbulb"></i>
              </div>
              <h4 className="font-bold">Pro Tip</h4>
            </div>
            <p className="text-xs text-indigo-100 leading-relaxed">
              Use the AI Analysis feature to generate context-aware comments based on specific term performance and subject trends.
            </p>
          </div>
        </div>

        {/* Report Preview Area */}
        <div className="xl:col-span-3 space-y-6">
          {isExporting && (
            <div className="glass-card p-4 rounded-2xl border-indigo-100 border-2 animate-pulse">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-bold text-indigo-600">Generating High-Quality Transcript...</span>
                <span className="text-xs font-black text-indigo-400">{exportProgress}%</span>
              </div>
              <div className="w-full h-2 bg-indigo-50 rounded-full overflow-hidden">
                <div className="h-full bg-indigo-600 transition-all duration-300" style={{ width: `${exportProgress}%` }}></div>
              </div>
            </div>
          )}

          <div className="bg-white rounded-[2.5rem] shadow-2xl overflow-hidden border border-gray-100 max-w-5xl mx-auto relative group">
            <div className="absolute top-0 left-0 w-2 h-full bg-indigo-600"></div>

            {/* Report Header */}
            <div className="bg-gray-50/50 p-10 border-b border-gray-100 relative">
              <div className="absolute top-0 right-0 m-10 opacity-5 pointer-events-none">
                <i className="fas fa-certificate text-[12rem]"></i>
              </div>

              <div className="flex flex-col md:flex-row justify-between items-start gap-8 relative z-10">
                <div className="flex items-center gap-8">
                  <div className="relative">
                    <img src={student.avatar} className="w-28 h-28 rounded-3xl object-cover shadow-xl border-4 border-white" alt="" />
                    <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-emerald-500 rounded-lg flex items-center justify-center text-white border-2 border-white shadow-lg">
                      <i className="fas fa-check text-xs"></i>
                    </div>
                  </div>
                  <div>
                    <h2 className="text-3xl font-black text-gray-800 tracking-tight">{student.name}</h2>
                    <div className="flex gap-2 mt-2">
                      <span className="px-3 py-1 bg-indigo-600 text-white text-[10px] font-black uppercase rounded-full tracking-widest">
                        {student.gradeLevel}
                      </span>
                      <span className="px-3 py-1 bg-gray-200 text-gray-600 text-[10px] font-black uppercase rounded-full tracking-widest">
                        Section {student.section}
                      </span>
                    </div>
                    <p className="text-gray-400 text-xs mt-3 font-medium">Student ID: <span className="text-indigo-600">{student.id}</span></p>
                  </div>
                </div>

                <div className="md:text-right">
                  <h3 className="text-xl font-black text-indigo-900 tracking-tighter uppercase">CEDAR CREST INTERNATIONAL SCHOOL</h3>
                  <p className="text-gray-400 text-xs font-bold uppercase tracking-widest mt-1">Official Academic Transcript</p>
                  <div className="mt-4 inline-flex items-center gap-3 bg-white px-4 py-2 rounded-2xl shadow-sm border border-gray-100">
                    <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
                    <span className="text-xs font-black text-gray-700">TERM {selectedTerm} - 2024</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Performance Content */}
            <div className="p-10">
              <div className="grid grid-cols-1 lg:grid-cols-5 gap-12">
                {/* Grades Table */}
                <div className="lg:col-span-3 space-y-6">
                  <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                    <i className="fas fa-list-ul text-indigo-500"></i>
                    Subject Assessments
                  </h4>

                  {filteredGrades.length > 0 ? (
                    <div className="overflow-hidden rounded-3xl border border-gray-100">
                      <table className="w-full text-left">
                        <thead className="bg-gray-50 border-b border-gray-100">
                          <tr>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Subject</th>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Score</th>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Grade</th>
                          </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                          {filteredGrades.map((grade, idx) => {
                            const percent = (grade.score / grade.maxScore) * 100;
                            let gradeLetter = 'F';
                            let gradeColor = 'text-rose-500';
                            if (percent >= 90) { gradeLetter = 'A+'; gradeColor = 'text-emerald-500'; }
                            else if (percent >= 80) { gradeLetter = 'A'; gradeColor = 'text-emerald-600'; }
                            else if (percent >= 70) { gradeLetter = 'B'; gradeColor = 'text-indigo-500'; }
                            else if (percent >= 60) { gradeLetter = 'C'; gradeColor = 'text-amber-500'; }

                            return (
                              <tr key={idx} className="hover:bg-indigo-50/30 transition-colors group">
                                <td className="px-6 py-4">
                                  <p className="font-bold text-gray-800">{grade.subject}</p>
                                  <p className="text-[10px] text-gray-400 font-bold uppercase">Assessment {idx + 1}</p>
                                </td>
                                <td className="px-6 py-4 text-center">
                                  <span className="font-black text-gray-700">{grade.score}</span>
                                  <span className="text-gray-300 mx-1">/</span>
                                  <span className="text-gray-400 text-xs">{grade.maxScore}</span>
                                </td>
                                <td className="px-6 py-4 text-right">
                                  <span className={`text-lg font-black ${gradeColor}`}>{gradeLetter}</span>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  ) : (
                    <div className="p-10 text-center bg-gray-50 rounded-3xl border-2 border-dashed border-gray-200">
                      <i className="fas fa-folder-open text-3xl text-gray-300 mb-3"></i>
                      <p className="text-gray-500 font-bold text-sm">No assessments found for the selected criteria.</p>
                      <button onClick={() => setSelectedSubjects(SUBJECTS)} className="text-xs text-indigo-600 font-bold mt-2 hover:underline">Reset Filters</button>
                    </div>
                  )}
                </div>

                {/* Summary & Insights */}
                <div className="lg:col-span-2 space-y-8">
                  <div className="space-y-4">
                    <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                      <i className="fas fa-chart-pie text-emerald-500"></i>
                      Attendance Trend
                    </h4>
                    <div className="bg-emerald-50 rounded-3xl p-6 border border-emerald-100">
                      <div className="flex items-end justify-between">
                        <div>
                          <p className="text-4xl font-black text-emerald-700">{attendanceStats.rate}%</p>
                          <p className="text-[10px] font-black text-emerald-600 uppercase mt-1 tracking-widest">Presence Rate</p>
                        </div>
                        <div className="text-right">
                          <p className="text-xs font-bold text-emerald-600/60 uppercase">Term Summary</p>
                          <p className="text-xs font-black text-emerald-700">{attendanceStats.present} Days Present</p>
                        </div>
                      </div>
                      <div className="mt-4 w-full h-1.5 bg-emerald-100 rounded-full overflow-hidden">
                        <div className="h-full bg-emerald-500" style={{ width: `${attendanceStats.rate}%` }}></div>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                        <i className="fas fa-brain text-purple-500"></i>
                        AI Commentary
                      </h4>
                      <button 
                        onClick={handleGenerateComment}
                        disabled={isGeneratingAI || filteredGrades.length === 0}
                        className="text-[10px] font-black text-purple-600 uppercase tracking-widest hover:text-purple-800 disabled:opacity-30"
                      >
                        {isGeneratingAI ? 'Processing...' : 'Recalculate'}
                      </button>
                    </div>
                    <div className="bg-purple-50 rounded-3xl p-6 border border-purple-100 relative min-h-[140px]">
                      {isGeneratingAI && (
                        <div className="absolute inset-0 bg-purple-50/80 backdrop-blur-[2px] rounded-3xl flex items-center justify-center z-10">
                          <i className="fas fa-circle-notch fa-spin text-purple-600 text-2xl"></i>
                        </div>
                      )}
                      <p className="text-sm text-purple-900 leading-relaxed font-medium italic">
                        "{aiComment || "Personalized academic feedback will appear here after analysis. Click recalculate to begin."}"
                      </p>
                      <div className="mt-4 flex items-center gap-2">
                        <div className="w-6 h-6 rounded-full bg-purple-200 flex items-center justify-center text-[10px] text-purple-700">
                          <i className="fas fa-sparkles"></i>
                        </div>
                        <span className="text-[10px] font-bold text-purple-400 uppercase tracking-tighter">Powered by Gemini AI Engine</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Verification Footer */}
            <div className="bg-gray-50 p-10 border-t border-gray-100 flex flex-col md:flex-row justify-between items-center gap-8">
              <div className="flex items-center gap-6">
                <div className="text-center">
                  <div className="w-24 h-12 border-b-2 border-gray-300 mb-2 font-handwriting text-gray-400 italic text-sm flex items-end justify-center">
                    S. Thompson
                  </div>
                  <p className="text-[10px] font-black text-gray-400 uppercase tracking-widest">Head of School</p>
                </div>
                <div className="w-px h-12 bg-gray-200"></div>
                <div className="text-center">
                  <div className="w-24 h-12 border-b-2 border-gray-300 mb-2 flex items-center justify-center">
                    <i className="fas fa-stamp text-gray-200 text-xl"></i>
                  </div>
                  <p className="text-[10px] font-black text-gray-400 uppercase tracking-widest">Official Seal</p>
                </div>
              </div>

              <div className="text-center md:text-right">
                <p className="text-[10px] font-black text-gray-300 uppercase tracking-tighter mb-1">Authenticity Code</p>
                <p className="text-xs font-mono text-gray-400 tracking-widest">ES-2024-TR-{student.id}-{selectedTerm}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <style>{`
        @font-face {
          font-family: 'Handwriting';
          src: local('Dancing Script'), local('cursive');
        }
        .font-handwriting {
          font-family: 'Handwriting', cursive;
        }
      `}</style>
    </div>
  );
};

export default ReportCard;

