import React, { useState, useMemo, useRef } from 'react';
import { Student, AttendanceStatus } from '../types';

interface StudentListProps {
  students: Student[];
  onAttendanceChange: (studentId: string, status: AttendanceStatus) => void;
  onBulkAttendanceChange: (studentIds: string[], status: AttendanceStatus) => void;
  onAddStudent: (student: Student) => void;
}

const StudentList: React.FC<StudentListProps> = ({ students, onAttendanceChange, onBulkAttendanceChange, onAddStudent }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [newStudent, setNewStudent] = useState({
    name: '',
    id: '',
    gradeLevel: 'Grade 10',
    section: 'A',
    avatar: ''
  });

  const fileInputRef = useRef<HTMLInputElement>(null);
  const today = new Date().toISOString().split('T')[0];

  const filteredStudents = useMemo(() => {
    return students.filter(student => 
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.id.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [students, searchTerm]);

  const toggleAll = () => {
    if (selectedIds.size === filteredStudents.length) {
      setSelectedIds(new Set());
    } else {
      setSelectedIds(new Set(filteredStudents.map(s => s.id)));
    }
  };

  const toggleOne = (id: string) => {
    const next = new Set(selectedIds);
    if (next.has(id)) {
      next.delete(id);
    } else {
      next.add(id);
    }
    setSelectedIds(next);
  };

  const handleBulkUpdate = (status: AttendanceStatus) => {
    onBulkAttendanceChange(Array.from(selectedIds), status);
    setSelectedIds(new Set());
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setNewStudent(prev => ({ ...prev, avatar: reader.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onAddStudent({
      ...newStudent,
      grades: [],
      attendance: {},
      avatar: newStudent.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(newStudent.name)}&background=random`
    });
    setIsAddModalOpen(false);
    setNewStudent({ name: '', id: '', gradeLevel: 'Grade 10', section: 'A', avatar: '' });
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-24">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Student Directory</h1>
          <p className="text-gray-500">Manage student profiles and track daily attendance.</p>
        </div>
        <button 
          onClick={() => setIsAddModalOpen(true)}
          className="bg-indigo-600 text-white px-6 py-3 rounded-2xl font-bold hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-200 active:scale-95"
        >
          <i className="fas fa-plus mr-2"></i> Register New Student
        </button>
      </div>

      <div className="relative group max-w-md">
        <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 group-focus-within:text-indigo-500 transition-colors"></i>
        <input 
          type="text" 
          placeholder="Search by name or ID..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-all"
        />
      </div>

      <div className="glass-card rounded-3xl overflow-hidden shadow-sm relative">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 w-12">
                <input 
                  type="checkbox" 
                  className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer"
                  checked={selectedIds.size > 0 && selectedIds.size === filteredStudents.length}
                  onChange={toggleAll}
                />
              </th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Student</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Grade & Section</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center">Daily Status</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {filteredStudents.map((student) => {
              const currentStatus = student.attendance[today] || null;
              const isSelected = selectedIds.has(student.id);

              return (
                <tr key={student.id} className={`transition-colors group ${isSelected ? 'bg-indigo-50/50' : 'hover:bg-gray-50/50'}`}>
                  <td className="px-6 py-5">
                    <input 
                      type="checkbox" 
                      className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer"
                      checked={isSelected}
                      onChange={() => toggleOne(student.id)}
                    />
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={student.avatar} className="w-12 h-12 rounded-2xl object-cover shadow-sm" alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{student.name}</p>
                        <p className="text-xs text-gray-400 font-mono">{student.id}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5">
                    <span className="px-3 py-1 bg-indigo-50 text-indigo-700 rounded-full text-xs font-bold uppercase tracking-wider">
                      {student.gradeLevel} - {student.section}
                    </span>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex justify-center gap-2">
                      <button 
                        onClick={() => onAttendanceChange(student.id, AttendanceStatus.PRESENT)}
                        className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${currentStatus === AttendanceStatus.PRESENT ? 'bg-emerald-500 text-white' : 'bg-gray-100 text-gray-400 hover:bg-emerald-50'}`}
                      >
                        <i className="fas fa-check"></i>
                      </button>
                      <button 
                        onClick={() => onAttendanceChange(student.id, AttendanceStatus.ABSENT)}
                        className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${currentStatus === AttendanceStatus.ABSENT ? 'bg-rose-500 text-white' : 'bg-gray-100 text-gray-400 hover:bg-rose-50'}`}
                      >
                        <i className="fas fa-times"></i>
                      </button>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <button className="text-gray-300 hover:text-indigo-600 p-2"><i className="fas fa-ellipsis-h"></i></button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Floating Action Bar */}
      {selectedIds.size > 0 && (
        <div className="fixed bottom-8 left-1/2 -translate-x-1/2 bg-indigo-900 text-white px-8 py-4 rounded-[2rem] shadow-2xl flex items-center gap-6 border border-indigo-700 z-50 animate-fadeIn">
          <span className="text-sm font-bold">{selectedIds.size} Students Selected</span>
          <div className="h-6 w-px bg-indigo-700"></div>
          <button onClick={() => handleBulkUpdate(AttendanceStatus.PRESENT)} className="text-xs font-black uppercase tracking-widest text-emerald-400 hover:text-white transition-colors">Mark Present</button>
          <button onClick={() => handleBulkUpdate(AttendanceStatus.ABSENT)} className="text-xs font-black uppercase tracking-widest text-rose-400 hover:text-white transition-colors">Mark Absent</button>
          <button onClick={() => setSelectedIds(new Set())} className="text-xs font-bold text-gray-400">Cancel</button>
        </div>
      )}

      {/* Add Student Modal */}
      {isAddModalOpen && (
        <div className="fixed inset-0 bg-indigo-900/60 backdrop-blur-sm z-[100] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-xl overflow-hidden animate-slideUp">
            <div className="bg-indigo-600 p-8 text-white flex justify-between items-center">
              <div>
                <h2 className="text-2xl font-black tracking-tight">Register Student</h2>
                <p className="text-indigo-100 text-xs font-bold uppercase tracking-widest mt-1">New Enrollment Form</p>
              </div>
              <button onClick={() => setIsAddModalOpen(false)} className="w-10 h-10 bg-white/10 rounded-full hover:bg-white/20 transition-colors">
                <i className="fas fa-times"></i>
              </button>
            </div>

            <form onSubmit={handleSubmit} className="p-10 space-y-6">
              <div className="flex flex-col items-center gap-4 mb-8">
                <div 
                  onClick={() => fileInputRef.current?.click()}
                  className="w-24 h-24 rounded-[2rem] bg-gray-50 border-2 border-dashed border-gray-200 flex items-center justify-center cursor-pointer hover:border-indigo-400 hover:bg-indigo-50 transition-all overflow-hidden group"
                >
                  {newStudent.avatar ? (
                    <img src={newStudent.avatar} className="w-full h-full object-cover" alt="Preview" />
                  ) : (
                    <div className="text-center group-hover:scale-110 transition-transform">
                      <i className="fas fa-camera text-gray-300 text-xl"></i>
                      <p className="text-[8px] font-black text-gray-400 uppercase mt-1">Upload Photo</p>
                    </div>
                  )}
                </div>
                <input ref={fileInputRef} type="file" accept="image/*" onChange={handleFileChange} className="hidden" />
              </div>

              <div className="grid grid-cols-2 gap-6">
                <div className="col-span-2">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Full Name</label>
                  <input required value={newStudent.name} onChange={e => setNewStudent({...newStudent, name: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold" placeholder="e.g. John Doe" />
                </div>
                <div>
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Student ID</label>
                  <input required value={newStudent.id} onChange={e => setNewStudent({...newStudent, id: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-mono text-sm" placeholder="S2024-001" />
                </div>
                <div>
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Grade Level</label>
                  <select value={newStudent.gradeLevel} onChange={e => setNewStudent({...newStudent, gradeLevel: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold">
                    {[7,8,9,10,11,12].map(g => <option key={g} value={`Grade ${g}`}>Grade {g}</option>)}
                  </select>
                </div>
              </div>

              <button type="submit" className="w-full bg-indigo-600 text-white py-4 rounded-[1.5rem] font-black uppercase tracking-[0.2em] shadow-xl shadow-indigo-100 hover:bg-indigo-700 active:scale-95 transition-all mt-4">
                Confirm Registration
              </button>
            </form>
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideUp { from { transform: translateY(50px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
        .animate-slideUp { animation: slideUp 0.4s cubic-bezier(0.16, 1, 0.3, 1) forwards; }
      `}</style>
    </div>
  );
};

