            <div className="p-10">
              <div className="grid grid-cols-1 lg:grid-cols-5 gap-12">
                {/* Grades Table */}
                <div className="lg:col-span-3 space-y-6">
                  <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2">
                    <i className="fas fa-list-ul text-orange-500"></i>
                    Subject Assessments
                  </h4>

                  {filteredGrades.length > 0 ? (
                    <div className="overflow-hidden rounded-3xl border border-gray-100">
                      <table className="w-full text-left">
                        <thead className="bg-gray-50 border-b border-gray-100">
                          <tr>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Subject</th>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Score</th>
                            <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Grade</th>
                          </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                          {filteredGrades.map((grade, idx) => {
                            const percent = (grade.score / grade.maxScore) * 100;
                            let gradeLetter = 'F';
                            let gradeColor = 'text-rose-500';
                            if (percent >= 90) { gradeLetter = 'A+'; gradeColor = 'text-emerald-500'; }
                            else if (percent >= 80) { gradeLetter = 'A'; gradeColor = 'text-emerald-600'; }
                            else if (percent >= 70) { gradeLetter = 'B'; gradeColor = 'text-indigo-500'; }
                            else if (percent >= 60) { gradeLetter = 'C'; gradeColor = 'text-amber-500'; }

                            return (
                              <tr key={idx} className="hover:bg-indigo-50/30 transition-colors group">
                                <td className="px-6 py-4">
                                  <p className="font-bold text-gray-800">{grade.subject}</p>
                                  <p className="text-[10px] text-gray-400 font-bold uppercase">Assessment {idx + 1}</p>
                                </td>
                                <td className="px-6 py-4 text-center">
                                  <span className="font-black text-gray-700">{grade.score}</span>
                                  <span className="text-gray-300 mx-1">/</span>
                                  <span className="text-gray-400 text-xs">{grade.maxScore}</span>
                                </td>
                                <td className="px-6 py-4 text-right">
                                  <span className={`text-lg font-black ${gradeColor}`}>{gradeLetter}</span>
                                </td>
                              </tr>
                            );
                          })}
