                      </table>
                    </div>
                  ) : (
                    <div className="p-10 text-center bg-gray-50 rounded-3xl border-2 border-dashed border-gray-200">
                      <i className="fas fa-folder-open text-3xl text-gray-300 mb-3"></i>
