    studentId: '',
    subject: SUBJECTS[0],
    term: 1,
    score: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const totalStudents = students.length;
  const totalTeachers = teachers.length;

  const attendanceData = [
    { name: 'Mon', value: 92 },
    { name: 'Tue', value: 95 },
    { name: 'Wed', value: 88 },
    { name: 'Thu', value: 96 },
    { name: 'Fri', value: 94 },
  ];

  const stats = [
    { label: 'Total Students', value: totalStudents, icon: 'fa-user-graduate', color: 'bg-indigo-500' },
    { label: 'Total Teachers', value: totalTeachers, icon: 'fa-chalkboard-teacher', color: 'bg-purple-500' },
    { label: 'Avg Attendance', value: '94%', icon: 'fa-calendar-check', color: 'bg-emerald-500' },
    { label: 'School Rating', value: '4.9', icon: 'fa-star', color: 'bg-amber-500' },
  ];

  const handleQuickSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (quickGrade.studentId && quickGrade.score !== '') {
      onGradeUpdate(quickGrade.studentId, quickGrade.subject, quickGrade.term, Number(quickGrade.score));
      setShowSuccess(true);
      setQuickGrade(prev => ({ ...prev, score: '' }));
      setTimeout(() => setShowSuccess(false), 3000);
