                <div><label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Staff ID</label><input required value={newTeacher.id} onChange={e => setNewTeacher({...newTeacher, id: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-purple-500 outline-none font-mono text-sm" placeholder="FAC-772" /></div>
                <div><label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Department</label><select value={newTeacher.department} onChange={e => setNewTeacher({...newTeacher, department: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-purple-500 outline-none font-bold">
