                  </label>
                  <div className="group relative">
                    <i className="fas fa-info-circle text-gray-300 hover:text-indigo-400 cursor-help transition-colors"></i>
                    <div className="absolute bottom-full right-0 mb-2 w-48 p-3 bg-gray-900 text-white text-[10px] rounded-xl opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none z-50 shadow-xl leading-relaxed">
                      Select assessment period.
                      <div className="absolute top-full right-2 border-8 border-transparent border-t-gray-900"></div>
                    </div>
                  </div>
                </div>
                <div className="grid grid-cols-3 gap-2 bg-gray-50 p-1.5 rounded-2xl border border-gray-100">
                  {[1, 2, 3].map(t => (
                    <button key={t} onClick={() => setSelectedTerm(t)} className={`py-2.5 rounded-xl text-xs font-black transition-all duration-300 ${selectedTerm === t ? 'bg-white text-indigo-600 shadow-md' : 'text-gray-400 hover:text-gray-600'}`}>Term {t}</button>
                  ))}
                </div>
              </div>
              <div>
                <label className="block text-xs font-black text-gray-700 uppercase tracking-tighter mb-4 flex items-center gap-2"><i className="fas fa-book-open text-orange-500"></i>Included Subjects</label>
                <div className="flex flex-wrap gap-2 max-h-56 overflow-y-auto pr-2 scrollbar-hide bg-gray-50/50 p-3 rounded-2xl border border-gray-50">
                  {SUBJECTS.map(s => (
                    <button key={s} onClick={() => setSelectedSubjects(prev => prev.includes(s) ? prev.filter(x => x !== s) : [...prev, s])} className={`px-3 py-2 rounded-xl text-[10px] font-black uppercase transition-all border-2 ${selectedSubjects.includes(s) ? 'bg-white border-indigo-200 text-indigo-700 shadow-sm' : 'bg-transparent border-transparent text-gray-400'}`}>{s}</button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="xl:col-span-3 space-y-6">
          <div className="bg-white rounded-[2.5rem] shadow-2xl overflow-hidden border border-gray-100 max-w-5xl mx-auto relative group">
            <div className="absolute top-0 left-0 w-2 h-full bg-indigo-600"></div>
            <div className="bg-gray-50/50 p-10 border-b border-gray-100 relative">
              <div className="flex flex-col md:flex-row justify-between items-start gap-8 relative z-10">
                <div className="flex items-center gap-8">
                  <div className="relative">
                    <img src={student.avatar} className="w-28 h-28 rounded-3xl object-cover shadow-xl border-4 border-white" alt="" />
                    <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-emerald-500 rounded-lg flex items-center justify-center text-white border-2 border-white shadow-lg"><i className="fas fa-check text-xs"></i></div>
                  </div>
                  <div>
                    <h2 className="text-3xl font-black text-gray-800 tracking-tight">{student.name}</h2>
                    <div className="flex gap-2 mt-2">
                      <span className="px-3 py-1 bg-indigo-600 text-white text-[10px] font-black uppercase rounded-full tracking-widest">{student.gradeLevel}</span>
                      <span className="px-3 py-1 bg-gray-200 text-gray-600 text-[10px] font-black uppercase rounded-full tracking-widest">Section {student.section}</span>
                    </div>
                    <p className="text-gray-400 text-xs mt-3 font-medium">Student ID: <span className="text-indigo-600">{student.id}</span></p>
                  </div>
                </div>
                <div className="md:text-right flex flex-col items-center md:items-end">
                  <div className="flex items-center gap-4 mb-3">
                    <div className="w-16 h-16 bg-white rounded-full p-1 border-2 border-orange-500 shadow-md">
                      <img src={schoolLogo} alt="Logo" className="w-full h-full object-cover rounded-full" />
                    </div>
                    <div className="text-left md:text-right">
                      <h3 className="text-xl font-black text-indigo-900 tracking-tighter uppercase leading-tight">CEDAR CREST</h3>
                      <p className="text-[10px] font-black text-orange-600 uppercase tracking-[0.2em]">International School</p>
                    </div>
                  </div>
                  <div className="mt-4 inline-flex items-center gap-3 bg-white px-4 py-2 rounded-2xl shadow-sm border border-gray-100">
                    <span className="text-xs font-black text-gray-700 uppercase">TERM {selectedTerm} ASSESSMENT</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="p-10">
              <div className="grid grid-cols-1 lg:grid-cols-5 gap-12">
                <div className="lg:col-span-3 space-y-6">
                  <h4 className="text-sm font-black text-gray-400 uppercase tracking-widest flex items-center gap-2"><i className="fas fa-list-ul text-orange-500"></i>Subject Assessments</h4>
                  {filteredGrades.length > 0 ? (
                    <div className="overflow-hidden rounded-3xl border border-gray-100">
                      <table className="w-full text-left">
                        <thead className="bg-gray-50 border-b border-gray-100">
                          <tr><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Subject</th><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Score</th><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Grade</th></tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                          {filteredGrades.map((grade, idx) => {
                            const percent = (grade.score / grade.maxScore) * 100;
                            let gradeLetter = percent >= 90 ? 'A+' : percent >= 80 ? 'A' : percent >= 70 ? 'B' : percent >= 60 ? 'C' : 'F';
                            return (
                              <tr key={idx} className="hover:bg-indigo-50/30 transition-colors">
