                  <p className="text-[10px] font-black text-gray-800 uppercase tracking-widest">{reportSettings.headOfSchool}</p>
                  <p className="text-[8px] font-bold text-gray-400 uppercase tracking-tighter">Head of School</p>
                </div>
                <div className="text-center">
                   <div className="w-20 h-20 mb-2 opacity-60">
                     <img src={schoolLogo} className="w-full h-full object-contain rounded-full grayscale" />
                   </div>
                   <p className="text-[10px] font-black text-gray-400 uppercase tracking-widest">Official Seal</p>
                </div>
              </div>
              <div className="text-center md:text-right">
                <p className="text-[10px] font-black text-gray-300 uppercase tracking-tighter mb-1">Authenticity Code</p>
                <p className="text-xs font-mono text-gray-400 tracking-widest">{reportSettings.authPrefix}-{student.id}-{selectedTerm}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {isSettingsOpen && (
        <div className="fixed inset-0 bg-indigo-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-xl overflow-hidden">
            <div className="bg-orange-600 p-8 text-white flex justify-between items-center">
              <div><h2 className="text-2xl font-black">Verification Settings</h2><p className="text-orange-100 text-xs font-bold uppercase mt-1">Configure Report Authenticity</p></div>
              <button onClick={() => setIsSettingsOpen(false)} className="w-10 h-10 bg-white/10 rounded-full hover:bg-white/20 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <div className="p-10 space-y-6">
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Head of School Name</label>
                <input value={reportSettings.headOfSchool} onChange={e => onUpdateReportSettings({...reportSettings, headOfSchool: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-bold" />
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Authenticity Code Prefix</label>
                <input value={reportSettings.authPrefix} onChange={e => onUpdateReportSettings({...reportSettings, authPrefix: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-mono" />
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Principal Signature / School Stamp</label>
                <div onClick={() => sigInputRef.current?.click()} className="w-full h-32 bg-gray-50 border-2 border-dashed border-gray-200 rounded-2xl flex items-center justify-center cursor-pointer hover:border-orange-400 transition-all overflow-hidden group">
                  {reportSettings.signature ? <img src={reportSettings.signature} className="max-h-full max-w-full object-contain" /> : <div className="text-center group-hover:scale-105 transition-transform"><i className="fas fa-pen-nib text-gray-300 text-2xl"></i><p className="text-[8px] font-black text-gray-400 uppercase mt-2">Upload Image</p></div>}
                </div>
                <input ref={sigInputRef} type="file" accept="image/*" className="hidden" onChange={handleSigChange} />
              </div>
              <button onClick={() => setIsSettingsOpen(false)} className="w-full bg-orange-600 text-white py-4 rounded-[1.5rem] font-black uppercase tracking-widest shadow-xl hover:bg-orange-700 active:scale-95 transition-all">Save Verification Settings</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

