import React, { useState, useCallback, useEffect, useRef } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  useEffect(() => {
    localStorage.setItem('cc_students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem('cc_events', JSON.stringify(events));
  }, [events]);

  useEffect(() => {
    localStorage.setItem('cc_school_logo', schoolLogo);
  }, [schoolLogo]);

  useEffect(() => {
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
  }, [reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students? This action cannot be undone.`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Delete this event?')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <Dashboard 
            students={students} 
            teachers={teachers} 
            onGradeUpdate={handleGradeUpdate} 
            schoolLogo={schoolLogo}
            onUpdateLogo={setSchoolLogo}
            events={events}
            onAddEvent={handleAddEvent}
            onUpdateEvent={handleUpdateEvent}
            onDeleteEvent={handleDeleteEvent}
          />
        );
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
            onAddStudent={handleAddStudent}
            onDeleteStudent={handleDeleteStudent}
            onBulkDeleteStudents={handleBulkDeleteStudents}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
            onAddTeacher={handleAddTeacher}
            onDeleteTeacher={handleDeleteTeacher}
            onBulkDeleteTeachers={handleBulkDeleteTeachers}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return (
          <ReportCard 
            students={students} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={setReportSettings}
          />
        );
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search directory..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-emerald-100 shadow-sm">
              <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
              <span className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">System Online</span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://ui-avatars.com/api/?name=Admin&background=6366f1&color=fff" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Super Admin</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">CCIS Portal</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import { GoogleGenAI } from "@google/genai";
import { Student, SchoolEvent } from "../types";

export const generateReportComment = async (student: Student): Promise<string> => {
  try {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

    const gradesSummary = student.grades.length > 0 
      ? student.grades.map(g => `${g.subject} (${g.score}/${g.maxScore})`).join(', ')
      : "No grades recorded for this term.";

    const attendanceCount = Object.values(student.attendance).filter(a => a === 'PRESENT').length;
    const totalDays = Object.keys(student.attendance).length;
    const attendanceRate = totalDays > 0 ? Math.round((attendanceCount / totalDays) * 100) : 100;

    const prompt = `
      As a professional school principal at Cedar Crest International School, write a concise (2-4 sentences) terminal report comment for the student: ${student.name}.

      Analyze the following data to provide a nuanced and personalized comment:
      - Academic Performance: ${gradesSummary}
      - Attendance: ${attendanceCount} days present out of ${totalDays} (${attendanceRate}% attendance rate).

      Guidelines:
      1. Mention the attendance rate. If it's excellent (>=95%), praise their commitment. If it's concerning (<90%), gently emphasize the importance of regular attendance for academic success.
      2. Explicitly identify their strongest subject(s) based on the scores provided.
      3. Identify any subjects where there is room for growth or more focus needed.
      4. Use a tone that is professional, encouraging, and specific. Avoid generic "well done" statements.

      Response should be a single paragraph.
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });

    return response.text || "The student has demonstrated a positive attitude toward learning. We look forward to their continued progress in the next term.";
  } catch (error) {
    console.error("Error generating comment:", error);
    return "The student is making steady progress across the curriculum. Continued focus on subject fundamentals and consistent attendance is recommended for future academic success.";
  }
};

export const generateEventEmail = async (event: SchoolEvent): Promise<string> => {
  try {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
    const prompt = `
      As the Administrative Office of Cedar Crest International School, draft a professional and inviting email to student guardians about an upcoming event.

      Event Details:
      - Title: ${event.title}
      - Date: ${event.date}
      - Time: ${event.time}
      - Location: ${event.location}
      - Description: ${event.description}

      Guidelines:
      1. Start with a professional greeting.
      2. Clearly state the purpose of the event.
      3. Encourage attendance and explain why it is important for the school community.
      4. Provide the logistics clearly.
      5. End with a professional sign-off from "CCIS Administration".

      Keep the email concise and suitable for a school setting.
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });

    return response.text || "Professional email draft could not be generated.";
  } catch (error) {
    console.error("Error generating email:", error);
    return `Dear Guardians,\n\nWe invite you to our upcoming event: ${event.title}.\n\nDate: ${event.date}\nTime: ${event.time}\nLocation: ${event.location}\n\nWe look forward to seeing you there.\n\nBest regards,\nCCIS Administration`;
  }
};

