                  <input ref={fileInputRef} type="file" accept="image/*" className="hidden" onChange={handleLogoChange} />
                </div>
                <div>
                  <p className="text-xs font-black text-gray-700 uppercase">School Badge</p>
                  <button onClick={() => fileInputRef.current?.click()} className="text-[10px] font-bold text-indigo-600 hover:text-indigo-800 uppercase tracking-widest mt-1">Replace Image</button>
                </div>
              </div>
