                <option value="">Select Student</option>
                {students.map(s => <option key={s.id} value={s.id}>{s.name} ({s.id})</option>)}
              </select>
              <div className="grid grid-cols-2 gap-4">
                <select value={quickGrade.term} onChange={e => setQuickGrade({...quickGrade, term: Number(e.target.value)})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold">
                  {[1, 2, 3].map(t => <option key={t} value={t}>Term {t}</option>)}
                </select>
                <input type="number" max="100" min="0" placeholder="Score %" value={quickGrade.score} onChange={e => setQuickGrade({...quickGrade, score: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-center" />
              </div>
              <button type="submit" className="w-full bg-gray-800 text-white py-4 rounded-2xl font-black uppercase tracking-widest text-xs hover:bg-black transition-all">Record Grade</button>
            </form>
          </div>

          <div className="bg-white p-8 rounded-[2.5rem] shadow-sm border border-gray-100">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-gray-800">School Events</h3>
              <button onClick={() => openEventModal()} className="w-8 h-8 bg-indigo-50 text-indigo-600 rounded-full flex items-center justify-center hover:bg-indigo-100 transition-colors"><i className="fas fa-plus text-xs"></i></button>
            </div>
            <div className="space-y-6">
              {events.length === 0 ? (
                <p className="text-gray-400 text-xs italic text-center py-4">No upcoming events scheduled.</p>
              ) : events.map((event) => (
                <div key={event.id} className="flex flex-col gap-3 group relative">
                  <div className="flex items-center gap-4">
                    <div className={`${event.bg} ${event.color} w-14 h-14 rounded-2xl flex flex-col items-center justify-center font-black flex-shrink-0`}>
                      <span className="text-xs">{event.date.split('-')[2]}</span>
                      <span className="text-[10px] uppercase">{new Date(event.date).toLocaleString('default', { month: 'short' })}</span>
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-bold text-gray-700 truncate">{event.title}</p>
                      <p className="text-[10px] text-gray-400 font-bold uppercase tracking-wider">{event.time} - {event.location}</p>
                    </div>
                    <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                      <button onClick={() => openEventModal(event)} className="text-gray-400 hover:text-indigo-600"><i className="fas fa-edit text-xs"></i></button>
                      <button onClick={() => onDeleteEvent(event.id)} className="text-gray-400 hover:text-rose-600"><i className="fas fa-trash text-xs"></i></button>
                    </div>
                  </div>
                  <button 
                    onClick={() => handleNotifyGuardians(event)}
                    className="text-[9px] font-black uppercase tracking-[0.2em] text-indigo-600 hover:text-indigo-800 flex items-center gap-2 self-start pl-18 ml-18"
                  >
                    <i className="fas fa-paper-plane"></i>
                    Notify Guardians
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Event Edit/Add Modal */}
      {isEventModalOpen && (
        <div className="fixed inset-0 bg-indigo-900/40 backdrop-blur-sm z-[200] flex items-center justify-center p-6">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-lg overflow-hidden animate-slideUp">
            <div className="bg-indigo-600 p-8 text-white flex justify-between items-center">
              <div>
                <h2 className="text-2xl font-black">{editingEvent ? 'Edit Event' : 'Add New Event'}</h2>
                <p className="text-indigo-100 text-xs font-bold uppercase mt-1">School Calendar Management</p>
              </div>
              <button onClick={() => setIsEventModalOpen(false)} className="text-white hover:text-indigo-200 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={handleSaveEvent} className="p-10 space-y-5">
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Event Title</label>
                <input required value={newEvent.title} onChange={e => setNewEvent({...newEvent, title: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold" />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Date</label>
                  <input required type="date" value={newEvent.date} onChange={e => setNewEvent({...newEvent, date: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-medium" />
                </div>
                <div>
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Time</label>
                  <input required type="time" value={newEvent.time} onChange={e => setNewEvent({...newEvent, time: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-medium" />
                </div>
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Location</label>
                <input required value={newEvent.location} onChange={e => setNewEvent({...newEvent, location: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold" />
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Short Description</label>
                <textarea value={newEvent.description} onChange={e => setNewEvent({...newEvent, description: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-medium h-24 resize-none" />
              </div>
              <button type="submit" className="w-full bg-indigo-600 text-white py-4 rounded-3xl font-black uppercase tracking-widest shadow-xl active:scale-95 transition-all">
                {editingEvent ? 'Update Event' : 'Schedule Event'}
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Email Notification Preview Modal */}
      {isEmailModalOpen && (activeEventForEmail) && (
        <div className="fixed inset-0 bg-gray-900/60 backdrop-blur-sm z-[300] flex items-center justify-center p-6">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-2xl overflow-hidden animate-slideUp">
            <div className="bg-gray-800 p-8 text-white flex justify-between items-center">
              <div>
                <h2 className="text-xl font-black">Notify Guardians</h2>
                <p className="text-gray-400 text-[10px] font-bold uppercase tracking-widest mt-1">Review Email Draft for: {activeEventForEmail.title}</p>
              </div>
              <button onClick={() => setIsEmailModalOpen(false)} className="text-white hover:text-gray-400 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <div className="p-10 space-y-6">
              <div className="bg-gray-50 p-6 rounded-[2rem] border border-gray-100 min-h-[300px] relative">
                {isGeneratingEmail ? (
                  <div className="absolute inset-0 flex flex-col items-center justify-center gap-4">
                    <i className="fas fa-circle-notch fa-spin text-3xl text-indigo-500"></i>
                    <p className="text-xs font-black text-indigo-600 uppercase tracking-widest">Drafting Professional Message...</p>
                  </div>
                ) : (
                  <textarea 
                    value={draftEmail}
                    onChange={(e) => setDraftEmail(e.target.value)}
                    className="w-full h-full bg-transparent border-none outline-none text-gray-700 text-sm leading-relaxed resize-none scrollbar-hide font-medium"
                  />
                )}
              </div>

              <div className="flex items-center gap-4 text-amber-600 bg-amber-50 p-4 rounded-2xl border border-amber-100">
                <i className="fas fa-info-circle"></i>
                <p className="text-[10px] font-bold">Sending this will notify all {students.length} registered student guardians via their official email addresses.</p>
              </div>

              <div className="flex gap-4">
                <button 
                  onClick={() => setIsEmailModalOpen(false)} 
                  className="flex-1 bg-gray-100 text-gray-600 py-4 rounded-3xl font-black uppercase tracking-widest active:scale-95 transition-all"
                >
                  Cancel
                </button>
                <button 
                  onClick={handleSendEmails}
                  disabled={isGeneratingEmail || isSending}
                  className="flex-1 bg-indigo-600 text-white py-4 rounded-3xl font-black uppercase tracking-widest shadow-xl active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-3"
                >
                  {isSending ? (
                    <>
                      <i className="fas fa-circle-notch fa-spin"></i>
                      Queueing...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-paper-plane"></i>
                      Send Now
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideUp { from { transform: translateY(50px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
        .animate-slideUp { animation: slideUp 0.4s cubic-bezier(0.16, 1, 0.3, 1) forwards; }
      `}</style>
    </div>
  );
};

export default Dashboard;

export enum AttendanceStatus {
  PRESENT = 'PRESENT',
  ABSENT = 'ABSENT',
  LATE = 'LATE',
}

export interface Grade {
  subject: string;
  score: number;
  maxScore: number;
  term: number;
}

export interface Student {
  id: string;
  name: string;
  gradeLevel: string;
  section: string;
  avatar: string;
  guardianEmail: string;
  grades: Grade[];
  attendance: Record<string, AttendanceStatus>;
}

export interface Teacher {
  id: string;
  name: string;
  department: string;
  email: string;
  avatar: string;
  attendance: Record<string, AttendanceStatus>;
}

export interface SchoolEvent {
  id: string;
  title: string;
  date: string;
  time: string;
  location: string;
  description: string;
  color: string;
  bg: string;
}

export interface SchoolStats {
  totalStudents: number;
  totalTeachers: number;
  avgAttendance: number;
  topPerformers: number;
}

export type View = 'dashboard' | 'students' | 'teachers' | 'grading' | 'reports';

import React, { useState, useCallback, useEffect, useRef } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  useEffect(() => {
    localStorage.setItem('cc_students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem('cc_events', JSON.stringify(events));
  }, [events]);

  useEffect(() => {
    localStorage.setItem('cc_school_logo', schoolLogo);
  }, [schoolLogo]);

  useEffect(() => {
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
  }, [reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students? This action cannot be undone.`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Delete this event?')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <Dashboard 
            students={students} 
            teachers={teachers} 
            onGradeUpdate={handleGradeUpdate} 
            schoolLogo={schoolLogo}
            onUpdateLogo={setSchoolLogo}
            events={events}
            onAddEvent={handleAddEvent}
            onUpdateEvent={handleUpdateEvent}
            onDeleteEvent={handleDeleteEvent}
          />
        );
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
            onAddStudent={handleAddStudent}
            onDeleteStudent={handleDeleteStudent}
            onBulkDeleteStudents={handleBulkDeleteStudents}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
            onAddTeacher={handleAddTeacher}
            onUpdateTeacher={handleUpdateTeacher}
            onDeleteTeacher={handleDeleteTeacher}
            onBulkDeleteTeachers={handleBulkDeleteTeachers}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return (
          <ReportCard 
            students={students} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={setReportSettings}
          />
        );
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search directory..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-emerald-100 shadow-sm">
              <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
              <span className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">System Online</span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://ui-avatars.com/api/?name=Admin&background=6366f1&color=fff" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Super Admin</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">CCIS Portal</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

