import React, { useState, useCallback, useEffect, useRef } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  useEffect(() => {
    localStorage.setItem('cc_students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem('cc_events', JSON.stringify(events));
  }, [events]);

  useEffect(() => {
    localStorage.setItem('cc_school_logo', schoolLogo);
  }, [schoolLogo]);

  useEffect(() => {
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
  }, [reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students? This action cannot be undone.`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Are you sure you want to delete this event? This will remove it from the school calendar.')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <Dashboard 
            students={students} 
            teachers={teachers} 
            onGradeUpdate={handleGradeUpdate} 
            schoolLogo={schoolLogo}
            onUpdateLogo={setSchoolLogo}
            events={events}
            onAddEvent={handleAddEvent}
            onUpdateEvent={handleUpdateEvent}
            onDeleteEvent={handleDeleteEvent}
          />
        );
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
            onAddStudent={handleAddStudent}
            onDeleteStudent={handleDeleteStudent}
            onBulkDeleteStudents={handleBulkDeleteStudents}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
            onAddTeacher={handleAddTeacher}
            onUpdateTeacher={handleUpdateTeacher}
            onDeleteTeacher={handleDeleteTeacher}
            onBulkDeleteTeachers={handleBulkDeleteTeachers}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return (
          <ReportCard 
            students={students} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={setReportSettings}
          />
        );
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search directory..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow text-black font-medium"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-emerald-100 shadow-sm">
              <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
              <span className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">System Online</span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://ui-avatars.com/api/?name=Admin&background=6366f1&color=fff" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Super Admin</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">CCIS Portal</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useRef } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from 'recharts';
import { Student, Teacher, SchoolEvent } from '../types';
import { SUBJECTS } from '../constants';
import { generateEventEmail } from '../services/geminiService';

interface DashboardProps {
  students: Student[];
  teachers: Teacher[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
  schoolLogo: string;
  onUpdateLogo: (url: string) => void;
  events: SchoolEvent[];
  onAddEvent: (event: SchoolEvent) => void;
  onUpdateEvent: (event: SchoolEvent) => void;
  onDeleteEvent: (id: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ 
  students, 
  teachers, 
  onGradeUpdate, 
  schoolLogo, 
  onUpdateLogo,
  events,
  onAddEvent,
  onUpdateEvent,
  onDeleteEvent
}) => {
  const [quickGrade, setQuickGrade] = useState({
    studentId: '',
    subject: SUBJECTS[0],
    term: 1,
    score: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);
  const [isEventModalOpen, setIsEventModalOpen] = useState(false);
  const [isEmailModalOpen, setIsEmailModalOpen] = useState(false);
  const [editingEvent, setEditingEvent] = useState<SchoolEvent | null>(null);
  const [newEvent, setNewEvent] = useState<Partial<SchoolEvent>>({
    title: '', date: '', time: '', location: '', description: '', color: 'text-indigo-600', bg: 'bg-indigo-50'
  });
  const [draftEmail, setDraftEmail] = useState('');
  const [isGeneratingEmail, setIsGeneratingEmail] = useState(false);
  const [activeEventForEmail, setActiveEventForEmail] = useState<SchoolEvent | null>(null);
  const [isSending, setIsSending] = useState(false);

  const fileInputRef = useRef<HTMLInputElement>(null);

  const stats = [
    { label: 'Total Students', value: students.length, icon: 'fa-user-graduate', color: 'bg-indigo-500' },
    { label: 'Total Teachers', value: teachers.length, icon: 'fa-chalkboard-teacher', color: 'bg-purple-500' },
    { label: 'Avg Attendance', value: '94%', icon: 'fa-calendar-check', color: 'bg-emerald-500' },
    { label: 'School Rating', value: '4.9', icon: 'fa-star', color: 'bg-amber-500' },
  ];

  const handleQuickSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (quickGrade.studentId && quickGrade.score !== '') {
      onGradeUpdate(quickGrade.studentId, quickGrade.subject, quickGrade.term, Number(quickGrade.score));
      setShowSuccess(true);
      setQuickGrade(prev => ({ ...prev, score: '' }));
      setTimeout(() => setShowSuccess(false), 3000);
    }
  };

  const handleLogoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => onUpdateLogo(reader.result as string);
      reader.readAsDataURL(file);
    }
  };

  const openEventModal = (event?: SchoolEvent) => {
    if (event) {
      setEditingEvent(event);
      setNewEvent(event);
    } else {
      setEditingEvent(null);
      setNewEvent({ title: '', date: '', time: '', location: '', description: '', color: 'text-indigo-600', bg: 'bg-indigo-50' });
    }
    setIsEventModalOpen(true);
  };

  const handleSaveEvent = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingEvent) {
      onUpdateEvent(newEvent as SchoolEvent);
    } else {
      onAddEvent({ ...newEvent, id: Date.now().toString() } as SchoolEvent);
    }
    setIsEventModalOpen(false);
  };

  const handleNotifyGuardians = async (event: SchoolEvent) => {
    setActiveEventForEmail(event);
    setIsEmailModalOpen(true);
    setIsGeneratingEmail(true);
    const draft = await generateEventEmail(event);
    setDraftEmail(draft);
    setIsGeneratingEmail(false);
  };

  const handleSendEmails = () => {
    setIsSending(true);
    setTimeout(() => {
      setIsSending(false);
      setIsEmailModalOpen(false);
      alert(`Notifications for "${activeEventForEmail?.title}" have been successfully queued for delivery to all student guardians.`);
    }, 1500);
  };

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      <div className="flex justify-between items-end">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Administrative Hub</h1>
          <p className="text-gray-500 mt-1 font-medium">Overview of Cedar Crest International School operations.</p>
        </div>
        <div className="text-right hidden sm:block">
          <p className="text-sm font-medium text-gray-400">Current Session</p>
          <p className="text-lg font-bold text-indigo-900">Academic Year 2024/2025</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, i) => (
          <div key={i} className="glass-card p-6 rounded-3xl shadow-sm hover:shadow-md transition-all duration-300 group hover:-translate-y-1">
            <div className="flex items-center gap-4">
              <div className={`${stat.color} w-12 h-12 rounded-2xl flex items-center justify-center text-white text-xl transition-transform group-hover:scale-110 shadow-lg`}>
                <i className={`fas ${stat.icon}`}></i>
              </div>
              <div>
                <p className="text-[10px] text-gray-400 font-black uppercase tracking-widest">{stat.label}</p>
                <p className="text-3xl font-black text-gray-800">{stat.value}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-8">
          <div className="glass-card p-8 rounded-[2.5rem] shadow-sm">
            <h3 className="text-xl font-bold text-gray-800 mb-8">Attendance Trends</h3>
            <div className="h-[300px] w-full">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={[{ name: 'Mon', value: 92 }, { name: 'Tue', value: 95 }, { name: 'Wed', value: 88 }, { name: 'Thu', value: 96 }, { name: 'Fri', value: 94 }]}>
                  <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f1f5f9" />
                  <XAxis dataKey="name" axisLine={false} tickLine={false} tick={{fill: '#94a3b8', fontSize: 12, fontWeight: 700}} dy={10} />
                  <YAxis axisLine={false} tickLine={false} tick={{fill: '#94a3b8', fontSize: 12, fontWeight: 700}} domain={[0, 100]} />
                  <Tooltip cursor={{fill: '#f8fafc'}} contentStyle={{borderRadius: '1rem', border: 'none'}} />
                  <Bar dataKey="value" radius={[8, 8, 8, 8]} barSize={40}>
                    <Cell fill="#6366f1" />
                    <Cell fill="#6366f1" />
                    <Cell fill="#a5b4fc" />
                    <Cell fill="#6366f1" />
                    <Cell fill="#6366f1" />
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            <div className="bg-white p-8 rounded-[2rem] border border-gray-100 shadow-sm flex flex-col justify-between group">
              <div>
                <h4 className="text-lg font-bold text-gray-800 mb-2">School Profile</h4>
                <p className="text-xs text-gray-400 font-medium leading-relaxed">Customize the official institute appearance.</p>
              </div>
              <div className="mt-6 flex items-center gap-6">
                <div className="relative group/logo">
                  <img src={schoolLogo} className="w-20 h-20 rounded-[1.5rem] object-cover border-4 border-gray-50 shadow-sm" alt="Logo" />
                  <button onClick={() => fileInputRef.current?.click()} className="absolute -bottom-2 -right-2 w-8 h-8 bg-indigo-600 text-white rounded-xl flex items-center justify-center shadow-lg"><i className="fas fa-camera text-xs"></i></button>
                  <input ref={fileInputRef} type="file" accept="image/*" className="hidden" onChange={handleLogoChange} />
                </div>
                <div>
                  <p className="text-xs font-black text-gray-700 uppercase">School Badge</p>
                  <button onClick={() => fileInputRef.current?.click()} className="text-[10px] font-bold text-indigo-600 hover:text-indigo-800 uppercase tracking-widest mt-1">Replace Image</button>
                </div>
              </div>
            </div>
            <div className="bg-gradient-to-br from-indigo-600 to-indigo-800 p-8 rounded-[2rem] text-white shadow-xl">
              <h4 className="text-lg font-bold mb-2">School Motto</h4>
