import React, { useState, useMemo, useRef } from 'react';
import { Teacher, AttendanceStatus } from '../types';

interface TeacherListProps {
  teachers: Teacher[];
  onAttendanceChange: (teacherId: string, status: AttendanceStatus) => void;
  onBulkAttendanceChange: (teacherIds: string[], status: AttendanceStatus) => void;
  onAddTeacher: (teacher: Teacher) => void;
  onUpdateTeacher: (teacher: Teacher) => void;
  onDeleteTeacher: (id: string) => void;
  onBulkDeleteTeachers: (ids: string[]) => void;
}

const TeacherList: React.FC<TeacherListProps> = ({ 
  teachers, 
  onAttendanceChange, 
  onBulkAttendanceChange, 
  onAddTeacher, 
  onUpdateTeacher,
  onDeleteTeacher,
  onBulkDeleteTeachers
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingTeacherId, setEditingTeacherId] = useState<string | null>(null);

  const [formData, setFormData] = useState({
    name: '',
    id: '',
    department: 'Mathematics',
    email: '',
    avatar: ''
  });

  const fileInputRef = useRef<HTMLInputElement>(null);
  const today = new Date().toISOString().split('T')[0];

  const filteredTeachers = useMemo(() => {
    return teachers.filter(teacher => 
      teacher.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      teacher.department.toLowerCase().includes(searchTerm.toLowerCase()) ||
      teacher.email.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [teachers, searchTerm]);

  const toggleOne = (id: string) => {
    const next = new Set(selectedIds);
    if (next.has(id)) next.delete(id);
    else next.add(id);
    setSelectedIds(next);
  };

  const handleBulkUpdate = (status: AttendanceStatus) => {
    onBulkAttendanceChange(Array.from(selectedIds), status);
    setSelectedIds(new Set());
  };

  const handleBulkDelete = () => {
    onBulkDeleteTeachers(Array.from(selectedIds));
    setSelectedIds(new Set());
  };

  const openModal = (teacher?: Teacher) => {
    if (teacher) {
      setEditingTeacherId(teacher.id);
      setFormData({
        name: teacher.name,
        id: teacher.id,
        department: teacher.department,
        email: teacher.email,
        avatar: teacher.avatar
      });
    } else {
      setEditingTeacherId(null);
      setFormData({
        name: '',
        id: '',
        department: 'Mathematics',
        email: '',
        avatar: ''
      });
    }
    setIsModalOpen(true);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setFormData(prev => ({ ...prev, avatar: reader.result as string }));
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const payload = {
      ...formData,
      attendance: editingTeacherId ? (teachers.find(t => t.id === editingTeacherId)?.attendance || {}) : {},
      avatar: formData.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(formData.name)}&background=random`
    };

    if (editingTeacherId) {
      onUpdateTeacher(payload as Teacher);
    } else {
      onAddTeacher(payload as Teacher);
    }

    setIsModalOpen(false);
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-24">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Faculty Directory</h1>
          <p className="text-gray-500">Coordinate school staff and track professional check-ins.</p>
        </div>
        <button 
          onClick={() => openModal()}
          className="bg-purple-600 text-white px-6 py-3 rounded-2xl font-bold hover:bg-purple-700 transition-all shadow-lg shadow-purple-200 active:scale-95"
        >
          <i className="fas fa-user-plus mr-2"></i> Register New Faculty
        </button>
      </div>

      <div className="relative group max-w-md">
        <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 group-focus-within:text-purple-500 transition-colors"></i>
        <input 
          type="text" 
          placeholder="Search by name, department, or email..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-purple-500 outline-none transition-all text-black font-medium"
        />
      </div>

      <div className="glass-card rounded-3xl overflow-hidden shadow-sm">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 w-12">
                <input 
                  type="checkbox" 
                  className="w-5 h-5 rounded-md border-gray-300 text-purple-600 focus:ring-purple-500 cursor-pointer"
                  checked={selectedIds.size > 0 && selectedIds.size === filteredTeachers.length && filteredTeachers.length > 0}
                  onChange={() => setSelectedIds(selectedIds.size === filteredTeachers.length && filteredTeachers.length > 0 ? new Set() : new Set(filteredTeachers.map(t => t.id)))}
                />
              </th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Faculty Member</th>
