import React, { useState, useMemo, useEffect } from 'react';
import { Student, Grade } from '../types';
import { SUBJECTS } from '../constants';

interface GradingProps {
  students: Student[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
}

interface PerformanceAlert {
  id: string;
  studentName: string;
  score: number;
  subject: string;
}

const Grading: React.FC<GradingProps> = ({ students, onGradeUpdate }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0]);
  const [selectedTerm, setSelectedTerm] = useState(1);
  const [localScores, setLocalScores] = useState<Record<string, string>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);

  const THRESHOLD = 60;

  // Filter students who should have grades for the selected criteria
  const studentGrades = useMemo(() => {
    return students.map(student => {
      const grade = student.grades.find(g => g.subject === selectedSubject && g.term === selectedTerm);
      return {
        id: student.id,
        name: student.name,
        avatar: student.avatar,
        gradeLevel: student.gradeLevel,
        currentScore: grade?.score ?? 0,
        maxScore: grade?.maxScore ?? 100
      };
    });
  }, [students, selectedSubject, selectedTerm]);

  const handleScoreChange = (studentId: string, val: string) => {
    if (val === '' || (Number(val) >= 0 && Number(val) <= 100)) {
      setLocalScores(prev => ({ ...prev, [studentId]: val }));
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    const newAlerts: PerformanceAlert[] = [];

    // Scan for performance issues before committing
    Object.entries(localScores).forEach(([studentId, scoreStr]) => {
      const score = Number(scoreStr);
      if (scoreStr !== '' && score < THRESHOLD) {
        const student = students.find(s => s.id === studentId);
        if (student) {
          newAlerts.push({
            id: `${studentId}-${Date.now()}`,
            studentName: student.name,
            score: score,
            subject: selectedSubject
          });
        }
      }
    });

    if (newAlerts.length > 0) {
      setAlerts(prev => [...newAlerts, ...prev].slice(0, 5)); // Keep last 5 alerts
    }

    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 800));

    Object.entries(localScores).forEach(([studentId, score]) => {
      if (score !== '') {
        onGradeUpdate(studentId, selectedSubject, selectedTerm, Number(score));
      }
    });

    setLocalScores({});
    setIsSaving(false);
  };

  const dismissAlert = (alertId: string) => {
    setAlerts(prev => prev.filter(a => a.id !== alertId));
  };

  const getStatusColor = (score: number) => {
    if (score >= 85) return 'bg-emerald-500';
    if (score >= 70) return 'bg-indigo-500';
    if (score >= THRESHOLD) return 'bg-amber-500';
    return 'bg-rose-500';
  };

  const getStatusIcon = (score: number) => {
    if (score >= 85) return 'fa-star';
    if (score >= 70) return 'fa-medal';
    if (score >= THRESHOLD) return 'fa-award';
    return 'fa-exclamation-triangle';
  };

  const getStatusText = (score: number) => {
    if (score >= 85) return 'Excellent';
    if (score >= 70) return 'Good';
    if (score >= THRESHOLD) return 'Passing';
    return 'Academic Alert';
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-10">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Grading Management</h1>
          <p className="text-gray-500">Record and update student assessments for the current term.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
            {[1, 2, 3].map(term => (
              <button
                key={term}
                onClick={() => setSelectedTerm(term)}
                className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${
                  selectedTerm === term ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                Term {term}
              </button>
            ))}
          </div>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            className="bg-white border-none rounded-2xl px-4 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-medium text-black"
          >
            {SUBJECTS.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Performance Alerts Section */}
      {alerts.length > 0 && (
        <div className="space-y-3">
          <div className="flex items-center justify-between px-2">
            <h3 className="text-xs font-black text-rose-500 uppercase tracking-widest flex items-center gap-2">
              <i className="fas fa-bell animate-swing"></i>
              Low Performance Alerts
            </h3>
            <button onClick={() => setAlerts([])} className="text-[10px] font-bold text-gray-400 hover:text-gray-600 uppercase tracking-wider">Dismiss All</button>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
            {alerts.map((alert) => (
              <div key={alert.id} className="bg-rose-50 border border-rose-100 p-4 rounded-2xl flex items-center justify-between group animate-fadeIn">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 bg-rose-500 text-white rounded-full flex items-center justify-center text-xs">
                    <i className="fas fa-exclamation"></i>
                  </div>
                  <div>
                    <p className="text-xs font-bold text-gray-800">{alert.studentName}</p>
                    <p className="text-[10px] text-rose-600 font-bold uppercase">{alert.score}% in {alert.subject}</p>
                  </div>
                </div>
                <button 
                  onClick={() => dismissAlert(alert.id)}
                  className="text-gray-300 hover:text-rose-500 transition-colors"
                >
                  <i className="fas fa-times text-sm"></i>
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="glass-card rounded-3xl overflow-hidden shadow-sm">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase">Student</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center">Previous Score</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-center w-64">New Score & Progress</th>
              <th className="px-6 py-4 text-sm font-bold text-gray-600 uppercase text-right">Rating</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {studentGrades.map((item) => {
              const currentVal = localScores[item.id] !== undefined ? localScores[item.id] : '';
              const displayScore = currentVal !== '' ? Number(currentVal) : item.currentScore;
              const statusColor = getStatusColor(displayScore);
              const isFailing = displayScore < THRESHOLD;

              return (
                <tr key={item.id} className={`transition-colors group ${isFailing ? 'bg-rose-50/20' : 'hover:bg-gray-50/50'}`}>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <div className="relative">
                        <img src={item.avatar} className={`w-12 h-12 rounded-2xl object-cover border-2 transition-all ${isFailing ? 'border-rose-300 shadow-sm shadow-rose-100' : 'border-transparent group-hover:border-indigo-100'}`} alt="" />
                        {isFailing && (
                          <div className="absolute -top-1 -right-1 w-4 h-4 bg-rose-500 rounded-full flex items-center justify-center text-[8px] text-white border-2 border-white animate-pulse">
                            !
                          </div>
                        )}
                      </div>
                      <div>
                        <p className="font-bold text-gray-800">{item.name}</p>
                        <p className="text-xs text-gray-500 font-semibold uppercase">{item.gradeLevel}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-center">
                    <div className={`inline-flex items-center justify-center w-12 h-12 rounded-full font-bold text-sm ${item.currentScore < THRESHOLD ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-gray-500'}`}>
                      {item.currentScore}
                    </div>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex flex-col gap-2">
                      <div className="flex items-center gap-3">
                        <input
                          type="number"
                          min="0"
                          max="100"
                          placeholder={item.currentScore.toString()}
                          value={currentVal}
                          onChange={(e) => handleScoreChange(item.id, e.target.value)}
                          className={`w-20 text-center py-2 bg-gray-50 border-2 rounded-xl outline-none font-bold transition-all text-black ${
                            currentVal !== '' && Number(currentVal) < THRESHOLD 
                              ? 'border-rose-400 text-rose-600 focus:bg-rose-50' 
                              : 'border-transparent focus:border-indigo-500 focus:bg-white text-black'
                          }`}
                        />
                        <span className="text-sm font-bold text-gray-400">/ 100</span>
                      </div>
                      <div className="w-full h-2 bg-gray-100 rounded-full overflow-hidden relative">
                        <div 
                          className={`absolute inset-y-0 left-0 transition-all duration-500 ease-out ${statusColor} ${isFailing ? 'animate-pulse' : ''}`}
                          style={{ width: `${displayScore}%` }}
                        ></div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex flex-col items-end">
                      <span className={`inline-flex items-center gap-2 text-sm font-black px-3 py-1 rounded-full ${statusColor.replace('bg-', 'text-').replace('500', '600')} ${statusColor.replace('bg-', 'bg-')}/10`}>
                        <i className={`fas ${getStatusIcon(displayScore)} text-[10px]`}></i>
                        {displayScore}%
                      </span>
                      <span className={`text-[10px] font-bold uppercase tracking-widest mt-1 ${isFailing ? 'text-rose-500' : 'text-gray-400'}`}>
                        {getStatusText(displayScore)}
                      </span>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>

        <div className="p-6 bg-gray-50/50 border-t border-gray-100 flex justify-end">
          <button
            onClick={handleSave}
            disabled={isSaving || Object.keys(localScores).length === 0}
            className="bg-indigo-600 text-white px-8 py-4 rounded-2xl font-bold hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-lg shadow-indigo-200 flex items-center gap-3 active:scale-95"
          >
            {isSaving ? (
              <>
                <i className="fas fa-circle-notch fa-spin"></i>
                Analyzing Performance...
              </>
            ) : (
              <>
                <i className="fas fa-check-double"></i>
                Finalize Term {selectedTerm} Grades
              </>
            )}
          </button>
        </div>
      </div>

      <style>{`
        @keyframes swing {
          0%, 100% { transform: rotate(0deg); }
          20% { transform: rotate(15deg); }
          40% { transform: rotate(-10deg); }
          60% { transform: rotate(5deg); }
          80% { transform: rotate(-5deg); }
        }
        .animate-swing {
          animation: swing 2s infinite ease-in-out;
          transform-origin: top center;
        }
      `}</style>
    </div>
  );
};

export default Grading;

import React, { useState, useMemo, useRef } from 'react';
import { Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateReportComment } from '../services/geminiService';

interface ReportSettings {
  headOfSchool: string;
  signature: string;
  authPrefix: string;
}

interface ReportCardProps {
  students: Student[];
  schoolLogo: string;
  reportSettings: ReportSettings;
  onUpdateReportSettings: (settings: ReportSettings) => void;
}

const ReportCard: React.FC<ReportCardProps> = ({ students, schoolLogo, reportSettings, onUpdateReportSettings }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [selectedTerm, setSelectedTerm] = useState<number>(1);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>(SUBJECTS);
  const [aiComment, setAiComment] = useState<string>('');
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const sigInputRef = useRef<HTMLInputElement>(null);
  const student = students.find(s => s.id === selectedStudentId);

  const filteredGrades = useMemo(() => {
    if (!student) return [];
    return student.grades.filter(g => 
      g.term === selectedTerm && 
      selectedSubjects.includes(g.subject)
    );
  }, [student, selectedTerm, selectedSubjects]);

  const attendanceStats = useMemo(() => {
    if (!student) return { present: 0, absent: 0, rate: 0 };
    const attendanceValues = Object.values(student.attendance);
    const present = attendanceValues.filter(v => v === AttendanceStatus.PRESENT).length;
    const total = attendanceValues.length || 1;
    const absent = attendanceValues.filter(v => v === AttendanceStatus.ABSENT).length;
    return { present, absent, rate: Math.round((present / total) * 100) };
  }, [student]);

  const handleGenerateComment = async () => {
    if (!student) return;
    setIsGeneratingAI(true);
    const comment = await generateReportComment({ ...student, grades: filteredGrades });
    setAiComment(comment);
    setIsGeneratingAI(false);
  };

  const handleSigChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        onUpdateReportSettings({ ...reportSettings, signature: reader.result as string });
      };
      reader.readAsDataURL(file);
    }
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportPDF = () => {
    setIsExporting(true);
    setExportProgress(0);
    const interval = setInterval(() => {
      setExportProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(() => {
            setIsExporting(false);
            alert(`Report for ${student?.name} (Term ${selectedTerm}) has been generated and downloaded.`);
          }, 500);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  if (!student) return <div className="p-8 text-center text-gray-500">No student selected.</div>;

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6 no-print">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Performance Reporting</h1>
          <p className="text-gray-500 mt-1">Configure and generate official terminal academic transcripts.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <button 
            onClick={() => setIsSettingsOpen(true)}
            className="bg-white border-2 border-orange-200 text-orange-600 px-6 py-3 rounded-2xl font-bold hover:bg-orange-50 transition-all flex items-center gap-2"
          >
            <i className="fas fa-signature"></i>
            Verification Settings
          </button>
          <select 
            value={selectedStudentId}
            onChange={(e) => { setSelectedStudentId(e.target.value); setAiComment(''); }}
            className="bg-white border-2 border-transparent hover:border-indigo-100 rounded-2xl px-5 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black transition-all"
          >
            {students.map(s => <option key={s.id} value={s.id}>{s.name} ({s.id})</option>)}
          </select>
          <div className="flex gap-2">
            <button 
