              </div>
              <div>
                <label className="block text-xs font-black text-gray-700 uppercase tracking-tighter mb-4 flex items-center gap-2"><i className="fas fa-book-open text-orange-500"></i>Included Subjects</label>
                <div className="flex flex-wrap gap-2 max-h-56 overflow-y-auto pr-2 scrollbar-hide bg-gray-50/50 p-3 rounded-2xl border border-gray-50">
                  {SUBJECTS.map(s => (
                    <button key={s} onClick={() => setSelectedSubjects(prev => prev.includes(s) ? prev.filter(x => x !== s) : [...prev, s])} className={`px-3 py-2 rounded-xl text-[10px] font-black uppercase transition-all border-2 ${selectedSubjects.includes(s) ? 'bg-white border-indigo-200 text-black shadow-sm' : 'bg-transparent border-transparent text-gray-400'}`}>{s}</button>
