                </div>
              </div>
              <div className="text-center md:text-right">
                <p className="text-[10px] font-black text-gray-300 uppercase tracking-tighter mb-1">Authenticity Code</p>
                <p className="text-xs font-mono text-black tracking-widest">{reportSettings.authPrefix}-{student.id}-{selectedTerm}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {isSettingsOpen && (
        <div className="fixed inset-0 bg-indigo-900/60 backdrop-blur-sm z-[200] flex items-center justify-center p-6 animate-fadeIn no-print">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-xl overflow-hidden">
