            </div>
            <div className="p-10 space-y-6">
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Head of School Name</label>
                <input value={reportSettings.headOfSchool} onChange={e => onUpdateReportSettings({...reportSettings, headOfSchool: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-bold text-black" />
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Authenticity Code Prefix</label>
                <input value={reportSettings.authPrefix} onChange={e => onUpdateReportSettings({...reportSettings, authPrefix: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-mono text-black" />
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Principal Signature / School Stamp</label>
                <div onClick={() => sigInputRef.current?.click()} className="w-full h-32 bg-gray-50 border-2 border-dashed border-gray-200 rounded-2xl flex items-center justify-center cursor-pointer hover:border-orange-400 transition-all overflow-hidden group">
                  {reportSettings.signature ? <img src={reportSettings.signature} className="max-h-full max-w-full object-contain" /> : <div className="text-center group-hover:scale-105 transition-transform"><i className="fas fa-pen-nib text-gray-300 text-2xl"></i><p className="text-[8px] font-black text-gray-400 uppercase mt-2">Upload Image</p></div>}
                </div>
                <input ref={sigInputRef} type="file" accept="image/*" className="hidden" onChange={handleSigChange} />
              </div>
              <button onClick={() => setIsSettingsOpen(false)} className="w-full bg-orange-600 text-white py-4 rounded-[1.5rem] font-black uppercase tracking-widest shadow-xl hover:bg-orange-700 active:scale-95 transition-all">Save Verification Settings</button>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @media print {
          body * {
            visibility: hidden;
          }
          .printable-report, .printable-report * {
            visibility: visible;
          }
          .printable-report {
            position: absolute;
            left: 0;
            top: 0;
            width: 100% !important;
            margin: 0 !important;
            padding: 0 !important;
            border: none !important;
            box-shadow: none !important;
            background: white !important;
          }
          .no-print {
            display: none !important;
          }
          @page {
            size: auto;
            margin: 10mm;
          }
          .printable-report .p-10 {
            padding: 5mm !important;
          }
          .printable-report .bg-gray-50 {
            background-color: #f9fafb !important;
            -webkit-print-color-adjust: exact;
          }
          .printable-report .bg-indigo-600 {
            background-color: #4f46e5 !important;
            -webkit-print-color-adjust: exact;
          }
          .printable-report .bg-emerald-50 {
            background-color: #ecfdf5 !important;
            -webkit-print-color-adjust: exact;
          }
        }
      `}</style>
    </div>
  );
};

