import CommunicationHub from './components/CommunicationHub';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  const [notificationLogs, setNotificationLogs] = useState<NotificationLog[]>(() => {
    const saved = localStorage.getItem('cc_notif_logs');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem('cc_students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem('cc_events', JSON.stringify(events));
  }, [events]);

  useEffect(() => {
    localStorage.setItem('cc_notif_logs', JSON.stringify(notificationLogs));
  }, [notificationLogs]);

  useEffect(() => {
    localStorage.setItem('cc_school_logo', schoolLogo);
  }, [schoolLogo]);

  useEffect(() => {
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
  }, [reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students? This action cannot be undone.`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Are you sure you want to delete this event? This will remove it from the school calendar.')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const handleLogNotification = (log: NotificationLog) => {
    setNotificationLogs(prev => [log, ...prev]);
  };

  const renderView = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <Dashboard 
            students={students} 
            teachers={teachers} 
            onGradeUpdate={handleGradeUpdate} 
            schoolLogo={schoolLogo}
            onUpdateLogo={setSchoolLogo}
            events={events}
            onAddEvent={handleAddEvent}
            onUpdateEvent={handleUpdateEvent}
            onDeleteEvent={handleDeleteEvent}
          />
        );
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
            onAddStudent={handleAddStudent}
            onDeleteStudent={handleDeleteStudent}
            onBulkDeleteStudents={handleBulkDeleteStudents}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
            onAddTeacher={handleAddTeacher}
            onUpdateTeacher={handleUpdateTeacher}
            onDeleteTeacher={handleDeleteTeacher}
            onBulkDeleteTeachers={handleBulkDeleteTeachers}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return (
          <ReportCard 
            students={students} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={setReportSettings}
            onLogNotification={handleLogNotification}
          />
        );
      case 'notifications':
        return (
          <CommunicationHub 
            students={students} 
            notificationLogs={notificationLogs}
            onSendMessage={handleLogNotification}
          />
        );
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="relative w-96 hidden md:block">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search directory..." 
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow text-black font-medium"
            />
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-emerald-100 shadow-sm">
              <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
              <span className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">System Online</span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://ui-avatars.com/api/?name=Admin&background=6366f1&color=fff" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Super Admin</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">CCIS Portal</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useMemo, useRef } from 'react';
import { Student, AttendanceStatus } from '../types';

interface StudentListProps {
  students: Student[];
  onAttendanceChange: (studentId: string, status: AttendanceStatus) => void;
  onBulkAttendanceChange: (studentIds: string[], status: AttendanceStatus) => void;
  onAddStudent: (student: Student) => void;
  onDeleteStudent: (id: string) => void;
  onBulkDeleteStudents: (ids: string[]) => void;
}

const StudentList: React.FC<StudentListProps> = ({ 
  students, 
  onAttendanceChange, 
  onBulkAttendanceChange, 
  onAddStudent, 
  onDeleteStudent,
  onBulkDeleteStudents 
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [newStudent, setNewStudent] = useState({
    name: '',
    id: '',
    gradeLevel: 'Grade 10',
    section: 'A',
    avatar: '',
    guardianName: '',
    guardianEmail: '',
    guardianPhone: ''
  });

  const fileInputRef = useRef<HTMLInputElement>(null);
  const today = new Date().toISOString().split('T')[0];

  const filteredStudents = useMemo(() => {
    return students.filter(student => 
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.id.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [students, searchTerm]);

  const toggleAll = () => {
    if (selectedIds.size === filteredStudents.length && filteredStudents.length > 0) {
      setSelectedIds(new Set());
    } else {
      setSelectedIds(new Set(filteredStudents.map(s => s.id)));
    }
  };

  const toggleOne = (id: string) => {
    const next = new Set(selectedIds);
    if (next.has(id)) next.delete(id);
    else next.add(id);
    setSelectedIds(next);
  };

  const handleBulkUpdate = (status: AttendanceStatus) => {
    onBulkAttendanceChange(Array.from(selectedIds), status);
    setSelectedIds(new Set());
  };

  const handleBulkDelete = () => {
    onBulkDeleteStudents(Array.from(selectedIds));
    setSelectedIds(new Set());
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setNewStudent(prev => ({ ...prev, avatar: reader.result as string }));
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onAddStudent({
      ...newStudent,
      grades: [],
      attendance: {},
      avatar: newStudent.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(newStudent.name)}&background=random`
    });
    setIsAddModalOpen(false);
    setNewStudent({ name: '', id: '', gradeLevel: 'Grade 10', section: 'A', avatar: '', guardianName: '', guardianEmail: '', guardianPhone: '' });
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-24">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Student Directory</h1>
          <p className="text-gray-500">Manage enrollment and access guardian contact profiles.</p>
        </div>
        <button 
          onClick={() => setIsAddModalOpen(true)}
          className="bg-indigo-600 text-white px-6 py-3 rounded-2xl font-bold hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-200 active:scale-95"
        >
          <i className="fas fa-plus mr-2"></i> Register New Student
        </button>
      </div>

      <div className="relative group max-w-md">
        <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 group-focus-within:text-indigo-500 transition-colors"></i>
        <input 
          type="text" 
          placeholder="Search by name or ID..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-all text-black font-medium"
        />
      </div>

      <div className="glass-card rounded-[2rem] overflow-hidden shadow-sm relative">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 w-12">
                <input 
                  type="checkbox" 
                  className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer"
                  checked={selectedIds.size > 0 && selectedIds.size === filteredStudents.length && filteredStudents.length > 0}
                  onChange={toggleAll}
                />
              </th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Student</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Grade & Section</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Daily Status</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {filteredStudents.length === 0 ? (
              <tr>
                <td colSpan={5} className="px-6 py-10 text-center text-gray-400 font-medium italic">No students matching your search.</td>
              </tr>
            ) : filteredStudents.map((student) => {
              const currentStatus = student.attendance[today] || null;
              const isSelected = selectedIds.has(student.id);
              return (
                <tr key={student.id} className={`transition-colors group ${isSelected ? 'bg-indigo-50/50' : 'hover:bg-gray-50/50'}`}>
                  <td className="px-6 py-5">
                    <input type="checkbox" className="w-5 h-5 rounded-md border-gray-300 text-indigo-600 focus:ring-indigo-500 cursor-pointer" checked={isSelected} onChange={() => toggleOne(student.id)} />
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={student.avatar} className="w-12 h-12 rounded-2xl object-cover shadow-sm" alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{student.name}</p>
                        <p className="text-[10px] text-gray-400 font-mono">{student.id}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5">
                    <span className="px-3 py-1 bg-indigo-50 text-indigo-700 rounded-full text-[9px] font-black uppercase tracking-widest">{student.gradeLevel} - {student.section}</span>
                  </td>
                  <td className="px-6 py-5 text-center">
                    <div className="flex justify-center gap-2">
                      <button onClick={() => onAttendanceChange(student.id, AttendanceStatus.PRESENT)} className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${currentStatus === AttendanceStatus.PRESENT ? 'bg-emerald-500 text-white' : 'bg-gray-100 text-gray-400 hover:bg-emerald-50'}`}><i className="fas fa-check"></i></button>
                      <button onClick={() => onAttendanceChange(student.id, AttendanceStatus.ABSENT)} className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${currentStatus === AttendanceStatus.ABSENT ? 'bg-rose-500 text-white' : 'bg-gray-100 text-gray-400 hover:bg-rose-50'}`}><i className="fas fa-times"></i></button>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex items-center justify-end gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                      <button onClick={() => onDeleteStudent(student.id)} className="w-8 h-8 rounded-full bg-rose-50 text-rose-500 hover:bg-rose-100 transition-colors flex items-center justify-center"><i className="fas fa-trash-alt text-xs"></i></button>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {isAddModalOpen && (
        <div className="fixed inset-0 bg-indigo-900/60 backdrop-blur-sm z-[100] flex items-center justify-center p-6 animate-fadeIn">
          <div className="bg-white rounded-[2.5rem] shadow-2xl w-full max-w-2xl overflow-hidden animate-slideUp">
            <div className="bg-indigo-600 p-8 text-white flex justify-between items-center">
              <div>
                <h2 className="text-2xl font-black tracking-tight">Register Student</h2>
                <p className="text-indigo-100 text-xs font-bold uppercase tracking-widest mt-1">Full Enrollment Profile</p>
              </div>
              <button onClick={() => setIsAddModalOpen(false)} className="w-10 h-10 bg-white/10 rounded-full hover:bg-white/20 transition-colors"><i className="fas fa-times"></i></button>
            </div>
            <form onSubmit={handleSubmit} className="p-10 space-y-6">
              <div className="flex flex-col items-center gap-4 mb-4">
                <div onClick={() => fileInputRef.current?.click()} className="w-24 h-24 rounded-[2rem] bg-gray-50 border-2 border-dashed border-gray-200 flex items-center justify-center cursor-pointer hover:border-indigo-400 hover:bg-indigo-50 transition-all overflow-hidden group">
                  {newStudent.avatar ? <img src={newStudent.avatar} className="w-full h-full object-cover" alt="Preview" /> : <div className="text-center group-hover:scale-110 transition-transform"><i className="fas fa-camera text-gray-300 text-xl"></i><p className="text-[8px] font-black text-gray-400 uppercase mt-1">Photo</p></div>}
                </div>
                <input ref={fileInputRef} type="file" accept="image/*" onChange={handleFileChange} className="hidden" />
              </div>

              <div className="grid grid-cols-2 gap-x-8 gap-y-6">
                <div className="col-span-2 border-b border-gray-100 pb-2">
                  <h3 className="text-xs font-black text-indigo-500 uppercase tracking-[0.2em]">Student Details</h3>
                </div>
                <div className="col-span-2 md:col-span-1">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Full Name</label>
                  <input required value={newStudent.name} onChange={e => setNewStudent({...newStudent, name: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black" placeholder="Student Name" />
                </div>
                <div className="col-span-2 md:col-span-1">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Student ID</label>
                  <input required value={newStudent.id} onChange={e => setNewStudent({...newStudent, id: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-mono text-sm text-black" placeholder="S2024-001" />
                </div>

                <div className="col-span-2 border-b border-gray-100 pb-2 mt-4">
                  <h3 className="text-xs font-black text-orange-500 uppercase tracking-[0.2em]">Guardian Contact</h3>
                </div>
                <div className="col-span-2 md:col-span-1">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Guardian Name</label>
                  <input required value={newStudent.guardianName} onChange={e => setNewStudent({...newStudent, guardianName: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-bold text-black" placeholder="Name" />
                </div>
                <div className="col-span-2 md:col-span-1">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Guardian Phone</label>
                  <input required value={newStudent.guardianPhone} onChange={e => setNewStudent({...newStudent, guardianPhone: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-medium text-black" placeholder="+1-xxx-xxx-xxxx" />
                </div>
                <div className="col-span-2">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Guardian Email</label>
                  <input required type="email" value={newStudent.guardianEmail} onChange={e => setNewStudent({...newStudent, guardianEmail: e.target.value})} className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-orange-500 outline-none font-medium text-black" placeholder="guardian@email.com" />
                </div>
              </div>

              <button type="submit" className="w-full bg-indigo-600 text-white py-4 rounded-[1.5rem] font-black uppercase tracking-[0.2em] shadow-xl shadow-indigo-100 hover:bg-indigo-700 active:scale-95 transition-all mt-4">Confirm Registration</button>
            </form>
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideUp { from { transform: translateY(50px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
        .animate-slideUp { animation: slideUp 0.4s cubic-bezier(0.16, 1, 0.3, 1) forwards; }
      `}</style>
    </div>
  );
};

export default StudentList;

import React, { useState, useMemo, useRef } from 'react';
import { Student, AttendanceStatus, NotificationLog } from '../types';
import { SUBJECTS } from '../constants';
import { generateReportComment } from '../services/geminiService';

interface ReportSettings {
  headOfSchool: string;
  signature: string;
  authPrefix: string;
}

interface ReportCardProps {
  students: Student[];
  schoolLogo: string;
  reportSettings: ReportSettings;
  onUpdateReportSettings: (settings: ReportSettings) => void;
  onLogNotification: (log: NotificationLog) => void;
}

const ReportCard: React.FC<ReportCardProps> = ({ students, schoolLogo, reportSettings, onUpdateReportSettings, onLogNotification }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [selectedTerm, setSelectedTerm] = useState<number>(1);
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>(SUBJECTS);
  const [aiComment, setAiComment] = useState<string>('');
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [isEmailing, setIsEmailing] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const sigInputRef = useRef<HTMLInputElement>(null);
  const student = students.find(s => s.id === selectedStudentId);

  const filteredGrades = useMemo(() => {
    if (!student) return [];
    return student.grades.filter(g => 
      g.term === selectedTerm && 
      selectedSubjects.includes(g.subject)
    );
  }, [student, selectedTerm, selectedSubjects]);

  const attendanceStats = useMemo(() => {
    if (!student) return { present: 0, absent: 0, rate: 0 };
    const attendanceValues = Object.values(student.attendance);
    const present = attendanceValues.filter(v => v === AttendanceStatus.PRESENT).length;
    const total = attendanceValues.length || 1;
    const absent = attendanceValues.filter(v => v === AttendanceStatus.ABSENT).length;
    return { present, absent, rate: Math.round((present / total) * 100) };
  }, [student]);

  const handleGenerateComment = async () => {
    if (!student) return;
    setIsGeneratingAI(true);
    const comment = await generateReportComment({ ...student, grades: filteredGrades });
    setAiComment(comment);
    setIsGeneratingAI(false);
  };

  const handleEmailGuardian = () => {
    if (!student) return;
    setIsEmailing(true);
    // Simulate API delay
    setTimeout(() => {
      setIsEmailing(false);
      onLogNotification({
        id: Date.now().toString(),
        timestamp: new Date().toISOString(),
        recipientEmail: student.guardianEmail,
        studentName: student.name,
        subject: `Term ${selectedTerm} Report: ${student.name}`,
        type: 'ACADEMIC',
        status: 'SENT'
      });
      alert(`The terminal report for ${student.name} has been securely emailed to ${student.guardianEmail}.`);
    }, 1200);
  };

  if (!student) return <div className="p-8 text-center text-gray-500">No student selected.</div>;

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6 no-print">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Academic Transcripts</h1>
          <p className="text-gray-500 mt-1 font-medium">Generate official results and notify guardians automatically.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <button 
            onClick={() => setIsSettingsOpen(true)}
            className="bg-white border-2 border-orange-200 text-orange-600 px-6 py-3 rounded-2xl font-bold hover:bg-orange-50 transition-all flex items-center gap-2 active:scale-95"
          >
            <i className="fas fa-cog"></i> Setup
          </button>
          <select 
            value={selectedStudentId}
            onChange={(e) => { setSelectedStudentId(e.target.value); setAiComment(''); }}
            className="bg-white border-2 border-transparent hover:border-indigo-100 rounded-2xl px-5 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black transition-all"
          >
            {students.map(s => <option key={s.id} value={s.id}>{s.name} ({s.id})</option>)}
          </select>
          <div className="flex gap-2">
            <button onClick={handleEmailGuardian} disabled={isEmailing} className="bg-emerald-600 text-white px-6 py-3 rounded-2xl font-bold hover:bg-emerald-700 transition-all shadow-lg shadow-emerald-200 flex items-center gap-2 active:scale-95 disabled:opacity-50">
              {isEmailing ? <i className="fas fa-spinner fa-spin"></i> : <i className="fas fa-paper-plane"></i>}
              Send to Guardian
            </button>
            <button onClick={() => window.print()} className="bg-gray-800 text-white px-6 py-3 rounded-2xl font-bold hover:bg-black transition-all shadow-lg flex items-center gap-2 active:scale-95">
              <i className="fas fa-print"></i> Print
            </button>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-4 gap-8 items-start">
        <div className="xl:col-span-1 space-y-6 no-print">
          <div className="glass-card p-8 rounded-[2rem] shadow-sm border border-white">
            <h3 className="text-[10px] font-black text-gray-400 uppercase tracking-[0.2em] mb-6">Assessment Controls</h3>
            <div className="space-y-8">
              <div>
                <label className="text-xs font-black text-gray-700 uppercase tracking-tighter mb-3 flex items-center gap-2"><i className="fas fa-calendar-alt text-indigo-500"></i>Period</label>
                <div className="grid grid-cols-3 gap-2 bg-gray-50 p-1.5 rounded-2xl border border-gray-100">
                  {[1, 2, 3].map(t => (
                    <button key={t} onClick={() => setSelectedTerm(t)} className={`py-2.5 rounded-xl text-xs font-black transition-all duration-300 ${selectedTerm === t ? 'bg-white text-indigo-600 shadow-md' : 'text-gray-400 hover:text-gray-600'}`}>T{t}</button>
                  ))}
                </div>
              </div>
              <div>
                <label className="block text-xs font-black text-gray-700 uppercase tracking-tighter mb-4 flex items-center gap-2"><i className="fas fa-brain text-purple-500"></i>AI Insights</label>
                <button 
                  onClick={handleGenerateComment}
                  disabled={isGeneratingAI}
                  className="w-full bg-purple-600 text-white py-3 rounded-2xl text-[10px] font-black uppercase tracking-widest shadow-lg shadow-purple-100 hover:bg-purple-700 transition-all flex items-center justify-center gap-2 active:scale-95 disabled:opacity-50"
                >
                  {isGeneratingAI ? <i className="fas fa-circle-notch fa-spin"></i> : <i className="fas fa-wand-sparkles"></i>}
                  Generate Analysis
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="xl:col-span-3 space-y-6">
          <div className="bg-white rounded-[2.5rem] shadow-2xl overflow-hidden border border-gray-100 max-w-5xl mx-auto relative group printable-report">
            <div className="bg-gray-50/50 p-10 border-b border-gray-100 relative">
              <div className="flex flex-col md:flex-row justify-between items-start gap-8 relative z-10">
                <div className="flex items-center gap-8">
                  <div className="relative">
                    <img src={student.avatar} className="w-28 h-28 rounded-3xl object-cover shadow-xl border-4 border-white" alt="" />
                    <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-emerald-500 rounded-lg flex items-center justify-center text-white border-2 border-white shadow-lg no-print"><i className="fas fa-check text-xs"></i></div>
                  </div>
                  <div>
                    <h2 className="text-3xl font-black text-gray-800 tracking-tight">{student.name}</h2>
                    <div className="flex gap-2 mt-2">
                      <span className="px-3 py-1 bg-indigo-600 text-white text-[10px] font-black uppercase rounded-full tracking-widest">{student.gradeLevel}</span>
                    </div>
                    <p className="text-gray-400 text-[10px] mt-4 font-black uppercase tracking-widest">Guardian: <span className="text-gray-800">{student.guardianName}</span></p>
                  </div>
                </div>
                <div className="md:text-right flex flex-col items-center md:items-end">
                  <div className="flex items-center gap-4 mb-3">
                    <div className="w-16 h-16 bg-white rounded-full p-1 border-2 border-orange-500 shadow-md">
                      <img src={schoolLogo} alt="Logo" className="w-full h-full object-cover rounded-full" />
                    </div>
                    <div className="text-left md:text-right">
                      <h3 className="text-xl font-black text-indigo-900 tracking-tighter uppercase leading-tight">CEDAR CREST</h3>
                      <p className="text-[10px] font-black text-orange-600 uppercase tracking-[0.2em]">International School</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="p-10">
              <div className="grid grid-cols-1 lg:grid-cols-5 gap-12">
                <div className="lg:col-span-3 space-y-6">
                  <h4 className="text-xs font-black text-gray-400 uppercase tracking-widest">Subject Assessments</h4>
                  {filteredGrades.length > 0 ? (
                    <div className="overflow-hidden rounded-3xl border border-gray-100">
                      <table className="w-full text-left">
                        <thead className="bg-gray-50 border-b border-gray-100">
                          <tr><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Subject</th><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Score</th><th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Grade</th></tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                          {filteredGrades.map((grade, idx) => {
                            const percent = (grade.score / grade.maxScore) * 100;
                            let gradeLetter = percent >= 90 ? 'A+' : percent >= 80 ? 'A' : percent >= 70 ? 'B' : percent >= 60 ? 'C' : 'F';
                            return (
                              <tr key={idx} className="hover:bg-indigo-50/30 transition-colors">
                                <td className="px-6 py-4"><p className="font-bold text-black">{grade.subject}</p></td>
                                <td className="px-6 py-4 text-center"><span className="font-black text-black">{grade.score}</span>/100</td>
                                <td className="px-6 py-4 text-right"><span className="text-lg font-black text-indigo-600">{gradeLetter}</span></td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  ) : <div className="p-10 text-center bg-gray-50 rounded-3xl border-2 border-dashed border-gray-200"><p className="text-gray-500 font-bold text-sm">No records found for Term {selectedTerm}.</p></div>}
                </div>
                <div className="lg:col-span-2 space-y-8">
                  <div className="bg-emerald-50 rounded-3xl p-6 border border-emerald-100">
                    <p className="text-4xl font-black text-emerald-700">{attendanceStats.rate}%</p>
                    <p className="text-[10px] font-black text-emerald-600 uppercase mt-1 tracking-widest">Attendance Rate</p>
                  </div>
                  <div className="bg-purple-50 rounded-3xl p-6 border border-purple-100 min-h-[140px]">
                    <h4 className="text-[9px] font-black text-purple-600 uppercase tracking-widest mb-3">AI Commentary</h4>
                    <p className="text-sm text-purple-900 leading-relaxed font-medium italic">"{aiComment || "Analysis pending generation..."}"</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-gray-50 p-10 border-t border-gray-100 text-center">
              <p className="text-[9px] font-black text-gray-400 uppercase tracking-widest">Report Authenticity ID</p>
              <p className="text-xs font-mono text-gray-600 mt-1">{reportSettings.authPrefix}-{student.id}-{selectedTerm}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReportCard;

export enum AttendanceStatus {
  PRESENT = 'PRESENT',
  ABSENT = 'ABSENT',
  LATE = 'LATE',
}

export interface Grade {
  subject: string;
  score: number;
  maxScore: number;
  term: number;
}

export interface Student {
  id: string;
  name: string;
  gradeLevel: string;
  section: string;
  avatar: string;
  guardianName: string;
  guardianEmail: string;
  guardianPhone: string;
  grades: Grade[];
  attendance: Record<string, AttendanceStatus>;
}

export interface Teacher {
  id: string;
  name: string;
  department: string;
  email: string;
  avatar: string;
  attendance: Record<string, AttendanceStatus>;
}

export interface SchoolEvent {
  id: string;
  title: string;
  date: string;
  time: string;
  location: string;
  description: string;
  color: string;
  bg: string;
}

export interface NotificationLog {
  id: string;
  timestamp: string;
  recipientEmail: string;
  studentName: string;
  subject: string;
  type: 'ACADEMIC' | 'EVENT' | 'EMERGENCY' | 'FEE' | 'GENERAL';
  status: 'SENT' | 'FAILED' | 'QUEUED';
}

export interface CommunicationTemplate {
  id: string;
  name: string;
  subject: string;
  content: string;
  category: 'ACADEMIC' | 'EVENT' | 'EMERGENCY' | 'FEE' | 'GENERAL';
}

export type View = 'dashboard' | 'students' | 'teachers' | 'grading' | 'reports' | 'notifications' | 'guardian_portal';

import React, { useState, useEffect } from 'react';
import { Student, NotificationLog, CommunicationTemplate } from '../types';
import { DEFAULT_TEMPLATES } from '../constants';

interface CommunicationHubProps {
  students: Student[];
  notificationLogs: NotificationLog[];
  onSendMessage: (log: NotificationLog) => void;
  templates: CommunicationTemplate[];
  onUpdateTemplates: (templates: CommunicationTemplate[]) => void;
}

const CommunicationHub: React.FC<CommunicationHubProps> = ({ 
  students, 
  notificationLogs, 
  onSendMessage,
  templates,
  onUpdateTemplates
}) => {
  const [activeTab, setActiveTab] = useState<'history' | 'templates' | 'compose'>('history');
  const [searchLog, setSearchLog] = useState('');
  const [isSending, setIsSending] = useState(false);

  // Compose State
  const [composeData, setComposeData] = useState({
    recipientType: 'all',
    individualId: '',
    subject: '',
    content: '',
    type: 'GENERAL' as NotificationLog['type']
  });

  const filteredLogs = notificationLogs.filter(log => 
    log.studentName.toLowerCase().includes(searchLog.toLowerCase()) ||
    log.recipientEmail.toLowerCase().includes(searchLog.toLowerCase()) ||
    log.subject.toLowerCase().includes(searchLog.toLowerCase())
  );

  const handleUseTemplate = (template: CommunicationTemplate) => {
    setComposeData({
      ...composeData,
      subject: template.subject,
      content: template.content,
      type: template.category
    });
    setActiveTab('compose');
  };

  const handleSend = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSending(true);

    // Simulated "Secure API" delay
    await new Promise(resolve => setTimeout(resolve, 1500));

    const recipients: Student[] = [];
    if (composeData.recipientType === 'all') {
      recipients.push(...students);
    } else if (composeData.individualId) {
      const s = students.find(std => std.id === composeData.individualId);
      if (s) recipients.push(s);
    }

    recipients.forEach(std => {
      onSendMessage({
        id: Math.random().toString(36).substr(2, 9),
        timestamp: new Date().toISOString(),
        recipientEmail: std.guardianEmail,
        studentName: std.name,
        subject: composeData.subject.replace('[StudentName]', std.name).replace('[GuardianName]', std.guardianName),
        type: composeData.type,
        status: 'SENT'
      });
    });

    setIsSending(false);
    alert(`Successfully dispatched ${recipients.length} secure notification(s).`);
    setActiveTab('history');
    setComposeData({ recipientType: 'all', individualId: '', subject: '', content: '', type: 'GENERAL' });
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-20">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Communication Hub</h1>
          <p className="text-gray-500 font-medium">Manage secure guardian outreach and audit logs.</p>
        </div>
        <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
          <button onClick={() => setActiveTab('history')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'history' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>History</button>
          <button onClick={() => setActiveTab('templates')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'templates' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>Templates</button>
          <button onClick={() => setActiveTab('compose')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'compose' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>Compose</button>
        </div>
      </div>

      {activeTab === 'history' && (
        <div className="space-y-4">
          <div className="relative max-w-md">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Search audit logs..." 
              value={searchLog}
              onChange={(e) => setSearchLog(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none text-black font-medium"
            />
          </div>

          <div className="glass-card rounded-[2rem] overflow-hidden shadow-sm border border-gray-100">
            <table className="w-full text-left">
              <thead className="bg-gray-50 border-b border-gray-100">
                <tr>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Time (UTC)</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Guardian/Ward</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Type</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Subject Reference</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest text-right">Delivery Status</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-50">
                {filteredLogs.length === 0 ? (
                  <tr><td colSpan={5} className="px-6 py-10 text-center text-gray-400 italic font-medium">No secure transmission logs found.</td></tr>
                ) : filteredLogs.map(log => (
                  <tr key={log.id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-6 py-4 text-[10px] text-gray-400 font-mono">{log.timestamp}</td>
                    <td className="px-6 py-4">
                      <p className="text-sm font-bold text-gray-800">{log.studentName}</p>
                      <p className="text-[10px] text-indigo-600 font-medium">{log.recipientEmail}</p>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-3 py-1 rounded-full text-[9px] font-black uppercase tracking-wider ${
                        log.type === 'EMERGENCY' ? 'bg-rose-100 text-rose-600' :
                        log.type === 'FEE' ? 'bg-amber-100 text-amber-600' :
                        log.type === 'ACADEMIC' ? 'bg-indigo-100 text-indigo-600' :
                        'bg-gray-100 text-gray-600'
                      }`}>{log.type}</span>
                    </td>
                    <td className="px-6 py-4 text-sm font-medium text-gray-700">{log.subject}</td>
                    <td className="px-6 py-4 text-right">
                      <span className="text-[10px] font-black text-emerald-600 bg-emerald-50 px-3 py-1 rounded-full border border-emerald-100">
                        <i className="fas fa-check-circle mr-1"></i> VERIFIED
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'templates' && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {templates.map(temp => (
            <div key={temp.id} className="bg-white p-6 rounded-[2rem] border border-gray-100 shadow-sm hover:shadow-md transition-all group cursor-pointer border-t-4 border-t-indigo-500 flex flex-col justify-between">
              <div>
                <div className="flex justify-between items-start mb-4">
                  <span className={`px-2 py-0.5 rounded-lg text-[8px] font-black uppercase tracking-widest ${
                    temp.category === 'EMERGENCY' ? 'bg-rose-50 text-rose-600' : 'bg-indigo-50 text-indigo-600'
                  }`}>{temp.category}</span>
                  <div className="flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                    <button className="text-gray-400 hover:text-indigo-600"><i className="fas fa-edit text-xs"></i></button>
                    <button className="text-gray-400 hover:text-rose-600"><i className="fas fa-trash text-xs"></i></button>
                  </div>
                </div>
                <h3 className="font-bold text-gray-800 mb-2">{temp.name}</h3>
                <p className="text-xs text-gray-500 line-clamp-4 leading-relaxed font-medium mb-6 italic">"{temp.content}"</p>
              </div>
              <button onClick={() => handleUseTemplate(temp)} className="text-[10px] font-black text-indigo-600 uppercase tracking-widest hover:text-indigo-800 flex items-center gap-2 pt-4 border-t border-gray-50">
                Use this Template <i className="fas fa-arrow-right transition-transform group-hover:translate-x-1"></i>
              </button>
            </div>
          ))}
          <div className="border-2 border-dashed border-gray-200 rounded-[2rem] p-6 flex flex-col items-center justify-center gap-3 text-gray-400 hover:border-indigo-300 hover:text-indigo-500 transition-all cursor-pointer bg-white/50">
            <div className="w-12 h-12 rounded-full bg-gray-50 flex items-center justify-center mb-2"><i className="fas fa-plus text-xl"></i></div>
            <span className="text-xs font-black uppercase tracking-widest">New Standard Template</span>
          </div>
        </div>
      )}

      {activeTab === 'compose' && (
        <div className="glass-card max-w-3xl mx-auto rounded-[2.5rem] overflow-hidden shadow-sm border border-gray-100">
          <div className="bg-indigo-600 p-8 text-white flex justify-between items-center">
            <div>
              <h2 className="text-xl font-black">Secure Outreach Messenger</h2>
              <p className="text-indigo-100 text-[10px] font-bold uppercase tracking-widest mt-1">End-to-End Encrypted Guardian Delivery</p>
            </div>
            <div className="w-12 h-12 bg-white/10 rounded-2xl flex items-center justify-center"><i className="fas fa-paper-plane"></i></div>
          </div>
          <form onSubmit={handleSend} className="p-10 space-y-6">
            <div className="grid grid-cols-2 gap-6">
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Recipient Type</label>
                <select 
                  value={composeData.recipientType}
                  onChange={e => setComposeData({...composeData, recipientType: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black"
                >
                  <option value="all">Broadcast to All Guardians</option>
                  <option value="individual">Single Ward Guardian</option>
                </select>
              </div>
              <div>
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Communication Category</label>
                <select 
                  value={composeData.type}
                  onChange={e => setComposeData({...composeData, type: e.target.value as NotificationLog['type']})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black"
                >
                  <option value="GENERAL">General Announcement</option>
                  <option value="ACADEMIC">Academic Report</option>
                  <option value="EVENT">School Event</option>
                  <option value="FEE">Fee/Finance Notice</option>
                  <option value="EMERGENCY">Emergency Alert</option>
                </select>
              </div>
              {composeData.recipientType === 'individual' && (
                <div className="col-span-2">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Select Student Ward</label>
                  <select 
                    value={composeData.individualId}
                    onChange={e => setComposeData({...composeData, individualId: e.target.value})}
                    className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black"
                    required
                  >
                    <option value="">Choose student...</option>
                    {students.map(s => <option key={s.id} value={s.id}>{s.name} ({s.id})</option>)}
                  </select>
                </div>
              )}
              <div className="col-span-2">
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Secure Subject Line</label>
                <input 
                  type="text" 
                  value={composeData.subject}
                  onChange={e => setComposeData({...composeData, subject: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black" 
                  placeholder="e.g. Upcoming Parent Teacher Association Meeting" 
                  required
                />
              </div>
              <div className="col-span-2">
                <div className="flex justify-between items-center mb-2">
                   <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest block">Message Body</label>
                   <span className="text-[9px] text-indigo-400 font-bold">Tip: Use [StudentName] for auto-personalization</span>
                </div>
                <textarea 
                  value={composeData.content}
                  onChange={e => setComposeData({...composeData, content: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-4 focus:ring-2 focus:ring-indigo-500 outline-none font-medium text-black min-h-[200px] resize-none" 
                  placeholder="Type your secure message here..." 
                  required
                ></textarea>
              </div>
            </div>
            <div className="flex gap-4">
               <button 
                type="submit" 
                disabled={isSending}
                className="flex-1 bg-indigo-600 text-white py-4 rounded-3xl font-black uppercase tracking-widest shadow-xl shadow-indigo-100 hover:bg-indigo-700 active:scale-95 transition-all disabled:opacity-50 flex items-center justify-center gap-3"
               >
                {isSending ? <><i className="fas fa-circle-notch fa-spin"></i> DISPATCHING...</> : <><i className="fas fa-paper-plane"></i> DISPATCH SECURELY</>}
               </button>
               <button 
                type="button"
                className="w-16 h-14 bg-emerald-50 text-emerald-600 rounded-3xl hover:bg-emerald-100 transition-all flex items-center justify-center text-xl border border-emerald-100"
                title="AI Polish Message"
               >
                <i className="fas fa-magic"></i>
               </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default CommunicationHub;

import React, { useState, useCallback, useEffect, useRef } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent, NotificationLog, CommunicationTemplate } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS, DEFAULT_TEMPLATES } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';
import CommunicationHub from './components/CommunicationHub';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [isAdminMode, setIsAdminMode] = useState(true); // Toggle for Admin vs Guardian simulation

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  const [notificationLogs, setNotificationLogs] = useState<NotificationLog[]>(() => {
    const saved = localStorage.getItem('cc_notif_logs');
    return saved ? JSON.parse(saved) : [];
  });

  const [templates, setTemplates] = useState<CommunicationTemplate[]>(() => {
    const saved = localStorage.getItem('cc_templates');
    return saved ? JSON.parse(saved) : DEFAULT_TEMPLATES;
  });

  useEffect(() => {
    localStorage.setItem('cc_students', JSON.stringify(students));
  }, [students]);

  useEffect(() => {
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem('cc_events', JSON.stringify(events));
  }, [events]);

  useEffect(() => {
    localStorage.setItem('cc_notif_logs', JSON.stringify(notificationLogs));
  }, [notificationLogs]);

  useEffect(() => {
    localStorage.setItem('cc_templates', JSON.stringify(templates));
  }, [templates]);

  useEffect(() => {
    localStorage.setItem('cc_school_logo', schoolLogo);
  }, [schoolLogo]);

  useEffect(() => {
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
  }, [reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students? This action cannot be undone.`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleTeacherAttendanceChange = useCallback((teacherId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (t.id === teacherId) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleTeacherBulkAttendanceChange = useCallback((teacherIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setTeachers(prev => prev.map(t => {
      if (teacherIds.includes(t.id)) {
        return { ...t, attendance: { ...t.attendance, [today]: status } };
      }
      return t;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Are you sure you want to delete this event? This will remove it from the school calendar.')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const handleLogNotification = (log: NotificationLog) => {
    setNotificationLogs(prev => [log, ...prev]);
  };

  const renderView = () => {
    if (!isAdminMode) {
      // Basic simulation of "Guardian Access Only"
      const currentGuardian = MOCK_STUDENTS[0]; // Simulating logged-in guardian for S001
      return (
        <div className="space-y-8 animate-fadeIn">
          <div className="bg-white p-8 rounded-[2.5rem] shadow-sm border border-indigo-50">
            <h1 className="text-2xl font-black text-indigo-900 mb-2">Guardian Secure Portal</h1>
            <p className="text-gray-500 font-medium">Viewing academic records for: <span className="text-indigo-600 font-bold">{currentGuardian.name}</span></p>
          </div>
          <ReportCard 
            students={[currentGuardian]} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={() => {}}
            onLogNotification={() => {}}
          />
        </div>
      );
    }

    switch (currentView) {
      case 'dashboard':
        return (
          <Dashboard 
            students={students} 
            teachers={teachers} 
            onGradeUpdate={handleGradeUpdate} 
            schoolLogo={schoolLogo}
            onUpdateLogo={setSchoolLogo}
            events={events}
            onAddEvent={handleAddEvent}
            onUpdateEvent={handleUpdateEvent}
            onDeleteEvent={handleDeleteEvent}
          />
        );
      case 'students':
        return (
          <StudentList 
            students={students} 
            onAttendanceChange={handleAttendanceChange}
            onBulkAttendanceChange={handleBulkAttendanceChange}
            onAddStudent={handleAddStudent}
            onDeleteStudent={handleDeleteStudent}
            onBulkDeleteStudents={handleBulkDeleteStudents}
          />
        );
      case 'teachers':
        return (
          <TeacherList 
            teachers={teachers} 
            onAttendanceChange={handleTeacherAttendanceChange}
            onBulkAttendanceChange={handleTeacherBulkAttendanceChange}
            onAddTeacher={handleAddTeacher}
            onUpdateTeacher={handleUpdateTeacher}
            onDeleteTeacher={handleDeleteTeacher}
            onBulkDeleteTeachers={handleBulkDeleteTeachers}
          />
        );
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} />;
      case 'reports':
        return (
          <ReportCard 
            students={students} 
            schoolLogo={schoolLogo}
            reportSettings={reportSettings}
            onUpdateReportSettings={setReportSettings}
            onLogNotification={handleLogNotification}
          />
        );
      case 'notifications':
        return (
          <CommunicationHub 
            students={students} 
            notificationLogs={notificationLogs}
            onSendMessage={handleLogNotification}
            templates={templates}
            onUpdateTemplates={setTemplates}
          />
        );
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />

      <main className="flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide">
        <header className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-4">
             <div className="relative w-96 hidden md:block">
              <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
              <input 
                type="text" 
                placeholder="Secure audit search..." 
                className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none transition-shadow text-black font-medium"
              />
            </div>
            <button 
              onClick={() => setIsAdminMode(!isAdminMode)}
              className={`px-4 py-2 rounded-xl text-[10px] font-black uppercase tracking-widest border transition-all ${isAdminMode ? 'bg-indigo-50 text-indigo-600 border-indigo-100' : 'bg-orange-50 text-orange-600 border-orange-100'}`}
            >
              {isAdminMode ? 'Switch to Guardian View' : 'Back to Admin Hub'}
            </button>
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-emerald-100 shadow-sm">
              <span className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></span>
              <span className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">Secure Cloud Active</span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100 cursor-pointer hover:shadow-md transition-shadow group">
              <img src="https://ui-avatars.com/api/?name=Admin&background=6366f1&color=fff" className="w-8 h-8 rounded-xl object-cover" alt="" />
              <div>
                <p className="text-xs font-bold text-gray-800 group-hover:text-indigo-600 transition-colors">Super Admin</p>
                <p className="text-[10px] text-gray-400 font-semibold uppercase tracking-wider">CCIS Portal</p>
              </div>
            </div>
          </div>
        </header>

        {renderView()}
      </main>

      <style>{`
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useMemo, useEffect } from 'react';
import { Student, Grade } from '../types';
import { SUBJECTS } from '../constants';

interface GradingProps {
  students: Student[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
}

interface PerformanceAlert {
  id: string;
  studentName: string;
  score: number;
  subject: string;
  studentId: string;
}

const Grading: React.FC<GradingProps> = ({ students, onGradeUpdate }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0]);
  const [selectedTerm, setSelectedTerm] = useState(1);
  const [localScores, setLocalScores] = useState<Record<string, string>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);

  const THRESHOLD = 60;

  const studentGrades = useMemo(() => {
    return students.map(student => {
      const grade = student.grades.find(g => g.subject === selectedSubject && g.term === selectedTerm);
      return {
        id: student.id,
        name: student.name,
        avatar: student.avatar,
        gradeLevel: student.gradeLevel,
        currentScore: grade?.score ?? 0,
        maxScore: grade?.maxScore ?? 100
      };
    });
  }, [students, selectedSubject, selectedTerm]);

  const handleScoreChange = (studentId: string, val: string) => {
    if (val === '' || (Number(val) >= 0 && Number(val) <= 100)) {
      setLocalScores(prev => ({ ...prev, [studentId]: val }));
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    const newAlerts: PerformanceAlert[] = [];

    Object.entries(localScores).forEach(([studentId, scoreStr]) => {
      const score = Number(scoreStr);
      if (scoreStr !== '' && score < THRESHOLD) {
        const student = students.find(s => s.id === studentId);
        if (student) {
          newAlerts.push({
            id: `${studentId}-${Date.now()}`,
            studentName: student.name,
            score: score,
            subject: selectedSubject,
            studentId: studentId
          });
        }
      }
    });

    if (newAlerts.length > 0) {
      setAlerts(prev => [...newAlerts, ...prev].slice(0, 5));
    }

    await new Promise(resolve => setTimeout(resolve, 800));

    Object.entries(localScores).forEach(([studentId, score]) => {
      if (score !== '') {
        onGradeUpdate(studentId, selectedSubject, selectedTerm, Number(score));
      }
    });

    setLocalScores({});
    setIsSaving(false);
  };

  const notifyGuardianOfLowGrade = (alert: PerformanceAlert) => {
    const student = students.find(s => s.id === alert.studentId);
    if (student) {
      alert(`Academic Alert dispatched to guardian: ${student.guardianEmail}. Re: ${student.name}'s performance in ${alert.subject}.`);
      setAlerts(prev => prev.filter(a => a.id !== alert.id));
    }
  };

  const getStatusColor = (score: number) => {
    if (score >= 85) return 'bg-emerald-500';
    if (score >= 70) return 'bg-indigo-500';
    if (score >= THRESHOLD) return 'bg-amber-500';
    return 'bg-rose-500';
  };

  const getStatusIcon = (score: number) => {
    if (score >= 85) return 'fa-star';
    if (score >= 70) return 'fa-medal';
    if (score >= THRESHOLD) return 'fa-award';
    return 'fa-exclamation-triangle';
  };

  const getStatusText = (score: number) => {
    if (score >= 85) return 'Excellent';
    if (score >= 70) return 'Good';
    if (score >= THRESHOLD) return 'Passing';
    return 'Academic Alert';
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-10">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Grading Management</h1>
          <p className="text-gray-500 font-medium">Capture secure assessments and trigger academic alerts.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
            {[1, 2, 3].map(term => (
              <button
                key={term}
                onClick={() => setSelectedTerm(term)}
                className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${
                  selectedTerm === term ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                Term {term}
              </button>
            ))}
          </div>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            className="bg-white border-none rounded-2xl px-4 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black"
          >
            {SUBJECTS.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>
        </div>
      </div>

      {alerts.length > 0 && (
        <div className="space-y-3">
          <div className="flex items-center justify-between px-2">
            <h3 className="text-xs font-black text-rose-500 uppercase tracking-widest flex items-center gap-2">
              <i className="fas fa-bell animate-swing"></i>
              Academic Performance Alerts
            </h3>
            <button onClick={() => setAlerts([])} className="text-[10px] font-bold text-gray-400 hover:text-gray-600 uppercase tracking-wider">Dismiss All</button>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
            {alerts.map((alert) => (
              <div key={alert.id} className="bg-rose-50 border border-rose-100 p-4 rounded-2xl flex items-center justify-between group animate-fadeIn">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 bg-rose-500 text-white rounded-full flex items-center justify-center text-xs">
                    <i className="fas fa-exclamation"></i>
                  </div>
                  <div>
                    <p className="text-xs font-bold text-gray-800">{alert.studentName}</p>
                    <p className="text-[10px] text-rose-600 font-black uppercase tracking-tighter">{alert.score}% in {alert.subject}</p>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button 
                    onClick={() => notifyGuardianOfLowGrade(alert)}
                    className="w-8 h-8 rounded-lg bg-rose-200 text-rose-700 hover:bg-rose-300 transition-colors flex items-center justify-center"
                    title="Notify Guardian"
                  >
                    <i className="fas fa-paper-plane text-[10px]"></i>
                  </button>
                  <button 
                    onClick={() => setAlerts(prev => prev.filter(a => a.id !== alert.id))}
                    className="text-gray-300 hover:text-rose-500 transition-colors"
                  >
                    <i className="fas fa-times text-sm"></i>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="glass-card rounded-[2rem] overflow-hidden shadow-sm border border-gray-100">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase tracking-widest">Student</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-center tracking-widest">Prev Score</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-center w-64 tracking-widest">Assessment Input</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-right tracking-widest">Rating</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {studentGrades.map((item) => {
              const currentVal = localScores[item.id] !== undefined ? localScores[item.id] : '';
              const displayScore = currentVal !== '' ? Number(currentVal) : item.currentScore;
              const statusColor = getStatusColor(displayScore);
              const isFailing = displayScore < THRESHOLD;

              return (
                <tr key={item.id} className={`transition-colors group ${isFailing ? 'bg-rose-50/20' : 'hover:bg-gray-50/50'}`}>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={item.avatar} className={`w-12 h-12 rounded-2xl object-cover border-2 transition-all ${isFailing ? 'border-rose-300' : 'border-transparent'}`} alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{item.name}</p>
                        <p className="text-[10px] text-gray-500 font-black uppercase tracking-widest">{item.gradeLevel}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-center">
                    <span className={`inline-block px-3 py-1 rounded-full font-black text-[10px] ${item.currentScore < THRESHOLD ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-gray-500'}`}>
                      {item.currentScore} / 100
                    </span>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-3">
                      <input
                        type="number"
                        min="0"
                        max="100"
                        value={currentVal}
                        onChange={(e) => handleScoreChange(item.id, e.target.value)}
                        className={`w-20 text-center py-2 bg-gray-50 border-2 rounded-xl outline-none font-black text-black transition-all ${
                          currentVal !== '' && Number(currentVal) < THRESHOLD 
                            ? 'border-rose-400 text-rose-600 focus:bg-rose-50' 
                            : 'border-transparent focus:border-indigo-500 focus:bg-white'
                        }`}
                        placeholder={item.currentScore.toString()}
                      />
                      <div className="flex-1 h-1.5 bg-gray-100 rounded-full overflow-hidden">
                        <div 
                          className={`h-full transition-all duration-500 ${statusColor}`}
                          style={{ width: `${displayScore}%` }}
                        ></div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex flex-col items-end">
                      <span className={`inline-flex items-center gap-2 text-xs font-black px-3 py-1 rounded-full ${statusColor.replace('bg-', 'text-').replace('500', '600')} ${statusColor.replace('bg-', 'bg-')}/10`}>
                        <i className={`fas ${getStatusIcon(displayScore)} text-[10px]`}></i>
                        {getStatusText(displayScore)}
                      </span>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <div className="p-6 bg-gray-50/50 border-t border-gray-100 flex justify-end">
          <button
            onClick={handleSave}
            disabled={isSaving || Object.keys(localScores).length === 0}
            className="bg-indigo-600 text-white px-8 py-4 rounded-2xl font-black uppercase tracking-[0.2em] hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-xl shadow-indigo-100 flex items-center gap-3 active:scale-95"
          >
            {isSaving ? <><i className="fas fa-sync fa-spin"></i> SECURING RECORD...</> : <><i className="fas fa-check-double"></i> FINALIZE ASSESSMENTS</>}
          </button>
        </div>
      </div>

      <style>{`
        @keyframes swing {
          0%, 100% { transform: rotate(0deg); }
          20% { transform: rotate(15deg); }
          40% { transform: rotate(-10deg); }
          60% { transform: rotate(5deg); }
          80% { transform: rotate(-5deg); }
        }
        .animate-swing {
          animation: swing 2s infinite ease-in-out;
          transform-origin: top center;
        }
      `}</style>
    </div>
  );
};

export default Grading;

import React, { useState, useMemo, useEffect } from 'react';
import { Student, Grade } from '../types';
import { SUBJECTS } from '../constants';

interface GradingProps {
  students: Student[];
  onGradeUpdate: (studentId: string, subject: string, term: number, score: number) => void;
}

interface PerformanceAlert {
  id: string;
  studentName: string;
  score: number;
  subject: string;
  studentId: string;
}

const Grading: React.FC<GradingProps> = ({ students, onGradeUpdate }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0]);
  const [selectedTerm, setSelectedTerm] = useState(1);
  const [localScores, setLocalScores] = useState<Record<string, string>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);

  const THRESHOLD = 60;

  const studentGrades = useMemo(() => {
    return students.map(student => {
      const grade = student.grades.find(g => g.subject === selectedSubject && g.term === selectedTerm);
      return {
        id: student.id,
        name: student.name,
        avatar: student.avatar,
        gradeLevel: student.gradeLevel,
        currentScore: grade?.score ?? 0,
        maxScore: grade?.maxScore ?? 100
      };
    });
  }, [students, selectedSubject, selectedTerm]);

  const handleScoreChange = (studentId: string, val: string) => {
    if (val === '' || (Number(val) >= 0 && Number(val) <= 100)) {
      setLocalScores(prev => ({ ...prev, [studentId]: val }));
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    const newAlerts: PerformanceAlert[] = [];

    Object.entries(localScores).forEach(([studentId, scoreStr]) => {
      const score = Number(scoreStr);
      if (scoreStr !== '' && score < THRESHOLD) {
        const student = students.find(s => s.id === studentId);
        if (student) {
          newAlerts.push({
            id: `${studentId}-${Date.now()}`,
            studentName: student.name,
            score: score,
            subject: selectedSubject,
            studentId: studentId
          });
        }
      }
    });

    if (newAlerts.length > 0) {
      setAlerts(prev => [...newAlerts, ...prev].slice(0, 5));
    }

    await new Promise(resolve => setTimeout(resolve, 800));

    Object.entries(localScores).forEach(([studentId, score]) => {
      if (score !== '') {
        onGradeUpdate(studentId, selectedSubject, selectedTerm, Number(score));
      }
    });

    setLocalScores({});
    setIsSaving(false);
  };

  // Renamed parameter from 'alert' to 'perfAlert' to avoid shadowing the global window.alert function
  const notifyGuardianOfLowGrade = (perfAlert: PerformanceAlert) => {
    const student = students.find(s => s.id === perfAlert.studentId);
    if (student) {
      window.alert(`Academic Alert dispatched to guardian: ${student.guardianEmail}. Re: ${student.name}'s performance in ${perfAlert.subject}.`);
      setAlerts(prev => prev.filter(a => a.id !== perfAlert.id));
    }
  };

  const getStatusColor = (score: number) => {
    if (score >= 85) return 'bg-emerald-500';
    if (score >= 70) return 'bg-indigo-500';
    if (score >= THRESHOLD) return 'bg-amber-500';
    return 'bg-rose-500';
  };

  const getStatusIcon = (score: number) => {
    if (score >= 85) return 'fa-star';
    if (score >= 70) return 'fa-medal';
    if (score >= THRESHOLD) return 'fa-award';
    return 'fa-exclamation-triangle';
  };

  const getStatusText = (score: number) => {
    if (score >= 85) return 'Excellent';
    if (score >= 70) return 'Good';
    if (score >= THRESHOLD) return 'Passing';
    return 'Academic Alert';
  };

  return (
    <div className="space-y-6 animate-fadeIn pb-10">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Grading Management</h1>
          <p className="text-gray-500 font-medium">Capture secure assessments and trigger academic alerts.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
            {[1, 2, 3].map(term => (
              <button
                key={term}
                onClick={() => setSelectedTerm(term)}
                className={`px-4 py-2 rounded-xl text-sm font-bold transition-all ${
                  selectedTerm === term ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-500 hover:bg-gray-50'
                }`}
              >
                Term {term}
              </button>
            ))}
          </div>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            className="bg-white border-none rounded-2xl px-4 py-3 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none font-bold text-black"
          >
            {SUBJECTS.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>
        </div>
      </div>

      {alerts.length > 0 && (
        <div className="space-y-3">
          <div className="flex items-center justify-between px-2">
            <h3 className="text-xs font-black text-rose-500 uppercase tracking-widest flex items-center gap-2">
              <i className="fas fa-bell animate-swing"></i>
              Academic Performance Alerts
            </h3>
            <button onClick={() => setAlerts([])} className="text-[10px] font-bold text-gray-400 hover:text-gray-600 uppercase tracking-wider">Dismiss All</button>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
            {alerts.map((alert) => (
              <div key={alert.id} className="bg-rose-50 border border-rose-100 p-4 rounded-2xl flex items-center justify-between group animate-fadeIn">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 bg-rose-500 text-white rounded-full flex items-center justify-center text-xs">
                    <i className="fas fa-exclamation"></i>
                  </div>
                  <div>
                    <p className="text-xs font-bold text-gray-800">{alert.studentName}</p>
                    <p className="text-[10px] text-rose-600 font-black uppercase tracking-tighter">{alert.score}% in {alert.subject}</p>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button 
                    onClick={() => notifyGuardianOfLowGrade(alert)}
                    className="w-8 h-8 rounded-lg bg-rose-200 text-rose-700 hover:bg-rose-300 transition-colors flex items-center justify-center"
                    title="Notify Guardian"
                  >
                    <i className="fas fa-paper-plane text-[10px]"></i>
                  </button>
                  <button 
                    onClick={() => setAlerts(prev => prev.filter(a => a.id !== alert.id))}
                    className="text-gray-300 hover:text-rose-500 transition-colors"
                  >
                    <i className="fas fa-times text-sm"></i>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="glass-card rounded-[2rem] overflow-hidden shadow-sm border border-gray-100">
        <table className="w-full text-left">
          <thead className="bg-gray-50 border-b border-gray-100">
            <tr>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase tracking-widest">Student</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-center tracking-widest">Prev Score</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-center w-64 tracking-widest">Assessment Input</th>
              <th className="px-6 py-4 text-[10px] font-black text-gray-600 uppercase text-right tracking-widest">Rating</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {studentGrades.map((item) => {
              const currentVal = localScores[item.id] !== undefined ? localScores[item.id] : '';
              const displayScore = currentVal !== '' ? Number(currentVal) : item.currentScore;
              const statusColor = getStatusColor(displayScore);
              const isFailing = displayScore < THRESHOLD;

              return (
                <tr key={item.id} className={`transition-colors group ${isFailing ? 'bg-rose-50/20' : 'hover:bg-gray-50/50'}`}>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-4">
                      <img src={item.avatar} className={`w-12 h-12 rounded-2xl object-cover border-2 transition-all ${isFailing ? 'border-rose-300' : 'border-transparent'}`} alt="" />
                      <div>
                        <p className="font-bold text-gray-800">{item.name}</p>
                        <p className="text-[10px] text-gray-500 font-black uppercase tracking-widest">{item.gradeLevel}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-center">
                    <span className={`inline-block px-3 py-1 rounded-full font-black text-[10px] ${item.currentScore < THRESHOLD ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-gray-500'}`}>
                      {item.currentScore} / 100
                    </span>
                  </td>
                  <td className="px-6 py-5">
                    <div className="flex items-center gap-3">
                      <input
                        type="number"
                        min="0"
                        max="100"
                        value={currentVal}
                        onChange={(e) => handleScoreChange(item.id, e.target.value)}
                        className={`w-20 text-center py-2 bg-gray-50 border-2 rounded-xl outline-none font-black text-black transition-all ${
                          currentVal !== '' && Number(currentVal) < THRESHOLD 
                            ? 'border-rose-400 text-rose-600 focus:bg-rose-50' 
                            : 'border-transparent focus:border-indigo-500 focus:bg-white'
                        }`}
                        placeholder={item.currentScore.toString()}
                      />
                      <div className="flex-1 h-1.5 bg-gray-100 rounded-full overflow-hidden">
                        <div 
                          className={`h-full transition-all duration-500 ${statusColor}`}
                          style={{ width: `${displayScore}%` }}
                        ></div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-5 text-right">
                    <div className="flex flex-col items-end">
                      <span className={`inline-flex items-center gap-2 text-xs font-black px-3 py-1 rounded-full ${statusColor.replace('bg-', 'text-').replace('500', '600')} ${statusColor.replace('bg-', 'bg-')}/10`}>
                        <i className={`fas ${getStatusIcon(displayScore)} text-[10px]`}></i>
                        {getStatusText(displayScore)}
                      </span>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <div className="p-6 bg-gray-50/50 border-t border-gray-100 flex justify-end">
          <button
            onClick={handleSave}
            disabled={isSaving || Object.keys(localScores).length === 0}
            className="bg-indigo-600 text-white px-8 py-4 rounded-2xl font-black uppercase tracking-[0.2em] hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all shadow-xl shadow-indigo-100 flex items-center gap-3 active:scale-95"
          >
            {isSaving ? <><i className="fas fa-sync fa-spin"></i> SECURING RECORD...</> : <><i className="fas fa-check-double"></i> FINALIZE ASSESSMENTS</>}
          </button>
        </div>
      </div>

      <style>{`
        @keyframes swing {
          0%, 100% { transform: rotate(0deg); }
          20% { transform: rotate(15deg); }
          40% { transform: rotate(-10deg); }
          60% { transform: rotate(5deg); }
          80% { transform: rotate(-5deg); }
        }
        .animate-swing {
          animation: swing 2s infinite ease-in-out;
          transform-origin: top center;
        }
      `}</style>
    </div>
  );
};

export default Grading;

import { NotificationLog } from "../types";

/**
 * Mock Secure Email Delivery Service
 * In a real production environment, this would call a Node.js/Python backend
 * which interfaces with SendGrid, AWS SES, or a private SMTP server.
 */
export const sendSecureEmail = async (
  recipient: string,
  subject: string,
  body: string,
  type: NotificationLog['type']
): Promise<Omit<NotificationLog, 'id' | 'timestamp'>> => {
  console.log(`[SMTP-SIMULATOR] Connecting to secure mail server...`);
  console.log(`[SMTP-SIMULATOR] Sending ${type} message to ${recipient}...`);

  // Simulate network latency
  await new Promise(resolve => setTimeout(resolve, 1000));

  // 95% success rate simulation
  const success = Math.random() > 0.05;

  if (success) {
    console.log(`[SMTP-SIMULATOR] Message delivered successfully.`);
    return {
      recipientEmail: recipient,
      studentName: "System Registered Ward", // Placeholder, usually looked up by ID
      subject: subject,
      type: type,
      status: 'SENT'
    };
  } else {
    console.error(`[SMTP-SIMULATOR] Failed to deliver message. Relay timeout.`);
    return {
      recipientEmail: recipient,
      studentName: "System Registered Ward",
      subject: subject,
      type: type,
      status: 'FAILED'
    };
  }
};

