        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { Student, NotificationLog, CommunicationTemplate } from '../types';
import { sendSecureEmail } from '../services/emailService';

interface CommunicationHubProps {
  students: Student[];
  notificationLogs: NotificationLog[];
  onSendMessage: (log: NotificationLog) => void;
  templates: CommunicationTemplate[];
  onUpdateTemplates: (templates: CommunicationTemplate[]) => void;
}

const CommunicationHub: React.FC<CommunicationHubProps> = ({ 
  students, 
  notificationLogs, 
  onSendMessage,
  templates,
  onUpdateTemplates
}) => {
  const [activeTab, setActiveTab] = useState<'history' | 'templates' | 'compose'>('history');
  const [searchLog, setSearchLog] = useState('');
  const [isSending, setIsSending] = useState(false);

  const [composeData, setComposeData] = useState({
    recipientType: 'all',
    individualId: '',
    subject: '',
    content: '',
    type: 'GENERAL' as NotificationLog['type']
  });

  const handleUseTemplate = (template: CommunicationTemplate) => {
    setComposeData({
      ...composeData,
      subject: template.subject,
      content: template.content,
      type: template.category
    });
    setActiveTab('compose');
  };

  const handleSend = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSending(true);

    const targetStudents: Student[] = [];
    if (composeData.recipientType === 'all') {
      targetStudents.push(...students);
    } else if (composeData.individualId) {
      const s = students.find(std => std.id === composeData.individualId);
      if (s) targetStudents.push(s);
    }

    for (const std of targetStudents) {
      const result = await sendSecureEmail(
        std.guardianEmail,
        composeData.subject.replace('[StudentName]', std.name),
        composeData.content.replace('[StudentName]', std.name),
        composeData.type
      );

      onSendMessage({
        ...result,
        id: Math.random().toString(36).substr(2, 9),
        timestamp: new Date().toISOString(),
        studentName: std.name,
      } as NotificationLog);
    }

    setIsSending(false);
    alert(`Transmission complete. Check audit logs for delivery verification.`);
    setActiveTab('history');
  };

  const filteredLogs = notificationLogs.filter(log => 
    log.studentName.toLowerCase().includes(searchLog.toLowerCase()) ||
    log.recipientEmail.toLowerCase().includes(searchLog.toLowerCase()) ||
    log.subject.toLowerCase().includes(searchLog.toLowerCase())
  );

  return (
    <div className="space-y-6 animate-fadeIn pb-20">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Communication Hub</h1>
          <p className="text-gray-500 font-medium">Securely broadcast announcements and verify delivery status.</p>
        </div>
        <div className="flex bg-white rounded-2xl p-1 shadow-sm border border-gray-100">
          <button onClick={() => setActiveTab('history')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'history' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>Audit Logs</button>
          <button onClick={() => setActiveTab('templates')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'templates' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>Templates</button>
          <button onClick={() => setActiveTab('compose')} className={`px-6 py-2.5 rounded-xl text-xs font-black transition-all ${activeTab === 'compose' ? 'bg-indigo-600 text-white shadow-md' : 'text-gray-400 hover:text-indigo-600'}`}>Compose</button>
        </div>
      </div>

      {activeTab === 'history' && (
        <div className="space-y-4">
          <div className="relative max-w-md">
            <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
            <input 
              type="text" 
              placeholder="Filter audit logs..." 
              value={searchLog}
              onChange={(e) => setSearchLog(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none text-black font-medium"
            />
          </div>

          <div className="glass-card rounded-[2rem] overflow-hidden shadow-sm border border-gray-100">
            <table className="w-full text-left">
              <thead className="bg-gray-50 border-b border-gray-100">
                <tr>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Time (UTC)</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Recipient</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Type</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest">Subject</th>
                  <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase tracking-widest text-right">Verification</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-50">
                {filteredLogs.length === 0 ? (
                  <tr><td colSpan={5} className="px-6 py-10 text-center text-gray-400 italic font-medium">No transmission logs found.</td></tr>
                ) : filteredLogs.map(log => (
                  <tr key={log.id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-6 py-4 text-[10px] text-gray-400 font-mono">{log.timestamp.split('T')[1].substring(0,8)}</td>
                    <td className="px-6 py-4">
                      <p className="text-sm font-bold text-gray-800">{log.studentName}</p>
                      <p className="text-[10px] text-indigo-600 font-bold">{log.recipientEmail}</p>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-2 py-0.5 rounded-lg text-[8px] font-black uppercase ${
                        log.type === 'EMERGENCY' ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-gray-600'
                      }`}>{log.type}</span>
                    </td>
                    <td className="px-6 py-4 text-sm font-medium text-gray-700 truncate max-w-xs">{log.subject}</td>
                    <td className="px-6 py-4 text-right">
                      {log.status === 'SENT' ? (
                        <span className="text-[10px] font-black text-emerald-600 bg-emerald-50 px-3 py-1 rounded-full border border-emerald-100">
                          <i className="fas fa-check-circle mr-1"></i> DELIVERED
                        </span>
                      ) : (
                        <span className="text-[10px] font-black text-rose-600 bg-rose-50 px-3 py-1 rounded-full border border-rose-100">
                          <i className="fas fa-exclamation-triangle mr-1"></i> FAILED
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'templates' && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {templates.map(temp => (
            <div key={temp.id} className="bg-white p-8 rounded-[2.5rem] border border-gray-100 shadow-sm hover:shadow-md transition-all group border-t-4 border-t-indigo-500 flex flex-col justify-between">
              <div>
                <p className="text-[10px] font-black text-indigo-600 uppercase tracking-widest mb-2">{temp.category}</p>
                <h3 className="font-bold text-gray-800 mb-4">{temp.name}</h3>
                <p className="text-xs text-gray-500 italic line-clamp-4 leading-relaxed font-medium mb-6">"{temp.content}"</p>
              </div>
              <button onClick={() => handleUseTemplate(temp)} className="w-full py-3 bg-gray-50 text-indigo-600 rounded-xl text-[10px] font-black uppercase tracking-widest hover:bg-indigo-600 hover:text-white transition-all">Use Template</button>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'compose' && (
        <div className="max-w-3xl mx-auto glass-card rounded-[2.5rem] border border-gray-100 shadow-sm overflow-hidden">
          <div className="bg-indigo-600 p-8 text-white">
            <h2 className="text-xl font-black">Compose Outbound Message</h2>
            <p className="text-indigo-100 text-[10px] font-bold uppercase mt-1">Authorized Secure Relay Service</p>
          </div>
          <form onSubmit={handleSend} className="p-10 space-y-6">
            <div className="grid grid-cols-2 gap-6">
              <div className="col-span-2">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-2 block">Recipients</label>
                <select 
                  value={composeData.recipientType}
                  onChange={e => setComposeData({...composeData, recipientType: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 text-black font-bold outline-none"
                >
                  <option value="all">Broadcast to All Guardians</option>
                  <option value="individual">Individual Ward Contact</option>
                </select>
              </div>
              {composeData.recipientType === 'individual' && (
                <div className="col-span-2">
                  <label className="text-[10px] font-black text-gray-400 uppercase mb-2 block">Select Student</label>
                  <select 
                    value={composeData.individualId}
                    onChange={e => setComposeData({...composeData, individualId: e.target.value})}
                    className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 text-black font-bold outline-none"
                    required
                  >
                    <option value="">Choose ward...</option>
                    {students.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                  </select>
                </div>
              )}
              <div className="col-span-2">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-2 block">Email Subject</label>
                <input 
                  type="text" 
                  value={composeData.subject}
                  onChange={e => setComposeData({...composeData, subject: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-3 focus:ring-2 focus:ring-indigo-500 text-black font-bold outline-none" 
                  required 
                />
              </div>
              <div className="col-span-2">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-2 block">Message Body</label>
                <textarea 
                  value={composeData.content}
                  onChange={e => setComposeData({...composeData, content: e.target.value})}
                  className="w-full bg-gray-50 border-none rounded-2xl px-5 py-4 focus:ring-2 focus:ring-indigo-500 text-black font-medium outline-none min-h-[200px] resize-none" 
                  required 
                />
              </div>
            </div>
            <button 
              type="submit" 
              disabled={isSending}
              className="w-full bg-indigo-600 text-white py-4 rounded-3xl font-black uppercase tracking-widest shadow-xl disabled:opacity-50 flex items-center justify-center gap-3 transition-all"
            >
              {isSending ? <><i className="fas fa-sync fa-spin"></i> RELAYING...</> : <><i className="fas fa-paper-plane"></i> DISPATCH SECURELY</>}
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default CommunicationHub;

import React, { useState, useCallback, useEffect } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent, NotificationLog, CommunicationTemplate } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS, DEFAULT_TEMPLATES } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';
import CommunicationHub from './components/CommunicationHub';
import GuardianPortal from './components/GuardianPortal';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [isAdminMode, setIsAdminMode] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  const [notificationLogs, setNotificationLogs] = useState<NotificationLog[]>(() => {
    const saved = localStorage.getItem('cc_notif_logs');
    return saved ? JSON.parse(saved) : [];
  });

  const [templates, setTemplates] = useState<CommunicationTemplate[]>(() => {
    const saved = localStorage.getItem('cc_templates');
    return saved ? JSON.parse(saved) : DEFAULT_TEMPLATES;
  });

  // Unified persistence with visual indicator
  useEffect(() => {
    setIsSaving(true);
    localStorage.setItem('cc_students', JSON.stringify(students));
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
    localStorage.setItem('cc_events', JSON.stringify(events));
    localStorage.setItem('cc_notif_logs', JSON.stringify(notificationLogs));
    localStorage.setItem('cc_templates', JSON.stringify(templates));
    localStorage.setItem('cc_school_logo', schoolLogo);
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
    const timer = setTimeout(() => setIsSaving(false), 800);
    return () => clearTimeout(timer);
  }, [students, teachers, events, notificationLogs, templates, schoolLogo, reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => {
    setStudents(prev => [newStudent, ...prev]);
  }, []);

  const handleDeleteStudent = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this student? All records will be lost.')) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  }, []);

  const handleBulkDeleteStudents = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} students?`)) {
      setStudents(prev => prev.filter(s => !ids.includes(s.id)));
    }
  }, []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => {
    setTeachers(prev => [newTeacher, ...prev]);
  }, []);

  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);

  const handleDeleteTeacher = useCallback((id: string) => {
    if (confirm('Are you sure you want to remove this faculty member?')) {
      setTeachers(prev => prev.filter(t => t.id !== id));
    }
  }, []);

  const handleBulkDeleteTeachers = useCallback((ids: string[]) => {
    if (confirm(`Are you sure you want to remove ${ids.length} staff members?`)) {
      setTeachers(prev => prev.filter(t => !ids.includes(t.id)));
    }
  }, []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => {
      if (studentIds.includes(s.id)) {
        return { ...s, attendance: { ...s.attendance, [today]: status } };
      }
      return s;
    }));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) {
          newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        } else {
          newGrades.push({ subject, score, maxScore: 100, term });
        }
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => {
    setEvents(prev => [...prev, event]);
  }, []);

  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);

  const handleDeleteEvent = useCallback((id: string) => {
    if (confirm('Are you sure you want to delete this event?')) {
      setEvents(prev => prev.filter(e => e.id !== id));
    }
  }, []);

  const handleLogNotification = useCallback((log: NotificationLog) => {
    setNotificationLogs(prev => [log, ...prev]);
  }, []);

  const renderView = () => {
    if (!isAdminMode) {
      const ward = students.find(s => s.id === 'S001') || students[0];
      return <GuardianPortal ward={ward} schoolLogo={schoolLogo} />;
    }

    switch (currentView) {
      case 'dashboard':
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
      case 'students':
        return <StudentList students={students} onAttendanceChange={handleAttendanceChange} onBulkAttendanceChange={handleBulkAttendanceChange} onAddStudent={handleAddStudent} onDeleteStudent={handleDeleteStudent} onBulkDeleteStudents={handleBulkDeleteStudents} />;
      case 'teachers':
        return <TeacherList teachers={teachers} onAttendanceChange={() => {}} onBulkAttendanceChange={() => {}} onAddTeacher={handleAddTeacher} onUpdateTeacher={handleUpdateTeacher} onDeleteTeacher={handleDeleteTeacher} onBulkDeleteTeachers={() => {}} />;
      case 'grading':
        return <Grading students={students} onGradeUpdate={handleGradeUpdate} onLogNotification={handleLogNotification} />;
      case 'reports':
        return <ReportCard students={students} schoolLogo={schoolLogo} reportSettings={reportSettings} onUpdateReportSettings={setReportSettings} onLogNotification={handleLogNotification} />;
      case 'notifications':
        return <CommunicationHub students={students} notificationLogs={notificationLogs} onSendMessage={handleLogNotification} templates={templates} onUpdateTemplates={setTemplates} />;
      default:
        return <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />;
    }
  };

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      {isAdminMode && <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />}

      <main className={`flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide ${!isAdminMode ? 'max-w-7xl mx-auto' : ''}`}>
        <header className="flex justify-between items-center mb-8 no-print">
          <div className="flex items-center gap-4">
             {isAdminMode && (
               <div className="relative w-96 hidden md:block">
                <i className="fas fa-search absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"></i>
                <input type="text" placeholder="Search system records..." className="w-full pl-12 pr-4 py-3 bg-white rounded-2xl border-none shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none text-black font-medium" />
              </div>
             )}
            <button 
              onClick={() => setIsAdminMode(!isAdminMode)}
              className={`px-4 py-2 rounded-xl text-[10px] font-black uppercase tracking-widest border transition-all shadow-sm ${isAdminMode ? 'bg-indigo-50 text-indigo-600 border-indigo-100' : 'bg-rose-50 text-rose-600 border-rose-100'}`}
            >
              {isAdminMode ? 'Simulate Guardian View' : 'Back to Admin Hub'}
            </button>
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-3 bg-white px-4 py-2 rounded-2xl border border-gray-100 shadow-sm">
              <span className={`w-2 h-2 rounded-full ${isSaving ? 'bg-orange-500 animate-pulse' : 'bg-emerald-500'}`}></span>
              <span className="text-[10px] font-black text-gray-500 uppercase tracking-widest">
                {isSaving ? 'Syncing Changes...' : 'System Synced'}
              </span>
            </div>
            <div className="flex items-center gap-3 bg-white p-2 pr-4 rounded-2xl shadow-sm border border-gray-100">
