import React, { useState, useCallback, useEffect } from 'react';
import { View, Student, Teacher, AttendanceStatus, SchoolEvent, NotificationLog, CommunicationTemplate } from './types';
import { MOCK_STUDENTS, MOCK_TEACHERS, DEFAULT_TEMPLATES } from './constants';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import StudentList from './components/StudentList';
import ReportCard from './components/ReportCard';
import Grading from './components/Grading';
import TeacherList from './components/TeacherList';
import CommunicationHub from './components/CommunicationHub';
import GuardianPortal from './components/GuardianPortal';

const DEFAULT_LOGO = "https://images.unsplash.com/photo-1546410531-bb4caa6b424d?auto=format&fit=crop&q=80&w=100";

const DEFAULT_EVENTS: SchoolEvent[] = [
  { id: '1', date: '2024-10-24', time: '09:00', title: 'Mid-Term Examinations', location: 'CCIS Hall', description: 'Mandatory exams for all grades.', color: 'text-indigo-600', bg: 'bg-indigo-50' },
  { id: '2', date: '2024-11-02', time: '14:00', title: 'Parent-Teacher Meeting', location: 'Main Auditorium', description: 'Review student performance with faculty.', color: 'text-purple-600', bg: 'bg-purple-50' },
  { id: '3', date: '2024-11-15', time: '10:00', title: 'Science Exhibition', location: 'Lab Block B', description: 'Showcasing student scientific projects.', color: 'text-emerald-600', bg: 'bg-emerald-50' },
];

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<View>('dashboard');
  const [isAdminMode, setIsAdminMode] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  const [schoolLogo, setSchoolLogo] = useState<string>(() => {
    return localStorage.getItem('cc_school_logo') || DEFAULT_LOGO;
  });

  const [reportSettings, setReportSettings] = useState(() => {
    const saved = localStorage.getItem('cc_report_settings');
    return saved ? JSON.parse(saved) : {
      headOfSchool: 'S. Thompson',
      signature: '',
      authPrefix: 'ES-2024-TR'
    };
  });

  const [students, setStudents] = useState<Student[]>(() => {
    const saved = localStorage.getItem('cc_students');
    return saved ? JSON.parse(saved) : MOCK_STUDENTS;
  });

  const [teachers, setTeachers] = useState<Teacher[]>(() => {
    const saved = localStorage.getItem('cc_teachers');
    return saved ? JSON.parse(saved) : MOCK_TEACHERS;
  });

  const [events, setEvents] = useState<SchoolEvent[]>(() => {
    const saved = localStorage.getItem('cc_events');
    return saved ? JSON.parse(saved) : DEFAULT_EVENTS;
  });

  const [notificationLogs, setNotificationLogs] = useState<NotificationLog[]>(() => {
    const saved = localStorage.getItem('cc_notif_logs');
    return saved ? JSON.parse(saved) : [];
  });

  const [templates, setTemplates] = useState<CommunicationTemplate[]>(() => {
    const saved = localStorage.getItem('cc_templates');
    return saved ? JSON.parse(saved) : DEFAULT_TEMPLATES;
  });

  // Global Persistence Engine
  useEffect(() => {
    setIsSaving(true);
    localStorage.setItem('cc_students', JSON.stringify(students));
    localStorage.setItem('cc_teachers', JSON.stringify(teachers));
    localStorage.setItem('cc_events', JSON.stringify(events));
    localStorage.setItem('cc_notif_logs', JSON.stringify(notificationLogs));
    localStorage.setItem('cc_templates', JSON.stringify(templates));
    localStorage.setItem('cc_school_logo', schoolLogo);
    localStorage.setItem('cc_report_settings', JSON.stringify(reportSettings));
    const timer = setTimeout(() => setIsSaving(false), 800);
    return () => clearTimeout(timer);
  }, [students, teachers, events, notificationLogs, templates, schoolLogo, reportSettings]);

  const handleAddStudent = useCallback((newStudent: Student) => setStudents(prev => [newStudent, ...prev]), []);
  const handleDeleteStudent = useCallback((id: string) => setStudents(prev => prev.filter(s => s.id !== id)), []);
  const handleBulkDeleteStudents = useCallback((ids: string[]) => setStudents(prev => prev.filter(s => !ids.includes(s.id))), []);

  const handleAddTeacher = useCallback((newTeacher: Teacher) => setTeachers(prev => [newTeacher, ...prev]), []);
  const handleUpdateTeacher = useCallback((updatedTeacher: Teacher) => {
    setTeachers(prev => prev.map(t => t.id === updatedTeacher.id ? updatedTeacher : t));
  }, []);
  const handleDeleteTeacher = useCallback((id: string) => setTeachers(prev => prev.filter(t => t.id !== id)), []);
  const handleBulkDeleteTeachers = useCallback((ids: string[]) => setTeachers(prev => prev.filter(t => !ids.includes(t.id))), []);

  const handleAttendanceChange = useCallback((studentId: string, status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => s.id === studentId ? { ...s, attendance: { ...s.attendance, [today]: status } } : s));
  }, []);

  const handleBulkAttendanceChange = useCallback((studentIds: string[], status: AttendanceStatus) => {
    const today = new Date().toISOString().split('T')[0];
    setStudents(prev => prev.map(s => studentIds.includes(s.id) ? { ...s, attendance: { ...s.attendance, [today]: status } } : s));
  }, []);

  const handleGradeUpdate = useCallback((studentId: string, subject: string, term: number, score: number) => {
    setStudents(prev => prev.map(s => {
      if (s.id === studentId) {
        const existingGradeIdx = s.grades.findIndex(g => g.subject === subject && g.term === term);
        const newGrades = [...s.grades];
        if (existingGradeIdx > -1) newGrades[existingGradeIdx] = { ...newGrades[existingGradeIdx], score };
        else newGrades.push({ subject, score, maxScore: 100, term });
        return { ...s, grades: newGrades };
      }
      return s;
    }));
  }, []);

  const handleAddEvent = useCallback((event: SchoolEvent) => setEvents(prev => [...prev, event]), []);
  const handleUpdateEvent = useCallback((updatedEvent: SchoolEvent) => {
    setEvents(prev => prev.map(e => e.id === updatedEvent.id ? updatedEvent : e));
  }, []);
  const handleDeleteEvent = useCallback((id: string) => setEvents(prev => prev.filter(e => e.id !== id)), []);

  const handleLogNotification = useCallback((log: NotificationLog) => {
    setNotificationLogs(prev => [log, ...prev]);
  }, []);

  return (
    <div className="flex min-h-screen bg-[#f1f5f9]">
      {isAdminMode && <Sidebar currentView={currentView} setView={setCurrentView} schoolLogo={schoolLogo} />}

      <main className={`flex-1 p-8 overflow-y-auto max-h-screen scrollbar-hide ${!isAdminMode ? 'max-w-7xl mx-auto' : ''}`}>
        <header className="flex justify-between items-center mb-8 no-print">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => setIsAdminMode(!isAdminMode)}
              className={`px-4 py-2 rounded-xl text-[10px] font-black uppercase tracking-widest border transition-all shadow-sm ${isAdminMode ? 'bg-indigo-50 text-indigo-600 border-indigo-100' : 'bg-rose-50 text-rose-600 border-rose-100'}`}
            >
              {isAdminMode ? 'View as Guardian' : 'Return to Admin Hub'}
            </button>
          </div>

          <div className="flex items-center gap-6">
            <div className="flex items-center gap-3 bg-white px-4 py-2 rounded-2xl border border-gray-100 shadow-sm">
              <span className={`w-2 h-2 rounded-full ${isSaving ? 'bg-orange-500 animate-pulse' : 'bg-emerald-500'}`}></span>
              <span className="text-[10px] font-black text-gray-500 uppercase tracking-widest">
                {isSaving ? 'Auto-Saving...' : 'Cloud Synced'}
              </span>
            </div>
          </div>
        </header>

        {(!isAdminMode) ? (
          <GuardianPortal ward={students.find(s => s.id === 'S001') || students[0]} schoolLogo={schoolLogo} />
        ) : (
          <>
            {currentView === 'dashboard' && <Dashboard students={students} teachers={teachers} onGradeUpdate={handleGradeUpdate} schoolLogo={schoolLogo} onUpdateLogo={setSchoolLogo} events={events} onAddEvent={handleAddEvent} onUpdateEvent={handleUpdateEvent} onDeleteEvent={handleDeleteEvent} />}
            {currentView === 'students' && <StudentList students={students} onAttendanceChange={handleAttendanceChange} onBulkAttendanceChange={handleBulkAttendanceChange} onAddStudent={handleAddStudent} onDeleteStudent={handleDeleteStudent} onBulkDeleteStudents={handleBulkDeleteStudents} />}
            {currentView === 'teachers' && <TeacherList teachers={teachers} onAttendanceChange={() => {}} onBulkAttendanceChange={() => {}} onAddTeacher={handleAddTeacher} onUpdateTeacher={handleUpdateTeacher} onDeleteTeacher={handleDeleteTeacher} onBulkDeleteTeachers={handleBulkDeleteTeachers} />}
            {currentView === 'grading' && <Grading students={students} onGradeUpdate={handleGradeUpdate} onLogNotification={handleLogNotification} />}
            {currentView === 'reports' && <ReportCard students={students} schoolLogo={schoolLogo} reportSettings={reportSettings} onUpdateReportSettings={setReportSettings} onLogNotification={handleLogNotification} />}
            {currentView === 'notifications' && <CommunicationHub students={students} notificationLogs={notificationLogs} onSendMessage={handleLogNotification} templates={templates} onUpdateTemplates={setTemplates} />}
          </>
        )}
      </main>

      <style>{`
        @media print {
          .no-print { display: none !important; }
          body { background: white !important; margin: 0 !important; padding: 0 !important; }
          main { 
            padding: 0 !important; 
            margin: 0 !important; 
            overflow: visible !important; 
            max-height: none !important; 
          }
          .flex { display: block !important; }
          .min-h-screen { min-height: 0 !important; }
          .bg-[#f1f5f9] { background: white !important; }
          .printable-report { 
            box-shadow: none !important; 
            border: 1px solid #eee !important; 
            border-radius: 0 !important; 
            width: 100% !important; 
            max-width: none !important;
            page-break-after: always;
            margin: 0 !important;
            padding: 20px !important;
          }
          #root > div { display: block !important; }
        }
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease-out forwards; }
      `}</style>
    </div>
  );
};

export default App;

import React, { useState, useMemo } from 'react';
import { Student, AttendanceStatus, NotificationLog } from '../types';
import { SUBJECTS } from '../constants';
import { generateReportComment } from '../services/geminiService';

interface ReportSettings {
  headOfSchool: string;
  signature: string;
  authPrefix: string;
}

interface ReportCardProps {
  students: Student[];
  schoolLogo: string;
  reportSettings: ReportSettings;
  onUpdateReportSettings: (settings: ReportSettings) => void;
  onLogNotification: (log: NotificationLog) => void;
}

const ReportCard: React.FC<ReportCardProps> = ({ students, schoolLogo, reportSettings, onUpdateReportSettings, onLogNotification }) => {
  const [selectedStudentId, setSelectedStudentId] = useState<string>(students[0]?.id || '');
  const [selectedTerm, setSelectedTerm] = useState<number>(1);
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);
  const [isEmailing, setIsEmailing] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [aiComments, setAiComments] = useState<Record<string, string>>({});
  const [isPrintAllMode, setIsPrintAllMode] = useState(false);

  const student = students.find(s => s.id === selectedStudentId);

  const handleGenerateComment = async (std: Student) => {
    setIsGeneratingAI(true);
    const filtered = std.grades.filter(g => g.term === selectedTerm);
    const comment = await generateReportComment({ ...std, grades: filtered });
    setAiComments(prev => ({ ...prev, [std.id]: comment }));
    setIsGeneratingAI(false);
  };

  const handleEmailGuardian = () => {
    if (!student) return;
    setIsEmailing(true);
    setTimeout(() => {
      setIsEmailing(false);
      onLogNotification({
        id: Date.now().toString(),
        timestamp: new Date().toISOString(),
        recipientEmail: student.guardianEmail,
        studentName: student.name,
        subject: `Term ${selectedTerm} Report: ${student.name}`,
        type: 'ACADEMIC',
        status: 'SENT'
      });
      alert(`Terminal report for ${student.name} dispatched to ${student.guardianEmail}.`);
    }, 1200);
  };

  const renderReportContent = (std: Student) => {
    const grades = std.grades.filter(g => g.term === selectedTerm);
    const attendanceValues = Object.values(std.attendance);
    const presentCount = attendanceValues.filter(v => v === AttendanceStatus.PRESENT).length;
    const totalDays = attendanceValues.length || 1;
    const rate = Math.round((presentCount / totalDays) * 100);

    return (
      <div key={std.id} className="bg-white rounded-[2.5rem] shadow-2xl overflow-hidden border border-gray-100 max-w-5xl mx-auto relative printable-report mb-8">
        {/* Header Section */}
        <div className="bg-gray-50/50 p-10 border-b border-gray-100 relative">
          <div className="flex flex-col md:flex-row justify-between items-start gap-8 relative z-10">
            <div className="flex items-center gap-8">
              <div className="relative">
                <img src={std.avatar} className="w-28 h-28 rounded-3xl object-cover shadow-xl border-4 border-white" alt="" />
                <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-emerald-500 rounded-lg flex items-center justify-center text-white border-2 border-white shadow-lg no-print"><i className="fas fa-check text-xs"></i></div>
              </div>
              <div>
                <h2 className="text-3xl font-black text-gray-800 tracking-tight uppercase">{std.name}</h2>
                <div className="flex gap-2 mt-2">
                  <span className="px-3 py-1 bg-indigo-600 text-white text-[10px] font-black uppercase rounded-full tracking-widest">{std.gradeLevel}</span>
                  <span className="px-3 py-1 bg-gray-800 text-white text-[10px] font-black uppercase rounded-full tracking-widest">TERM {selectedTerm}</span>
                </div>
                <p className="text-gray-400 text-[10px] mt-4 font-black uppercase tracking-widest">ID: <span className="text-gray-800">{std.id}</span> | GUARDIAN: <span className="text-gray-800">{std.guardianName}</span></p>
              </div>
            </div>
            <div className="md:text-right flex flex-col items-center md:items-end">
              <div className="flex items-center gap-4 mb-3">
                <div className="w-16 h-16 bg-white rounded-full p-1 border-2 border-orange-500 shadow-md">
                  <img src={schoolLogo} alt="Logo" className="w-full h-full object-cover rounded-full" />
                </div>
                <div className="text-left md:text-right">
                  <h3 className="text-xl font-black text-indigo-900 tracking-tighter uppercase leading-tight">CEDAR CREST</h3>
                  <p className="text-[10px] font-black text-orange-600 uppercase tracking-[0.2em]">International School</p>
                </div>
              </div>
              <p className="text-[9px] text-gray-400 font-bold uppercase tracking-widest">Empowering Minds, Excellence in Growth</p>
            </div>
          </div>
        </div>

        {/* Academic Body */}
        <div className="p-10">
          <div className="grid grid-cols-1 lg:grid-cols-5 gap-12">
            <div className="lg:col-span-3 space-y-6">
              <h4 className="text-xs font-black text-gray-400 uppercase tracking-widest border-b pb-2">Academic Performance Index</h4>
              {grades.length > 0 ? (
                <div className="overflow-hidden rounded-3xl border border-gray-100">
                  <table className="w-full text-left">
                    <thead className="bg-gray-50 border-b border-gray-100">
                      <tr>
                        <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase">Subject</th>
                        <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-center">Score</th>
                        <th className="px-6 py-4 text-[10px] font-black text-gray-500 uppercase text-right">Grade</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-50">
                      {grades.map((grade, idx) => {
                        const percent = (grade.score / grade.maxScore) * 100;
                        let gradeLetter = percent >= 90 ? 'A+' : percent >= 80 ? 'A' : percent >= 70 ? 'B' : percent >= 60 ? 'C' : 'F';
                        return (
                          <tr key={idx} className="hover:bg-indigo-50/30 transition-colors">
                            <td className="px-6 py-4"><p className="font-bold text-black">{grade.subject}</p></td>
                            <td className="px-6 py-4 text-center font-black text-black">{grade.score}/100</td>
                            <td className="px-6 py-4 text-right"><span className="text-lg font-black text-indigo-600">{gradeLetter}</span></td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="p-10 text-center bg-gray-50 rounded-3xl border-2 border-dashed border-gray-200">
                  <p className="text-gray-500 font-bold text-sm">No recorded results for this term.</p>
                </div>
              )}
            </div>

            <div className="lg:col-span-2 space-y-8">
              <div className="bg-emerald-50 rounded-3xl p-6 border border-emerald-100 flex items-center justify-between">
                <div>
                  <p className="text-4xl font-black text-emerald-700">{rate}%</p>
                  <p className="text-[10px] font-black text-emerald-600 uppercase mt-1 tracking-widest">Attendance Rate</p>
                </div>
                <i className="fas fa-calendar-check text-3xl text-emerald-200"></i>
              </div>

              <div className="bg-indigo-50 rounded-3xl p-6 border border-indigo-100">
                <h4 className="text-[9px] font-black text-indigo-600 uppercase tracking-widest mb-3">Principal's Commentary</h4>
                <p className="text-sm text-indigo-900 leading-relaxed font-medium italic">
                  {aiComments[std.id] || "Academic evaluation in progress..."}
                </p>
              </div>

              {/* Signatures for Print */}
              <div className="pt-8 space-y-8 mt-auto">
                <div className="border-t border-gray-200 pt-4 flex justify-between items-end">
                  <div className="text-center">
                    <div className="h-10"></div>
                    <div className="w-40 border-b border-gray-400 mb-1"></div>
                    <p className="text-[9px] font-black text-gray-500 uppercase tracking-widest">Class Teacher</p>
                  </div>
                  <div className="text-center">
                    <p className="text-sm font-black text-gray-800 mb-1 font-serif italic">{reportSettings.headOfSchool}</p>
                    <div className="w-48 border-b border-gray-400 mb-1"></div>
                    <p className="text-[9px] font-black text-gray-500 uppercase tracking-widest">Head of Institute</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Footer Area */}
        <div className="bg-gray-50 p-6 border-t border-gray-100 text-center flex justify-between items-center">
          <p className="text-[9px] font-black text-gray-400 uppercase tracking-widest">Generated Securely by CCIS Cloud</p>
          <p className="text-[9px] font-mono text-gray-500">{reportSettings.authPrefix}-{std.id}-{selectedTerm}-{new Date().getFullYear()}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-8 animate-fadeIn pb-20">
      {/* Control Header */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6 no-print">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 tracking-tight">Academic Transcripts</h1>
          <p className="text-gray-500 mt-1 font-medium">Professional terminal reports with institutional grading standards.</p>
        </div>
        <div className="flex flex-wrap gap-3">
          <button 
            onClick={() => setIsSettingsOpen(!isSettingsOpen)}
            className="bg-white border-2 border-orange-200 text-orange-600 px-6 py-3 rounded-2xl font-bold hover:bg-orange-50 transition-all active:scale-95"
          >
            <i className="fas fa-cog mr-2"></i> Report Setup
          </button>

          <select 
            disabled={isPrintAllMode}
            value={selectedStudentId}
            onChange={(e) => setSelectedStudentId(e.target.value)}
            className="bg-white border-2 border-transparent hover:border-indigo-100 rounded-2xl px-5 py-3 shadow-sm font-bold text-black outline-none transition-all disabled:opacity-50"
          >
            {students.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
          </select>

          <div className="flex gap-2">
            <button 
              onClick={() => setIsPrintAllMode(!isPrintAllMode)}
              className={`px-6 py-3 rounded-2xl font-bold transition-all flex items-center gap-2 active:scale-95 ${isPrintAllMode ? 'bg-orange-600 text-white shadow-lg' : 'bg-white border-2 border-gray-200 text-gray-600 hover:bg-gray-50'}`}
            >
              <i className="fas fa-layer-group"></i> {isPrintAllMode ? 'Exit Bulk Print' : 'Bulk Print'}
            </button>
            <button 
              onClick={() => window.print()} 
              className="bg-gray-800 text-white px-8 py-3 rounded-2xl font-bold hover:bg-black transition-all shadow-xl flex items-center gap-2 active:scale-95"
            >
              <i className="fas fa-print"></i> {isPrintAllMode ? 'Print All' : 'Print Report'}
            </button>
          </div>
        </div>
      </div>

      {/* Settings Panel */}
      {isSettingsOpen && (
        <div className="glass-card p-8 rounded-[2rem] border border-orange-100 bg-orange-50/30 no-print animate-slideDown">
          <h3 className="text-sm font-black text-orange-600 uppercase tracking-widest mb-6">Institute Identity Settings</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Head of School Name</label>
              <input 
                value={reportSettings.headOfSchool} 
                onChange={e => onUpdateReportSettings({...reportSettings, headOfSchool: e.target.value})}
                className="w-full bg-white border-none rounded-xl px-4 py-2 font-bold text-black shadow-sm"
              />
            </div>
            <div>
              <label className="text-[10px] font-black text-gray-400 uppercase tracking-widest mb-2 block">Auth Prefix</label>
              <input 
                value={reportSettings.authPrefix} 
                onChange={e => onUpdateReportSettings({...reportSettings, authPrefix: e.target.value})}
                className="w-full bg-white border-none rounded-xl px-4 py-2 font-mono text-sm text-black shadow-sm"
              />
            </div>
            <div className="flex items-end">
              <button 
                onClick={() => setIsSettingsOpen(false)}
                className="w-full bg-indigo-600 text-white py-2 rounded-xl text-xs font-black uppercase tracking-widest shadow-lg"
